import { ChartData, ChartOptions } from './ChartService';
import { logger } from '../../lib/logger';
import { flags } from '../../utils/feature-flags';
import { IChartRenderStrategy } from './strategies/IChartRenderStrategy';
import { BasicChartRenderStrategy } from './strategies/BasicChartRenderStrategy';
import { AdvancedChartRenderStrategy } from './strategies/AdvancedChartRenderStrategy';

/**
 * Handles the rendering of charts to various formats using strategy pattern
 */
export class ChartRenderer {
  private static strategy: IChartRenderStrategy;

  /**
   * Get the appropriate rendering strategy based on feature flags
   */
  private static getStrategy(): IChartRenderStrategy {
    if (!this.strategy) {
      if (flags.newChartRendering) {
        logger.info('Using advanced chart rendering strategy');
        this.strategy = new AdvancedChartRenderStrategy();
      } else {
        logger.info('Using basic chart rendering strategy');
        this.strategy = new BasicChartRenderStrategy();
      }
    }
    return this.strategy;
  }

  /**
   * Reset the strategy (useful for testing or configuration changes)
   */
  static resetStrategy(): void {
    this.strategy = null as any;
  }

  /**
   * Render a chart as a PNG image buffer
   * @param chartData Data to render
   * @param options Rendering options
   * @returns Buffer containing the PNG image
   */
  static async renderPNG(chartData: ChartData, options?: Partial<ChartOptions>): Promise<Buffer | null> {
    try {
      const strategy = this.getStrategy();
      return await strategy.renderPNG(chartData, options);
    } catch (error) {
      logger.error(
        {
          error: error instanceof Error ? error.message : String(error),
          stack: error instanceof Error ? error.stack : undefined,
        },
        'Failed to render chart as PNG'
      );
      return null;
    }
  }

  /**
   * Create a simple HTML representation of chart data
   * @param chartData Data to render
   * @param options Rendering options
   * @returns HTML string
   */
  static async renderHTML(chartData: ChartData, options: Partial<ChartOptions> = {}): Promise<string> {
    try {
      const strategy = this.getStrategy();
      return await strategy.renderHTML(chartData, options);
    } catch (error) {
      logger.error(
        {
          error: error instanceof Error ? error.message : String(error),
          stack: error instanceof Error ? error.stack : undefined,
        },
        'Failed to render chart as HTML'
      );
      return `<html><body><h1>Error rendering chart</h1><p>${error}</p></body></html>`;
    }
  }
}

import { logger } from '../../lib/logger';
import { flags } from '../../utils/feature-flags';
import { RepositoryManager } from '../../infrastructure/repositories/RepositoryManager';
import { CacheAdapter } from '../../cache/CacheAdapter';
import { CacheRedisAdapter } from '../../cache/CacheRedisAdapter';
import { Configuration } from '../../config';

/**
 * Chart rendering options
 */
export interface ChartOptions {
  /** Width of the chart in pixels */
  width: number;
  /** Height of the chart in pixels */
  height: number;
  /** Optional title for the chart */
  title?: string;
  /** Whether to include a legend */
  showLegend: boolean;
  /** Whether to add labels with values to the chart */
  showLabels: boolean;
  /** Whether to use light mode, otherwise dark mode */
  lightMode: boolean;
}

/**
 * Chart data format
 */
export interface ChartData {
  /** Labels for the data points */
  labels: string[];
  /** Datasets to be rendered */
  datasets: Array<{
    /** Name of the dataset */
    label: string;
    /** Data values */
    data: number[];
    /** Background colors (can be a single color or an array) */
    backgroundColor: string | string[];
    /** Border colors (can be a single color or an array) */
    borderColor?: string | string[];
  }>;
}

/**
 * Ship data entry format
 */
interface ShipDataEntry {
  shipName: string;
  count: number;
}

/**
 * Main service for chart generation and rendering
 */
export class ChartService {
  private repositoryManager: RepositoryManager;
  private cache: CacheAdapter;

  /**
   * Create a new ChartService
   */
  constructor(cache?: CacheAdapter) {
    this.repositoryManager = new RepositoryManager();
    this.cache =
      cache ??
      new CacheRedisAdapter(
        process.env.REDIS_URL ?? 'redis://localhost:6379',
        Configuration.charts.defaultCacheTTLSeconds
      );
  }

  /**
   * Generate ship usage statistics for a character or group
   * @param characterId Character ID or null if using a group
   * @param groupId Group ID or null if using a character
   * @param days Number of days to include in the chart
   * @returns Chart data or null if no data available
   */
  async generateShipUsageChart(characterId?: string, groupId?: string, days: number = 30): Promise<ChartData | null> {
    try {
      if (!characterId && !groupId) {
        logger.error('Either characterId or groupId must be provided');
        return null;
      }

      logger.info(
        `Generating ship usage chart for ${
          characterId ? `character ${characterId}` : `group ${groupId}`
        } over ${days} days`
      );

      // Calculate date range
      const endDate = new Date();
      const startDate = new Date(endDate);
      startDate.setDate(startDate.getDate() - days);

      // Use feature flag to conditionally use real database queries or mock data
      let shipData: ShipDataEntry[];

      if (flags.newChartRendering) {
        // Implement actual data retrieval when feature flag is enabled
        logger.info('Using database query for ship usage (feature flag enabled)');

        try {
          // Get repositories
          const killRepository = this.repositoryManager.getKillRepository();

          let characterIds: string[] = [];

          if (characterId) {
            // Single character
            characterIds = [characterId];
          } else if (groupId) {
            // Get characters from the group
            const characterRepository = this.repositoryManager.getCharacterRepository();
            const groups = await characterRepository.getAllCharacterGroups();
            const group = groups.find(g => g.id === groupId);

            if (group?.characters) {
              characterIds = group.characters.map((char: any) => char.eveId);
            }
          }

          // Get top ship types used for kills
          const topShips = await killRepository.getTopShipTypesUsed(
            characterIds.map(id => BigInt(id)),
            startDate,
            endDate,
            Configuration.charts.defaultTopLimit // Limit to top ships
          );

          shipData = topShips.map(ship => ({
            shipName: ship.shipTypeId, // In a real implementation, this would be mapped to ship names from ESI
            count: ship.count,
          }));

          logger.info(`Found ${shipData.length} ship types for chart`);
        } catch (error) {
          logger.error('Error in ship data retrieval, falling back to mock data', error);
          // Fall back to mock data
          shipData = [
            { shipName: 'Rifter (Mock)', count: 15 },
            { shipName: 'Punisher (Mock)', count: 8 },
            { shipName: 'Merlin (Mock)', count: 12 },
            { shipName: 'Incursus (Mock)', count: 5 },
          ];
        }
      } else {
        // Use mock data when feature flag is disabled
        logger.info('Using mock data for ship usage (feature flag disabled)');
        shipData = [
          { shipName: 'Rifter', count: 15 },
          { shipName: 'Punisher', count: 8 },
          { shipName: 'Merlin', count: 12 },
          { shipName: 'Incursus', count: 5 },
        ];
      }

      // Create chart data
      const chartData: ChartData = {
        labels: shipData.map(s => s.shipName),
        datasets: [
          {
            label: 'Ship Usage',
            data: shipData.map(s => s.count),
            backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF', '#FF9F40'],
          },
        ],
      };

      // Generate cache key
      const cacheKey = `ship-usage-${characterId}-${days}`;

      // Check cache
      const cachedData = await this.cache.get<ChartData>(cacheKey);
      if (cachedData) {
        logger.info(`Retrieved ship usage chart from cache for key: ${cacheKey}`);
        return cachedData;
      }

      // Store in cache
      // Cache for configured duration
      await this.cache.set(cacheKey, chartData, Configuration.charts.defaultCacheTTLSeconds);
      logger.info(`Stored ship usage chart in cache for key: ${cacheKey}`);

      return chartData;
    } catch (error) {
      logger.error(
        {
          error: error instanceof Error ? error.message : String(error),
          stack: error instanceof Error ? error.stack : undefined,
        },
        'Failed to generate ship usage chart'
      );
      return null;
    }
  }

  /**
   * Render a chart as a PNG buffer
   * @param chartData The data to render
   * @param options Rendering options
   * @returns Buffer containing the PNG image or null if rendering failed
   */
  async renderChart(_chartData: ChartData, options: Partial<ChartOptions> = {}): Promise<Buffer | null> {
    try {
      // Set default options
      const chartOptions: ChartOptions = {
        width: options.width ?? Configuration.charts.defaultWidth,
        height: options.height ?? Configuration.charts.defaultHeight,
        title: options.title,
        showLegend: options.showLegend ?? true,
        showLabels: options.showLabels ?? true,
        lightMode: options.lightMode ?? false,
      };

      logger.info(`Rendering chart with dimensions ${chartOptions.width}x${chartOptions.height}`);

      // Use feature flag to conditionally enable real chart rendering
      if (flags.newChartRendering) {
        logger.info('Using real chart rendering (feature flag enabled)');
        try {
          // This would be the actual implementation
          // const { ChartRenderer } = await import('../ChartRenderer');
          // return ChartRenderer.renderPNG(chartData, chartOptions);

          // Placeholder for now
          return Buffer.from('Real chart rendering output');
        } catch (error) {
          logger.error('Error in chart rendering, falling back to mock', error);
          // Fall back to mock buffer
        }
      }

      // Use mock buffer when feature flag is disabled
      logger.info('Using mock chart rendering (feature flag disabled)');
      const mockBuffer = Buffer.from('Mock chart rendering output');
      return mockBuffer;
    } catch (error) {
      logger.error(
        {
          error: error instanceof Error ? error.message : String(error),
          stack: error instanceof Error ? error.stack : undefined,
        },
        'Failed to render chart'
      );
      return null;
    }
  }
}

// Configuration options for the application

export const config = {
  // Cache settings
  cache: {
    // Default TTL for cache entries in milliseconds
    defaultTTL: 5 * 60 * 1000, // 5 minutes

    // Whether to enable caching
    enabled: true,
  },

  // API settings
  api: {
    // Rate limiting
    rateLimit: {
      windowMs: 15 * 60 * 1000, // 15 minutes
      max: 100, // limit each IP to 100 requests per windowMs
    },
  },
};

// Environment-based configuration
