export const CACHE_TTL = Number(process.env.CACHE_TTL) ?? 300; // seconds
// Legacy URLs (to be removed)
export const ESI_BASE_URL = process.env.ESI_BASE_URL ?? 'https://esi.evetech.net/latest';
export const ZKILLBOARD_BASE_URL = process.env.ZKILLBOARD_BASE_URL ?? 'https://zkillboard.com/api';

// Database configuration
