// HTTP Client configuration
export const HTTP_TIMEOUT = Number(process.env.HTTP_TIMEOUT) ?? 30000; // 30 seconds
export const HTTP_MAX_RETRIES = Number(process.env.HTTP_MAX_RETRIES) ?? 3;
export const HTTP_INITIAL_RETRY_DELAY = Number(process.env.HTTP_INITIAL_RETRY_DELAY) ?? 1000; // 1 second
export const HTTP_MAX_RETRY_DELAY = Number(process.env.HTTP_MAX_RETRY_DELAY) ?? 45000; // 45 seconds

// Rate limiting configuration
export const RATE_LIMIT_MIN_DELAY = Number(process.env.RATE_LIMIT_MIN_DELAY) ?? 1000; // 1 second
export const RATE_LIMIT_MAX_DELAY = Number(process.env.RATE_LIMIT_MAX_DELAY) ?? 10000; // 10 seconds

// Feature flags
export const NEW_CHART_RENDERING = process.env.NEW_CHART_RENDERING === 'true';

// Server configuration
