import { BaseEntity } from '../BaseEntity';
import { ensureRequiredBigInt } from '../../utils/conversion';
import { validateRequired, validatePositive, validateNonNegative } from '../../utils/validation';

/**
 * LossFact domain entity
 * Represents a character's loss in EVE Online
 */
export class LossFact extends BaseEntity {
  /** Killmail ID from EVE Online */
  readonly killmailId: bigint;

  /** Character EVE ID (who lost the ship) */
  readonly characterId: bigint;

  /** Timestamp of the loss */
  readonly killTime: Date;

  /** Ship type ID of the ship that was lost */
  shipTypeId: number;

  /** Solar system ID where the loss occurred */
  systemId: number;

  /** Total ISK value of the loss */
  totalValue: bigint;

  /** Number of attackers on the killmail */
  attackerCount: number;

  // Labels are inherited from BaseEntity

  /**
   * Create a new LossFact instance
   */
  constructor(props: {
    killmailId: bigint | string;
    characterId: bigint | string;
    killTime: Date;
    shipTypeId: number;
    systemId: number;
    totalValue: bigint | string;
    attackerCount: number;
    labels?: string[];
  }) {
    super();

    // Convert string IDs to bigint using utility
    this.killmailId = ensureRequiredBigInt(props.killmailId);
    this.characterId = ensureRequiredBigInt(props.characterId);

    this.killTime = props.killTime;
    this.shipTypeId = props.shipTypeId;
    this.systemId = props.systemId;
    this.totalValue = ensureRequiredBigInt(props.totalValue);
    this.attackerCount = props.attackerCount;
    this.labels = props.labels ?? [];

    this.validate();
  }

  /**
   * Validate the loss fact data using shared validation utilities
   * @throws Error if data is invalid
   */
  private validate(): void {
    validateRequired('killmailId', this.killmailId);
    validateRequired('characterId', this.characterId);
    validateRequired('killTime', this.killTime);
    validatePositive('shipTypeId', this.shipTypeId);
    validatePositive('systemId', this.systemId);
    validateNonNegative('attackerCount', this.attackerCount);
  }

  /**
   * Check if loss was a solo kill (by a single attacker)
   */
  get wasSoloKill(): boolean {
    return this.attackerCount === 1;
  }

  /**
   * Check if loss was a "gank" (multiple attackers)
   */
  get wasGanked(): boolean {
    return this.attackerCount > 1;
  }

  /**
   * Get the ISK value in millions (more readable)
   */
  get iskValueMillions(): number {
    return Number(this.totalValue) / 1000000;
  }

  /**
   * Get the loss type category based on ISK value
   * @returns 'cheap', 'moderate', 'expensive', or 'blingy'
   */
  getLossCategory(): 'cheap' | 'moderate' | 'expensive' | 'blingy' {
    const valueMillions = this.iskValueMillions;

    if (valueMillions < 10) {
      return 'cheap';
    } else if (valueMillions < 100) {
      return 'moderate';
    } else if (valueMillions < 1000) {
      return 'expensive';
    } else {
      return 'blingy';
    }
  }

  /**
   * Create a LossFact domain entity from a database model
   */
  static fromModel(model: any): LossFact {
    return new LossFact({
      killmailId: model.killmail_id,
      characterId: model.character_id,
      killTime: model.kill_time,
      shipTypeId: model.ship_type_id,
      systemId: model.system_id,
      totalValue: model.total_value,
      attackerCount: model.attacker_count,
      labels: model.labels ?? [],
    });
  }
}

import { AxiosRequestConfig } from 'axios';

/**
 * Configuration options for the ESI client
 */
export interface ESIClientConfig {
  /** Base URL for the ESI API */
  baseUrl?: string;
  /** Default timeout in milliseconds */
  timeout?: number;
  /** User-Agent string to identify the client */
  userAgent?: string;
  /** Cache TTL in seconds */
  cacheTtl?: number;
  /** Maximum number of retries for failed requests */
  maxRetries?: number;
  /** Initial retry delay in milliseconds */
  initialRetryDelay?: number;
}

/**
 * Interface for the unified ESI client
 */
export interface IESIClient {
  /**
   * Fetch data from the ESI API
   * @param endpoint The ESI endpoint to call
   * @param options Additional request options
   * @returns The response data
   */
  fetch<T>(endpoint: string, options?: AxiosRequestConfig): Promise<T>;

  /**
   * Fetch killmail data from ESI
   * @param killmailId The ID of the killmail
   * @param hash The killmail hash
   * @returns The killmail data
   */
  fetchKillmail(killmailId: number, hash: string): Promise<any>;

  /**
   * Fetch character information
   * @param characterId The character ID
   * @returns Character information
   */
  fetchCharacter(characterId: number): Promise<any>;

  /**
   * Fetch corporation information
   * @param corporationId The corporation ID
   * @returns Corporation information
   */
  fetchCorporation(corporationId: number): Promise<any>;

  /**
   * Fetch alliance information
   * @param allianceId The alliance ID
   * @returns Alliance information
   */
  fetchAlliance(allianceId: number): Promise<any>;

  /**
   * Fetch solar system information
   * @param systemId The solar system ID
   * @returns Solar system information
   */
  fetchSolarSystem(systemId: number): Promise<any>;

  /**
   * Fetch type information (ships, modules, etc.)
   * @param typeId The type ID
   * @returns Type information
   */
  fetchType(typeId: number): Promise<any>;

  /**
   * Clear the cache for a specific endpoint
   * @param endpoint Optional endpoint to clear cache for. If not provided, clears all cache.
   */
  clearCache(endpoint?: string): Promise<void>;
}

import { UnifiedESIClient } from './UnifiedESIClient';
import { MapActivityResponseSchema, UserCharactersResponseSchema } from '../../types/ingestion';
import { z } from 'zod';
import { logger } from '../../lib/logger';
import { RateLimiter } from '../../utils/rateLimiter';
import { rateLimiterManager } from '../../utils/RateLimiterManager';

// Infer types from schemas
type MapActivityResponse = z.infer<typeof MapActivityResponseSchema>;
type UserCharactersResponse = z.infer<typeof UserCharactersResponseSchema>;

// Define response type for raw API responses
interface RawApiResponse {
  data?: any[];
  [key: string]: any;
}

type ApiResponse = any[] | RawApiResponse;

export class MapClient {
  private readonly client: UnifiedESIClient;
  private readonly apiKey: string;
  private readonly rateLimiter: RateLimiter;

  constructor(baseUrl: string, apiKey: string) {
    this.apiKey = apiKey;
    this.client = new UnifiedESIClient({
      baseUrl,
      userAgent: 'EVE-Chart-Bot/1.0',
      timeout: 10000,
    });

    // Use shared rate limiter from singleton manager
    this.rateLimiter = rateLimiterManager.getRateLimiter('Map API');
  }

  /**
   * Get data array from response, handling both array and object responses
   */
  private getDataArray(response: ApiResponse): any[] {
    if (Array.isArray(response)) {
      return response;
    }
    return response.data ?? [];
  }

  /**
   * Fetch character activity data from the Map API
   */
  async getCharacterActivity(slug: string, days: number = 7, signal?: AbortSignal): Promise<MapActivityResponse> {
    try {
      logger.info(`Fetching character activity for map: ${slug}, days: ${days}`);

      // Respect rate limit
      await this.rateLimiter.wait(signal);

      const url = `/api/map/character-activity?slug=${slug}&days=${days}`;
      const response = await this.client.fetch<ApiResponse>(url, {
        headers: {
          Authorization: `Bearer ${this.apiKey}`,
        },
        signal,
      });

      if (response) {
        const dataArray = this.getDataArray(response);
        const dataCount = dataArray.length;
        logger.info(`Received ${dataCount} records in response`);

        // Log date range in the response
        if (dataArray.length > 0) {
          const dates = dataArray.map((item: any) => new Date(item.timestamp));
          const oldestDate = new Date(Math.min(...dates.map((d: Date) => d.getTime())));
          const newestDate = new Date(Math.max(...dates.map((d: Date) => d.getTime())));
          logger.info(`Date range in response: ${oldestDate.toISOString()} to ${newestDate.toISOString()}`);
        }

        // Try to validate the response against our schema
        try {
          const validated = MapActivityResponseSchema.parse(response);
          logger.info(`Successfully validated response with ${validated.data.length} activity records`);
          return validated;
        } catch (schemaError) {
          logger.error(`Schema validation error for map activity response:`, schemaError);
          // Return empty data array if validation fails
          return { data: [] };
        }
      }

      logger.warn(`No data received from map API`);
      return { data: [] };
    } catch (error) {
      logger.error(`Error fetching character activity from Map API:`, error);
      throw error;
    }
  }

  async getUserCharacters(slug: string, signal?: AbortSignal): Promise<UserCharactersResponse> {
    try {
      await this.rateLimiter.wait(signal);
      logger.info(`Fetching user characters for slug: ${slug}`);

      const response = await this.client.fetch<ApiResponse>('/api/map/user_characters', {
        headers: {
          Authorization: `Bearer ${this.apiKey}`,
        },
        params: {
          slug,
        },
        signal,
      });

      const parsedData = UserCharactersResponseSchema.parse(response);
      logger.info(`Parsed ${parsedData.data.length} user entries`);
      logger.info(`Total characters: ${parsedData.data.reduce((acc, user) => acc + user.characters.length, 0)}`);

      return parsedData;
    } catch (error) {
      logger.error({ error, slug }, 'Failed to fetch user characters from Map API');
      throw error;
    }
  }

  /**
   * Clean up resources
   */
  cleanup(): void {
    // Rate limiter is now managed by the singleton, no need to reset here
    // The manager will handle cleanup centrally
  }
}

import axios, { AxiosInstance, AxiosRequestConfig } from 'axios';
import { logger } from '../../lib/logger';
import { CacheAdapter } from '../../cache/CacheAdapter';
import { CacheRedisAdapter } from '../../cache/CacheRedisAdapter';
import { ESIClientConfig, IESIClient } from './ESIClient';
import { retryOperation } from '../../utils/retry';
import { RateLimiter } from '../../utils/rateLimiter';
import { rateLimiterManager } from '../../utils/RateLimiterManager';
import {
  REDIS_URL,
  ESI_BASE_URL,
  CACHE_TTL,
  HTTP_TIMEOUT,
  HTTP_MAX_RETRIES,
  HTTP_INITIAL_RETRY_DELAY,
} from '../../config';

/**
 * Unified client for interacting with EVE Online's ESI API
 * Combines caching and retry logic from previous implementations
 */
export class UnifiedESIClient implements IESIClient {
  private readonly client: AxiosInstance;
  private readonly config: Required<ESIClientConfig>;
  private readonly cache: CacheAdapter;
  private readonly rateLimiter: RateLimiter | null = null;

  /**
   * Create a new unified ESI client
   * @param config Optional configuration for the client
   * @param cache Optional cache adapter to use for requests
   */
  constructor(config: ESIClientConfig = {}, cache?: CacheAdapter) {
    this.config = {
      baseUrl: config.baseUrl ?? ESI_BASE_URL,
      timeout: config.timeout ?? HTTP_TIMEOUT,
      userAgent: config.userAgent ?? 'EVE-Chart-Bot/1.0',
      cacheTtl: config.cacheTtl ?? CACHE_TTL,
      maxRetries: config.maxRetries ?? HTTP_MAX_RETRIES,
      initialRetryDelay: config.initialRetryDelay ?? HTTP_INITIAL_RETRY_DELAY,
    };

    this.client = axios.create({
      baseURL: this.config.baseUrl,
      timeout: this.config.timeout,
      headers: {
        'User-Agent': this.config.userAgent,
        Accept: 'application/json',
      },
    });

    this.cache = cache ?? new CacheRedisAdapter(REDIS_URL, this.config.cacheTtl);

    // Only use rate limiter if this is an ESI client (not used for other APIs)
    if (this.config.baseUrl.includes('esi')) {
      this.rateLimiter = rateLimiterManager.getRateLimiter('ESI');
    }
  }

  /**
   * Fetch data from ESI with caching and retry logic
   * @param endpoint API endpoint to fetch
   * @param options Additional request options
   */
  async fetch<T>(endpoint: string, options: AxiosRequestConfig = {}): Promise<T> {
    const url = endpoint.startsWith('/') ? endpoint : `/${endpoint}`;
    const cacheKey = this.buildCacheKey(url, options.params ?? {});

    try {
      // Try to get from cache first
      const cachedData = await this.cache.get<T>(cacheKey);
      if (cachedData) {
        logger.debug(`ESI cache hit for ${url}`);
        return cachedData;
      }

      // Not in cache, fetch from API with retry logic
      logger.debug(`ESI cache miss for ${url}, fetching from API`);

      // Apply rate limiting if configured
      if (this.rateLimiter && options.signal) {
        // Cast to AbortSignal if it's compatible
        await this.rateLimiter.wait(options.signal as AbortSignal);
      } else if (this.rateLimiter) {
        await this.rateLimiter.wait();
      }

      const data = await retryOperation(
        () => this.client.get<T>(url, options).then(response => response.data),
        `ESI request to ${url}`,
        {
          maxRetries: this.config.maxRetries,
          initialRetryDelay: this.config.initialRetryDelay,
          timeout: this.config.timeout,
        }
      );

      if (!data) {
        throw new Error(`Failed to fetch data from ESI after ${this.config.maxRetries} retries`);
      }

      // Cache the response
      await this.cache.set(cacheKey, data, this.config.cacheTtl);

      return data;
    } catch (error) {
      logger.error(`Error fetching from ESI ${url}:`, error);
      throw error;
    }
  }

  /**
   * Build a cache key from an endpoint and parameters
   */
  private buildCacheKey(endpoint: string, params: Record<string, any>): string {
    const paramString =
      Object.keys(params).length > 0
        ? Object.entries(params)
            .sort(([keyA], [keyB]) => keyA.localeCompare(keyB))
            .map(([key, value]) => `${key}=${value}`)
            .join('&')
        : '';

    return `esi:${endpoint}${paramString ? `?${paramString}` : ''}`;
  }

  /**
   * Fetch killmail data
   */
  async fetchKillmail(killmailId: number, hash: string): Promise<any> {
    return this.fetch(`/killmails/${killmailId}/${hash}/`);
  }

  /**
   * Fetch character information
   */
  async fetchCharacter(characterId: number): Promise<any> {
    return this.fetch(`/characters/${characterId}/`);
  }

  /**
   * Fetch corporation information
   */
  async fetchCorporation(corporationId: number): Promise<any> {
    return this.fetch(`/corporations/${corporationId}/`);
  }

  /**
   * Fetch alliance information
   */
  async fetchAlliance(allianceId: number): Promise<any> {
    return this.fetch(`/alliances/${allianceId}/`);
  }

  /**
   * Fetch solar system information
   */
  async fetchSolarSystem(systemId: number): Promise<any> {
    return this.fetch(`/universe/systems/${systemId}/`);
  }

  /**
   * Fetch type information
   */
  async fetchType(typeId: number): Promise<any> {
    return this.fetch(`/universe/types/${typeId}/`);
  }

  /**
   * Clear the cache for a specific endpoint
   */
  async clearCache(endpoint?: string): Promise<void> {
    if (endpoint) {
      const normalizedEndpoint = endpoint.startsWith('/') ? endpoint : `/${endpoint}`;
      await this.cache.delete(`esi:${normalizedEndpoint}`);
    } else {
      // Clear all ESI cache
      await this.cache.clear();
    }
  }
}

import { UnifiedESIClient } from './UnifiedESIClient';
import { logger } from '../../lib/logger';
import { RateLimiter } from '../../utils/rateLimiter';
import { rateLimiterManager } from '../../utils/RateLimiterManager';
import { RATE_LIMIT_MIN_DELAY } from '../../config';

interface ZkillResponse {
  killID: number;
  killmail_id: number;
  zkb: {
    hash: string;
    totalValue: number;
    points: number;
    labels?: string[];
  };
  [key: string]: any; // Allow additional fields
}

export class ZkillClient {
  private readonly client: UnifiedESIClient;
  private readonly rateLimiter: RateLimiter;

  constructor(baseUrl: string = 'https://zkillboard.com/api') {
    this.client = new UnifiedESIClient({
      baseUrl,
      userAgent: 'EVE-Chart-Bot/1.0',
      timeout: 15000,
    });

    // Use shared rate limiter from singleton manager
    // Override the default delay with config value if needed
    this.rateLimiter = rateLimiterManager.getRateLimiter('zKillboard', {
      minDelayMs: RATE_LIMIT_MIN_DELAY,
    });
  }

  /**
   * Get a single killmail from zKillboard
   */
  async getKillmail(killId: number, signal?: AbortSignal): Promise<ZkillResponse | null> {
    try {
      await this.rateLimiter.wait(signal);
      logger.info(`Fetching killmail ${killId} from zKillboard`);

      const response = await this.client.fetch<Record<string, any>>(`/killID/${killId}/`, { signal });

      // Log the raw response for debugging
      logger.debug(`Raw zKill response for killmail ${killId}:`, JSON.stringify(response, null, 2));

      // Validate response structure
      if (!response || typeof response !== 'object') {
        logger.warn(`Invalid response format for killmail ${killId}`);
        return null;
      }

      // The response might be wrapped in an object with the killID as the key
      const killData = response[killId.toString()] || response;

      if (!killData || typeof killData !== 'object') {
        logger.warn(`Invalid kill data format for killmail ${killId}`);
        return null;
      }

      // Log the kill data structure
      logger.debug(`Kill data structure for ${killId}:`, {
        hasKillmailId: !!killData.killmail_id,
        hasZkb: !!killData.zkb,
        hasHash: !!killData.zkb?.hash,
        keys: Object.keys(killData),
        zkbKeys: killData.zkb ? Object.keys(killData.zkb) : [],
      });

      // Ensure required fields exist
      if (!killData.killmail_id || !killData.zkb?.hash) {
        logger.warn(`Missing required fields for killmail ${killId}`, {
          killmail_id: killData.killmail_id,
          hasZkb: !!killData.zkb,
          hasHash: !!killData.zkb?.hash,
        });
        return null;
      }

      // Ensure the killID matches
      if (killData.killID !== killId) {
        logger.warn(`KillID mismatch for killmail ${killId}`, {
          expected: killId,
          actual: killData.killID,
        });
        return null;
      }

      return killData as ZkillResponse;
    } catch (error) {
      logger.error(`Error fetching killmail ${killId} from zKillboard:`, error);
      throw error;
    }
  }

  /**
   * Get kills for a character from zKillboard
   */
  async getCharacterKills(characterId: number, page: number = 1, signal?: AbortSignal): Promise<ZkillResponse[]> {
    try {
      await this.rateLimiter.wait(signal);
      logger.info(`Fetching kills for character ${characterId} from zKillboard (page ${page})`);

      const response = await this.client.fetch<Record<string, any>>(`/characterID/${characterId}/page/${page}/`, {
        signal,
      });

      // Log the raw response for debugging
      logger.debug(`Raw zKill response for character ${characterId} kills:`, JSON.stringify(response, null, 2));

      if (!response || typeof response !== 'object') {
        logger.warn(`Invalid response format for character ${characterId} kills`);
        return [];
      }

      // Convert object response to array
      const kills = Object.values(response).filter(kill => {
        const isValid = kill && typeof kill === 'object' && kill.killmail_id && kill.zkb?.hash;

        if (!isValid) {
          logger.debug(`Invalid kill entry:`, {
            hasKillmailId: !!kill?.killmail_id,
            hasZkb: !!kill?.zkb,
            hasHash: !!kill?.zkb?.hash,
            keys: kill ? Object.keys(kill) : [],
          });
        }

        return isValid;
      }) as ZkillResponse[];

      return kills;
    } catch (error) {
      logger.error(`Error fetching kills for character ${characterId} from zKillboard:`, error);
      throw error;
    }
  }

  /**
   * Get losses for a character from zKillboard
   */
  async getCharacterLosses(characterId: number, page: number = 1, signal?: AbortSignal): Promise<ZkillResponse[]> {
    try {
      await this.rateLimiter.wait(signal);
      logger.info(`Fetching losses for character ${characterId} from zKillboard (page ${page})`);

      const response = await this.client.fetch<Record<string, any>>(
        `/losses/characterID/${characterId}/page/${page}/`,
        { signal }
      );

      // Log the raw response for debugging
      logger.debug(`Raw zKill response for character ${characterId} losses:`, JSON.stringify(response, null, 2));

      if (!response || typeof response !== 'object') {
        logger.warn(`Invalid response format for character ${characterId} losses`);
        return [];
      }

      // Convert object response to array
      const losses = Object.values(response).filter(kill => {
        const isValid = kill && typeof kill === 'object' && kill.killmail_id && kill.zkb?.hash;

        if (!isValid) {
          logger.debug(`Invalid loss entry:`, {
            hasKillmailId: !!kill?.killmail_id,
            hasZkb: !!kill?.zkb,
            hasHash: !!kill?.zkb?.hash,
            keys: kill ? Object.keys(kill) : [],
          });
        }

        return isValid;
      }) as ZkillResponse[];

      return losses;
    } catch (error) {
      logger.error(`Error fetching losses for character ${characterId} from zKillboard:`, error);
      throw error;
    }
  }

  /**
   * Clean up resources
   */
  cleanup(): void {
    // Rate limiter is now managed by the singleton, no need to reset here
    // The manager will handle cleanup centrally
  }
}

/**
 * ZKillboard API Client
 *
 * API Documentation: https://zkillboard.com/api/
 *
 * Features:
 * - Rate limiting with exponential backoff
 * - Automatic retries with increasing timeouts
 * - Pagination support for all list endpoints
 * - Detailed error logging
 * - Response validation
 *
 * Rate Limiting:
 * - 20 requests per minute
 * - Minimum 3s delay between requests
 * - Exponential backoff on errors
 * - Maximum 60s delay between requests
 * - Maximum 3 retries per request
 * - Timeout starts at 15s, increases with retries up to 45s
 *
 * Example URLs:
 * - Character Kills: https://zkillboard.com/api/kills/characterID/268946627/page/1/
 * - Character Losses: https://zkillboard.com/api/losses/characterID/268946627/page/1/
 * - Corporation Kills: https://zkillboard.com/api/kills/corporationID/123456789/
 * - System Kills: https://zkillboard.com/api/kills/systemID/30000142/
 *
 * Note: The API returns results in reverse chronological order (newest first).
 * Each page typically contains 100 results.
 */

import axios, { AxiosInstance } from 'axios';
import { logger } from '../../lib/logger';
import { timerManager } from '../../utils/timerManager';

// Rate limiting configuration
const RATE_LIMIT = {
  requestsPerMinute: 20, // More conservative than zKillboard's documented rate limit
  minDelay: 3000, // Minimum delay between requests (3 seconds)
  maxDelay: 60000, // Maximum delay between requests (60 seconds)
  backoffFactor: 2, // More aggressive exponential backoff
  maxRetries: 3, // Maximum number of retries per request
  initialTimeout: 15000, // Initial timeout of 15 seconds
  maxTimeout: 45000, // Maximum timeout of 45 seconds
};

/**
 * Client for interacting with ZKillboard API
 */
export class ZKillboardClient {
  private readonly client: AxiosInstance;
  private readonly baseUrl: string;
  private lastRequestTime: number = 0;
  private currentDelay: number = RATE_LIMIT.minDelay;
  private consecutiveErrors: number = 0;
  private currentTimeout: number = RATE_LIMIT.initialTimeout;

  constructor() {
    this.baseUrl = 'https://zkillboard.com/api';

    this.client = axios.create({
      baseURL: this.baseUrl,
      timeout: RATE_LIMIT.initialTimeout,
      headers: {
        'User-Agent': 'EVE-Chart-Bot/1.0',
        Accept: 'application/json',
      },
    });
  }

  private async rateLimit(signal?: AbortSignal): Promise<void> {
    const now = Date.now();
    const timeSinceLastRequest = now - this.lastRequestTime;

    // If we've had consecutive errors, use exponential backoff
    if (this.consecutiveErrors > 0) {
      this.currentDelay = Math.min(
        RATE_LIMIT.maxDelay,
        RATE_LIMIT.minDelay * Math.pow(RATE_LIMIT.backoffFactor, this.consecutiveErrors)
      );
      // Also increase timeout with each error
      this.currentTimeout = Math.min(
        RATE_LIMIT.maxTimeout,
        RATE_LIMIT.initialTimeout * Math.pow(RATE_LIMIT.backoffFactor, this.consecutiveErrors)
      );
    } else {
      // Reset delays on successful requests
      this.currentDelay = RATE_LIMIT.minDelay;
      this.currentTimeout = RATE_LIMIT.initialTimeout;
    }

    // Ensure minimum time between requests
    if (timeSinceLastRequest < this.currentDelay) {
      const waitTime = this.currentDelay - timeSinceLastRequest;
      logger.debug(`Rate limiting: waiting ${waitTime}ms before next request`);

      try {
        await timerManager.delay(waitTime, signal);
      } catch (error) {
        throw new Error('Rate limiting aborted');
      }
    }

    this.lastRequestTime = Date.now();
  }

  private async fetch<T>(endpoint: string, retryCount = 0, signal?: AbortSignal): Promise<T> {
    const url = `${this.baseUrl}/${endpoint}`;
    logger.debug(`Making request to zKillboard: ${url}`);

    // Check if already aborted
    if (signal?.aborted) {
      throw new Error('Request aborted');
    }

    try {
      await this.rateLimit(signal);

      // Update timeout for this request
      this.client.defaults.timeout = this.currentTimeout;

      // Create a managed abort controller for this request
      const requestAbortController = timerManager.createAbortController();

      // Link to parent signal if provided
      if (signal) {
        const abortHandler = () => requestAbortController.abort();
        signal.addEventListener('abort', abortHandler, { once: true });
        // Clean up the listener when request completes
        requestAbortController.signal.addEventListener(
          'abort',
          () => {
            signal.removeEventListener('abort', abortHandler);
          },
          { once: true }
        );
      }

      try {
        const response = await this.client.get<T>(endpoint, {
          signal: requestAbortController.signal,
        });
        // Remove controller from management after successful request
        timerManager.removeAbortController(requestAbortController);
        this.consecutiveErrors = 0; // Reset error count on success
        return response.data;
      } catch (error) {
        // Remove controller from management on error
        timerManager.removeAbortController(requestAbortController);
        throw error;
      }
    } catch (error: any) {
      this.consecutiveErrors++;
      const errorDetails = {
        url,
        status: error.response?.status,
        statusText: error.response?.statusText,
        data: error.response?.data,
        headers: error.response?.headers,
        timeout: error.code === 'ECONNABORTED' ? this.currentTimeout : undefined,
        error: error.message,
        consecutiveErrors: this.consecutiveErrors,
        currentDelay: this.currentDelay,
        currentTimeout: this.currentTimeout,
        retryCount,
        isTimeout: error.code === 'ECONNABORTED',
        isNetworkError: error.code === 'ECONNREFUSED' || error.code === 'ECONNRESET',
        isRateLimit: error.response?.status === 429,
        isServerError: error.response?.status >= 500,
        isClientError: error.response?.status >= 400 && error.response?.status < 500,
      };

      // Log different error types with appropriate severity
      if (error.response?.status === 429) {
        logger.warn(`ZKillboard rate limit hit:`, errorDetails);
      } else if (error.response?.status >= 500) {
        logger.error(`ZKillboard server error:`, errorDetails);
      } else if (error.code === 'ECONNABORTED') {
        logger.error(`ZKillboard request timeout:`, errorDetails);
      } else if (error.code === 'ECONNREFUSED' || error.code === 'ECONNRESET') {
        logger.error(`ZKillboard connection error:`, errorDetails);
      } else {
        logger.error(`ZKillboard request failed:`, errorDetails);
      }

      // Retry logic
      if (retryCount < RATE_LIMIT.maxRetries) {
        const nextRetry = retryCount + 1;
        logger.info(
          `Retrying request to ${endpoint} (attempt ${nextRetry}/${
            RATE_LIMIT.maxRetries
          }) - Last error: ${error.response?.status || error.code}`
        );
        return this.fetch(endpoint, nextRetry, signal);
      }

      throw error;
    }
  }

  /**
   * Get a single killmail by ID
   * @param killId - The killmail ID to fetch
   * @returns The killmail data or null if not found
   *
   * Note: This method will retry up to 3 times with exponential backoff
   * if the request fails.
   */
  public async getKillmail(killId: number): Promise<any> {
    const response = await this.fetch<Record<string, any>>(`killID/${killId}/`);
    logger.debug(`ZKillboard response for killmail ${killId}:`, response);

    // Handle both array and object responses
    if (Array.isArray(response)) {
      return response[0] || null;
    }

    // The response might be wrapped in an object with the killID as the key
    const killData = response[killId.toString()] || response;
    if (!killData || typeof killData !== 'object') {
      logger.warn(`Invalid kill data format for killmail ${killId}`, {
        responseType: typeof response,
        isArray: Array.isArray(response),
        responseKeys: Object.keys(response),
      });
      return null;
    }

    return killData;
  }

  /**
   * Get kills for a character
   * @param characterId - The EVE character ID
   * @param page - The page number to fetch (default: 1)
   * @returns Array of killmails for the character
   *
   * Note: The API returns kills in reverse chronological order (newest first).
   * Each page typically contains 100 killmails.
   *
   * Example URL: https://zkillboard.com/api/kills/characterID/268946627/page/1/
   */
  public async getCharacterKills(characterId: number, page: number = 1): Promise<any[]> {
    try {
      const response = await this.fetch<any[]>(`kills/characterID/${characterId}/page/${page}/`);
      logger.debug(`ZKillboard response for character ${characterId} page ${page}:`, response);
      return response;
    } catch (error) {
      logger.error(`Error fetching kills for character ${characterId} page ${page}:`, error);
      throw error;
    }
  }

  /**
   * Get losses for a character
   * @param characterId - The EVE character ID
   * @param page - The page number to fetch (default: 1)
   * @returns Array of losses for the character
   *
   * Note: The API returns losses in reverse chronological order (newest first).
   * Each page typically contains 100 losses.
   *
   * Example URL: https://zkillboard.com/api/losses/characterID/268946627/page/1/
   */
  public async getCharacterLosses(characterId: number, page: number = 1): Promise<any[]> {
    try {
      const response = await this.fetch<any[]>(`losses/characterID/${characterId}/page/${page}/`);
      logger.debug(`ZKillboard response for character ${characterId} page ${page}:`, response);
      return response;
    } catch (error) {
      logger.error(`Error fetching losses for character ${characterId} page ${page}:`, error);
      throw error;
    }
  }

  /**
   * Get kills for a corporation
   */
  public async getCorporationKills(corporationId: number): Promise<any[]> {
    return this.fetch(`kills/corporationID/${corporationId}/`);
  }

  /**
   * Get losses for a corporation
   */
  public async getCorporationLosses(corporationId: number): Promise<any[]> {
    return this.fetch(`losses/corporationID/${corporationId}/`);
  }

  /**
   * Get kills in a system
   */
  public async getSystemKills(systemId: number): Promise<any[]> {
    return this.fetch(`kills/systemID/${systemId}/`);
  }

  /**
   * Clean up resources and cancel any pending operations
   */
  public cleanup(): void {
    // Cleanup is now handled by the TimerManager
    logger.debug('ZKillboardClient cleanup called - managed by TimerManager');
  }
}

import { ClassConstructor, plainToInstance } from 'class-transformer';

/**
 * Generic mapper for converting between Prisma models and domain entities
 */
export class PrismaMapper {
  /**
   * Converts snake_case to camelCase
   */
  private static toCamelCase(str: string): string {
    return str.replace(/_([a-z])/g, (_match, letter) => letter.toUpperCase());
  }

  /**
   * Recursively converts an object's keys from snake_case to camelCase
   */
  private static convertKeysToCamelCase(obj: any): any {
    if (obj === null || obj === undefined) {
      return obj;
    }

    if (Array.isArray(obj)) {
      return obj.map(item => this.convertKeysToCamelCase(item));
    }

    if (typeof obj === 'object' && obj.constructor === Object) {
      const converted: any = {};
      for (const [key, value] of Object.entries(obj)) {
        const camelKey = this.toCamelCase(key);
        converted[camelKey] = this.convertKeysToCamelCase(value);
      }
      return converted;
    }

    return obj;
  }

  /**
   * Maps a Prisma model to a domain entity
   * @param model The Prisma model instance
   * @param EntityClass The domain entity class constructor
   * @returns A new instance of the domain entity
   */
  static map<T>(model: any, EntityClass: ClassConstructor<T>): T {
    // Convert snake_case keys to camelCase before mapping
    const convertedModel = this.convertKeysToCamelCase(model);

    return plainToInstance(EntityClass, convertedModel, {
      excludeExtraneousValues: true,
      enableImplicitConversion: true,
    });
  }

  /**
   * Maps an array of Prisma models to domain entities
   * @param models Array of Prisma model instances
   * @param EntityClass The domain entity class constructor
   * @returns Array of domain entity instances
   */
  static mapArray<T>(models: any[], EntityClass: ClassConstructor<T>): T[] {
    return models.map(model => this.map(model, EntityClass));
  }

  /**
   * Maps a Prisma model to a plain object, excluding Prisma-specific fields
   * @param model The Prisma model instance
   * @returns A plain object representation
   */
  static toPlainObject(model: any): Record<string, any> {
    const result: Record<string, any> = {};

    // Skip Prisma-specific fields
    const skipFields = ['$type', '$parent', '$path', '$args'];

    for (const [key, value] of Object.entries(model)) {
      if (!skipFields.includes(key)) {
        result[key] = value;
      }
    }

    return result;
  }
}

import { PrismaClient } from '@prisma/client';
