import { BaseRepository } from './BaseRepository';
import { logger } from '../../lib/logger';
import { LossFact } from '../../domain/killmail/LossFact';
import { PrismaMapper } from '../mapper/PrismaMapper';
import { buildWhereFilter } from '../../utils/query-helper';

/**
 * Repository for accessing ship loss data
 */
export class LossRepository extends BaseRepository {
  constructor() {
    super('lossFact');
  }

  /**
   * Get total losses for a character in a time period
   * @param characterId Character EVE ID
   * @param startDate Start date for query range
   * @param endDate End date for query range
   */
  async getLossesByCharacter(characterId: bigint, startDate: Date, endDate: Date): Promise<number> {
    return this.executeQuery(async () => {
      logger.debug(`Fetching losses for character ${characterId} from ${startDate} to ${endDate}`);

      const where = buildWhereFilter({
        character_id: characterId,
        kill_time: {
          gte: startDate,
          lte: endDate,
        },
      });

      const result = await this.prisma.lossFact.count({ where });

      return result;
    });
  }

  /**
   * Get high value losses for a character in a time period
   * @param characterId Character EVE ID
   * @param startDate Start date for query range
   * @param endDate End date for query range
   * @param valueThreshold Minimum ISK value to be considered high value
   */
  async getHighValueLossesByCharacter(
    characterId: bigint,
    startDate: Date,
    endDate: Date,
    valueThreshold: bigint = BigInt(100000000) // 100M ISK default threshold
  ): Promise<number> {
    return this.executeQuery(async () => {
      logger.debug(`Fetching high value losses for character ${characterId} from ${startDate} to ${endDate}`);

      const where = buildWhereFilter({
        character_id: characterId,
        kill_time: {
          gte: startDate,
          lte: endDate,
        },
        total_value: {
          gte: valueThreshold,
        },
      });

      const result = await this.prisma.lossFact.count({ where });

      return result;
    });
  }

  /**
   * Get total value lost for a character in a time period
   * @param characterId Character EVE ID
   * @param startDate Start date for query range
   * @param endDate End date for query range
   */
  async getTotalValueLostByCharacter(characterId: bigint, startDate: Date, endDate: Date): Promise<bigint> {
    return this.executeQuery(async () => {
      logger.debug(`Fetching total value lost for character ${characterId} from ${startDate} to ${endDate}`);

      const where = buildWhereFilter({
        character_id: characterId,
        kill_time: {
          gte: startDate,
          lte: endDate,
        },
      });

      const results = await this.prisma.lossFact.findMany({
        where,
        select: {
          total_value: true,
        },
      });

      // Sum up the total values
      return results.reduce((sum, result) => sum + result.total_value, BigInt(0));
    });
  }

  /**
   * Get losses summary for a group of characters
   * @param characterIds Array of character EVE IDs
   * @param startDate Start date for query range
   * @param endDate End date for query range
   */
  async getLossesSummaryByCharacters(
    characterIds: bigint[],
    startDate: Date,
    endDate: Date
  ): Promise<{
    totalLosses: number;
    highValueLosses: number;
    totalValueLost: bigint;
  }> {
    return this.executeQuery(async () => {
      logger.debug(`Fetching losses summary for ${characterIds.length} characters from ${startDate} to ${endDate}`);

      // Get all losses for these characters
      const where = buildWhereFilter({
        character_id: {
          in: characterIds,
        },
        kill_time: {
          gte: startDate,
          lte: endDate,
        },
      });

      const losses = await this.prisma.lossFact.findMany({ where });
      const lossFacts = PrismaMapper.mapArray(losses, LossFact);

      // Calculate summary
      const totalLosses = lossFacts.length;
      const highValueLosses = lossFacts.filter(loss => loss.totalValue >= BigInt(100000000)).length;
      const totalValueLost = lossFacts.reduce((sum, loss) => sum + loss.totalValue, BigInt(0));

      return {
        totalLosses,
        highValueLosses,
        totalValueLost,
      };
    });
  }

  /**
   * Get a loss by killmail ID
   * @param killmailId Killmail ID to look up
   */
  async getLoss(killmailId: bigint): Promise<LossFact | null> {
    return this.executeQuery(async () => {
      const loss = await this.prisma.lossFact.findUnique({
        where: { killmail_id: killmailId },
      });

      if (!loss) {
        return null;
      }

      return PrismaMapper.map(loss, LossFact);
    });
  }

  /**
   * Get all losses for a character
   * @param characterId Character EVE ID
   * @param startDate Start date for query range
   * @param endDate End date for query range
   */
  async getLossesForCharacter(characterId: bigint, startDate: Date, endDate: Date): Promise<LossFact[]> {
    return this.executeQuery(async () => {
      const losses = await this.prisma.lossFact.findMany({
        where: {
          character_id: characterId,
          kill_time: {
            gte: startDate,
            lte: endDate,
          },
        },
        orderBy: {
          kill_time: 'desc',
        },
      });

      return PrismaMapper.mapArray(losses, LossFact);
    });
  }

  /**
   * Save a loss record
   * @param loss LossFact domain entity to save
   */
  async saveLoss(loss: LossFact): Promise<void> {
    return this.executeQuery(async () => {
      // Check if this character is tracked (exists in characters table)
      const trackedCharacter = await this.prisma.character.findUnique({
        where: { eveId: loss.characterId },
        select: { eveId: true },
      });

      if (!trackedCharacter) {
        // Skip saving loss for untracked character
        return;
      }

      const data = loss.toObject();
      await this.prisma.lossFact.upsert({
        where: { killmail_id: loss.killmailId },
        update: {
          kill_time: data.killTime,
          system_id: data.systemId,
          total_value: data.totalValue,
          attacker_count: data.attackerCount,
          labels: data.labels,
          character_id: data.characterId,
          ship_type_id: data.shipTypeId,
        },
        create: {
          killmail_id: data.killmailId,
          kill_time: data.killTime,
          system_id: data.systemId,
          total_value: data.totalValue,
          attacker_count: data.attackerCount,
          labels: data.labels,
          character_id: data.characterId,
          ship_type_id: data.shipTypeId,
        },
      });
    });
  }

  /**
   * Delete all loss records
   */
  async deleteAllLosses(): Promise<void> {
    await this.executeQuery(async () => {
      await this.prisma.lossFact.deleteMany();
    });
  }

  /**
   * Get losses within a time range
   * @param startDate Start date for query range
   * @param endDate End date for query range
   */
  async getLossesByTimeRange(startDate: Date, endDate: Date): Promise<LossFact[]> {
    return this.executeQuery(async () => {
      const losses = await this.prisma.lossFact.findMany({
        where: {
          kill_time: {
            gte: startDate,
            lte: endDate,
          },
        },
        orderBy: {
          kill_time: 'desc',
        },
      });

      return PrismaMapper.mapArray(losses, LossFact);
    });
  }

  /**
   * Count total loss records
   */
  override async count(): Promise<number> {
    return this.executeQuery(async () => {
      return this.prisma.lossFact.count();
    });
  }

  /**
   * Get the top ship types lost within a date range
   */
  async getTopShipTypesLost(
    characterIds: (string | bigint)[],
    startDate: Date,
    endDate: Date,
    limit: number = 10
  ): Promise<Array<{ shipTypeId: string; count: number }>> {
    return this.executeQuery(async () => {
      // Find all losses for these characters in the date range
      const losses = await this.prisma.lossFact.findMany({
        where: {
          character_id: {
            in: characterIds.map(id => BigInt(id)),
          },
          kill_time: {
            gte: startDate,
            lte: endDate,
          },
        },
        select: {
          ship_type_id: true,
        },
      });
      // Count occurrences of each ship type
      const shipTypeCounts = new Map<string, number>();
      for (const loss of losses) {
        const shipTypeId = loss.ship_type_id.toString();
        shipTypeCounts.set(shipTypeId, (shipTypeCounts.get(shipTypeId) ?? 0) + 1);
      }
      return Array.from(shipTypeCounts.entries())
        .map(([shipTypeId, count]) => ({ shipTypeId, count }))
        .sort((a, b) => b.count - a.count)
        .slice(0, limit);
    });
  }
}

import { MapActivity } from '../../domain/activity/MapActivity';
import { BaseRepository } from './BaseRepository';
import { logger } from '../../lib/logger';

/**
 * Repository for map activity data access
 */
export class MapActivityRepository extends BaseRepository {
  constructor() {
    super('MapActivity');
  }

  /**
   * Get map activity for a character within a date range
   */
  async getActivityForCharacter(characterId: string, startDate: Date, endDate: Date): Promise<MapActivity[]> {
    return this.executeQuery(async () => {
      const activities = await this.prisma.mapActivity.findMany({
        where: {
          characterId: BigInt(characterId),
          timestamp: {
            gte: startDate,
            lte: endDate,
          },
        },
        orderBy: {
          timestamp: 'desc',
        },
      });

      // Map activities with proper field name conversion from snake_case to camelCase
      return activities.map((activity: any) => {
        return new MapActivity({
          characterId: activity.characterId || activity.character_id,
          timestamp: activity.timestamp,
          signatures: activity.signatures,
          connections: activity.connections,
          passages: activity.passages,
          allianceId: activity.allianceId || activity.alliance_id,
          corporationId: activity.corporationId || activity.corporation_id,
        });
      });
    });
  }

  /**
   * Get map activity for multiple characters within a date range
   */
  async getActivityForCharacters(characterIds: string[], startDate: Date, endDate: Date): Promise<MapActivity[]> {
    return this.executeQuery(async () => {
      // Filter out invalid character IDs and convert valid ones to BigInt
      const validCharacterIds = characterIds
        .filter(id => id && id !== '' && id !== 'undefined' && id !== 'null')
        .map(id => {
          try {
            return BigInt(id);
          } catch (error) {
            logger.warn(`Invalid character ID for BigInt conversion: ${id}`);
            return null;
          }
        })
        .filter((id): id is bigint => id !== null);

      if (validCharacterIds.length === 0) {
        logger.warn('No valid character IDs provided to getActivityForCharacters');
        return [];
      }

      logger.debug(`Querying map activity for ${validCharacterIds.length} valid character IDs`);

      const activities = await this.prisma.mapActivity.findMany({
        where: {
          characterId: {
            in: validCharacterIds,
          },
          timestamp: {
            gte: startDate,
            lte: endDate,
          },
        },
        orderBy: {
          timestamp: 'desc',
        },
      });

      logger.debug(`Raw activities from database:`, activities.slice(0, 2)); // Log first 2 records

      try {
        // Map activities with proper field name conversion from snake_case to camelCase
        return activities.map((activity: any) => {
          return new MapActivity({
            characterId: activity.characterId || activity.character_id,
            timestamp: activity.timestamp,
            signatures: activity.signatures,
            connections: activity.connections,
            passages: activity.passages,
            allianceId: activity.allianceId || activity.alliance_id,
            corporationId: activity.corporationId || activity.corporation_id,
          });
        });
      } catch (error) {
        logger.error(`Error mapping activities to MapActivity domain objects:`, error);
        logger.error(`Sample raw activity data:`, activities.slice(0, 1));
        throw error;
      }
    });
  }

  /**
   * Get map activity for a character group within a date range
   */
  async getActivityForGroup(groupId: string, startDate: Date, endDate: Date): Promise<MapActivity[]> {
    return this.executeQuery(async () => {
      // First, get all characters in the group
      const group = await this.prisma.characterGroup.findUnique({
        where: { id: groupId },
        include: { characters: true },
      });

      if (!group) {
        return [];
      }

      // Get character IDs and convert to strings
      const characterIds = group.characters.map(c => c.eveId.toString());

      // Get activity for all characters
      return this.getActivityForCharacters(characterIds, startDate, endDate);
    });
  }

  /**
   * Get map activity statistics for a character
   */
  async getActivityStats(
    characterId: string,
    startDate: Date,
    endDate: Date
  ): Promise<{
    totalSystems: number;
    totalSignatures: number;
    averageSignaturesPerSystem: number;
  }> {
    return this.executeQuery(async () => {
      const activities = await this.getActivityForCharacter(characterId, startDate, endDate);

      if (activities.length === 0) {
        return {
          totalSystems: 0,
          totalSignatures: 0,
          averageSignaturesPerSystem: 0,
        };
      }

      // Count unique activities as a proxy for systems since systemId doesn't exist
      const uniqueSystems = activities.length;

      // Sum total signatures
      const totalSignatures = activities.reduce((sum, a) => sum + a.signatures, 0);

      // Calculate average
      const averageSignaturesPerSystem = uniqueSystems > 0 ? totalSignatures / uniqueSystems : 0;

      return {
        totalSystems: uniqueSystems,
        totalSignatures,
        averageSignaturesPerSystem,
      };
    });
  }

  /**
   * Get map activity statistics for a character group
   */
  async getGroupActivityStats(
    groupId: string,
    startDate: Date,
    endDate: Date
  ): Promise<{
    totalSystems: number;
    totalSignatures: number;
    averageSignaturesPerSystem: number;
  }> {
    logger.info(
      `Getting map activity stats for group ${groupId} from ${startDate.toISOString()} to ${endDate.toISOString()}`
    );

    return this.executeQuery(async () => {
      // First, get all characters in the group
      const group = await this.prisma.characterGroup.findUnique({
        where: { id: groupId },
        include: { characters: true },
      });

      if (!group) {
        logger.warn(`Group ${groupId} not found`);
        return {
          totalSystems: 0,
          totalSignatures: 0,
          averageSignaturesPerSystem: 0,
        };
      }

      // Get character IDs
      const characterIds = group.characters.map(c => c.eveId);
      logger.info(`Group ${groupId} has ${characterIds.length} characters: ${characterIds.join(', ')}`);

      if (characterIds.length === 0) {
        logger.warn(`Group ${groupId} has no characters`);
        return {
          totalSystems: 0,
          totalSignatures: 0,
          averageSignaturesPerSystem: 0,
        };
      }

      const activities = await this.getActivityForGroup(groupId, startDate, endDate);

      if (activities.length === 0) {
        logger.warn(`No map activities found for group ${groupId}`);
        return {
          totalSystems: 0,
          totalSignatures: 0,
          averageSignaturesPerSystem: 0,
        };
      }

      logger.info(`Found ${activities.length} map activities for group ${groupId}`);

      // Count unique activities as a proxy for systems since systemId doesn't exist
      const uniqueSystems = activities.length;

      // Sum total signatures
      const totalSignatures = activities.reduce((sum, a) => sum + a.signatures, 0);

      // Calculate average
      const averageSignaturesPerSystem = uniqueSystems > 0 ? totalSignatures / uniqueSystems : 0;

      return {
        totalSystems: uniqueSystems,
        totalSignatures,
        averageSignaturesPerSystem,
      };
    });
  }

  /**
   * Get map activity grouped by time period for chart data
   */
  async getActivityGroupedByTime(
    characterIds: string[],
    startDate: Date,
    endDate: Date,
    groupBy: 'hour' | 'day' | 'week' = 'day'
  ): Promise<Array<{ timestamp: Date; signatures: number; systems: number }>> {
    return this.executeQuery(async () => {
      // Get all activity for the characters
      const activities = await this.getActivityForCharacters(characterIds, startDate, endDate);

      // Group by time period
      const timeMap = new Map<
        string,
        {
          timestamp: Date;
          signatures: number;
          systems: Set<string>; // Use a string combination of characterId+timestamp as unique identifier
        }
      >();

      // Format string for grouping
      const getTimeKey = (date: Date): string => {
        switch (groupBy) {
          case 'hour':
            return `${date.getFullYear()}-${date.getMonth()}-${date.getDate()}-${date.getHours()}`;
          case 'week': {
            const d = new Date(date);
            d.setDate(d.getDate() - d.getDay()); // Start of week (Sunday)
            return `${d.getFullYear()}-${d.getMonth()}-${d.getDate()}`;
          }
          case 'day':
          default:
            return `${date.getFullYear()}-${date.getMonth()}-${date.getDate()}`;
        }
      };

      // Group the activities
      for (const activity of activities) {
        const timeKey = getTimeKey(activity.timestamp);

        if (!timeMap.has(timeKey)) {
          timeMap.set(timeKey, {
            timestamp: new Date(activity.timestamp),
            signatures: 0,
            systems: new Set<string>(),
          });
        }

        const group = timeMap.get(timeKey);
        if (!group) continue;
        group.signatures += activity.signatures;
        // Use a composite key since systemId doesn't exist
        group.systems.add(`${activity.characterId}-${activity.timestamp.toISOString()}`);
      }

      // Convert to array and sort by timestamp
      return Array.from(timeMap.entries())
        .map(([_, group]) => ({
          timestamp: group.timestamp,
          signatures: group.signatures,
          systems: group.systems.size,
        }))
        .sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());
    });
  }

  /**
   * Create or update a map activity record
   */
  async upsertMapActivity(
    characterId: bigint,
    timestamp: Date,
    signatures: number,
    connections: number,
    passages: number,
    allianceId: number | null,
    corporationId: number | null
  ): Promise<void> {
    await this.prisma.mapActivity.upsert({
      where: {
        characterId_timestamp: {
          characterId,
          timestamp,
        },
      },
      update: {
        signatures,
        connections,
        passages,
        allianceId: allianceId ?? undefined,
        corporationId: corporationId ?? 0,
      },
      create: {
        characterId,
        timestamp,
        signatures,
        connections,
        passages,
        allianceId: allianceId ?? undefined,
        corporationId: corporationId ?? 0,
      },
    });
  }

  /**
   * Delete all map activity records
   */
  async deleteAllMapActivity(): Promise<void> {
    await this.executeQuery(async () => {
      await this.prisma.mapActivity.deleteMany();
    });
  }

  /**
   * Count total map activity records
   */
  override async count(): Promise<number> {
    return this.executeQuery(async () => {
      return this.prisma.mapActivity.count();
    });
  }
}

