import { CommandInteraction, MessageFlags, EmbedBuilder } from 'discord.js';
import { logger } from '../../logger';
import { redis } from '../../../infrastructure/cache/redis-client';

/**
 * Handler for security monitoring commands (admin only)
 */
export class SecurityMonitorHandler {
  private readonly ADMIN_USER_IDS = process.env.ADMIN_USER_IDS?.split(',') ?? [];

  async handle(interaction: CommandInteraction): Promise<void> {
    if (!interaction.isChatInputCommand()) return;

    // Check if user is admin
    if (!this.isAdmin(interaction.user.id)) {
      await interaction.reply({
        content: 'You do not have permission to use this command.',
        flags: MessageFlags.Ephemeral,
      });
      return;
    }

    const subcommand = interaction.options.getSubcommand();

    try {
      await interaction.deferReply({ flags: MessageFlags.Ephemeral });

      switch (subcommand) {
        case 'status':
          await this.handleStatus(interaction);
          break;
        case 'suspicious':
          await this.handleSuspicious(interaction);
          break;
        case 'blocked':
          await this.handleBlocked(interaction);
          break;
        case 'unblock':
          await this.handleUnblock(interaction);
          break;
        default:
          await interaction.editReply('Unknown security subcommand');
      }
    } catch (error) {
      logger.error('Error in security monitor handler:', error);
      await interaction.editReply('An error occurred while processing the command.');
    }
  }

  private isAdmin(userId: string): boolean {
    return this.ADMIN_USER_IDS.includes(userId);
  }

  private async handleStatus(interaction: CommandInteraction): Promise<void> {
    const embed = new EmbedBuilder().setTitle('Security Monitor Status').setColor(0x00aa00).setTimestamp();

    // Get rate limit stats
    const userLimitKeys = await redis.keys('ratelimit:*');
    const guildLimitKeys = await redis.keys('guild:*');
    const suspiciousKeys = await redis.keys('suspicious:*');

    embed.addFields([
      {
        name: 'Rate Limits',
        value: `Active user limits: ${userLimitKeys.length}\nActive guild limits: ${guildLimitKeys.length}\nSuspicious users: ${suspiciousKeys.length}`,
        inline: true,
      },
    ]);

    // Get blocked users count
    const blockedKeys = await redis.keys('security:abuse:blocked:*');
    embed.addFields([
      {
        name: 'Blocked Users',
        value: `Currently blocked: ${blockedKeys.length}`,
        inline: true,
      },
    ]);

    // Get recent suspicious activity count
    const recentSuspicious = await redis.keys('security:suspicious:*');
    let highSeverityCount = 0;

    for (const key of recentSuspicious.slice(0, 10)) {
      const activities = await redis.zrange(key, -10, -1);
      for (const activity of activities) {
        try {
          const parsed = JSON.parse(activity);
          if (parsed.severity === 'high') highSeverityCount++;
        } catch (e) {
          // Ignore parse errors
        }
      }
    }

    embed.addFields([
      {
        name: 'Recent Activity',
        value: `High severity incidents: ${highSeverityCount}`,
        inline: true,
      },
    ]);

    await interaction.editReply({ embeds: [embed] });
  }

  private async handleSuspicious(interaction: CommandInteraction): Promise<void> {
    const embed = new EmbedBuilder().setTitle('Recent Suspicious Activity').setColor(0xffaa00).setTimestamp();

    const suspiciousKeys = await redis.keys('security:suspicious:*');
    const recentActivities: any[] = [];

    // Get recent suspicious activities
    for (const key of suspiciousKeys.slice(0, 5)) {
      const userId = key.split(':').pop();
      const activities = await redis.zrange(key, -5, -1);

      for (const activity of activities) {
        try {
          const parsed = JSON.parse(activity);
          recentActivities.push({
            ...parsed,
            userId,
          });
        } catch (e) {
          // Ignore parse errors
        }
      }
    }

    // Sort by timestamp and take most recent
    recentActivities.sort((a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime());

    const topActivities = recentActivities.slice(0, 10);

    if (topActivities.length === 0) {
      embed.setDescription('No recent suspicious activity detected.');
    } else {
      for (const activity of topActivities) {
        const timestamp = new Date(activity.timestamp).toLocaleTimeString();
        embed.addFields([
          {
            name: `${activity.username} (${activity.userId})`,
            value: `Pattern: ${activity.pattern}\nSeverity: ${activity.severity}\nTime: ${timestamp}`,
            inline: false,
          },
        ]);
      }
    }

    await interaction.editReply({ embeds: [embed] });
  }

  private async handleBlocked(interaction: CommandInteraction): Promise<void> {
    const embed = new EmbedBuilder().setTitle('Blocked Users').setColor(0xff0000).setTimestamp();

    const blockedKeys = await redis.keys('security:abuse:blocked:*');

    if (blockedKeys.length === 0) {
      embed.setDescription('No users are currently blocked.');
    } else {
      for (const key of blockedKeys.slice(0, 10)) {
        const userId = key.split(':').pop();
        const blockData = await redis.get(key);

        if (blockData) {
          try {
            const parsed = JSON.parse(blockData);
            const ttl = await redis.ttl(key);
            embed.addFields([
              {
                name: `User ID: ${userId}`,
                value: `Reason: ${parsed.reason}\nExpires in: ${Math.floor(ttl / 60)} minutes`,
                inline: false,
              },
            ]);
          } catch (e) {
            // Ignore parse errors
          }
        }
      }
    }

    await interaction.editReply({ embeds: [embed] });
  }

  private async handleUnblock(interaction: CommandInteraction): Promise<void> {
    if (!interaction.isChatInputCommand()) {
      await interaction.editReply('This command can only be used as a chat input command.');
      return;
    }

    const userId = interaction.options.getString('user');

    if (!userId) {
      await interaction.editReply('Please provide a user ID to unblock.');
      return;
    }

    const key = `security:abuse:blocked:${userId}`;
    const existed = await redis.del(key);

    if (existed) {
      logger.info(`User ${userId} unblocked by ${interaction.user.tag}`);
      await interaction.editReply(`Successfully unblocked user ${userId}.`);
    } else {
      await interaction.editReply(`User ${userId} was not blocked.`);
    }
  }
}

import { CommandInteraction } from 'discord.js';
import { ChartFactory } from '../../../../services/charts';
import { CharacterRepository } from '../../../../infrastructure/repositories/CharacterRepository';
import { logger } from '../../../logger';
import { RepositoryManager } from '../../../../infrastructure/repositories/RepositoryManager';
import { CharacterGroup } from '../../../../domain/character/CharacterGroup';
import { MessageFlags } from 'discord.js';

/**
 * Base class for all chart command handlers
 */
export abstract class BaseChartHandler {
  protected chartFactory: ChartFactory;
  protected characterRepository: CharacterRepository;
  protected repositoryManager: RepositoryManager;

  constructor() {
    this.repositoryManager = new RepositoryManager();
    this.chartFactory = new ChartFactory();
    this.characterRepository = this.repositoryManager.getCharacterRepository();
  }

  /**
   * Handle a chart command interaction
   * This method must be implemented by all chart handlers
   */
  abstract handle(interaction: CommandInteraction): Promise<void>;

  /**
   * Convert a time period string to date range
   */
  protected getTimeRange(timePeriod: string = '7'): {
    startDate: Date;
    endDate: Date;
  } {
    const days = parseInt(timePeriod, 10) || 7;
    const endDate = new Date();
    const startDate = new Date();
    startDate.setDate(endDate.getDate() - days);

    return { startDate, endDate };
  }

  /**
   * Get all character groups from database and transform them into the format expected by chart generators
   */
  protected async getCharacterGroups(): Promise<
    Array<{
      groupId: string;
      name: string;
      characters: Array<{
        eveId: string;
        name: string;
        mainCharacterId?: string;
      }>;
      mainCharacterId?: string;
    }>
  > {
    try {
      logger.info('BaseChartHandler.getCharacterGroups() - calling characterRepository.getAllCharacterGroups()');
      const groups = await this.characterRepository.getAllCharacterGroups();
      logger.info(`BaseChartHandler.getCharacterGroups() - got ${groups.length} raw groups from repository`);

      // Filter out groups with no characters and transform to expected format
      const result = groups
        .filter((group: CharacterGroup) => group.characters.length > 0)
        .map((group: CharacterGroup) => ({
          groupId: group.id,
          name: group.name,
          characters: group.characters.map(char => ({
            eveId: char.eveId,
            name: char.name,
          })),
          mainCharacterId: group.mainCharacterId,
        }));

      logger.info(`BaseChartHandler.getCharacterGroups() - returning ${result.length} filtered groups`);
      return result;
    } catch (error) {
      logger.error('Error fetching character groups:', {
        error:
          error instanceof Error
            ? {
                name: error.name,
                message: error.message,
                stack: error.stack,
              }
            : error,
        errorType: typeof error,
        errorString: String(error),
      });
      return [];
    }
  }

  /**
   * Handle any errors that occur during command execution
   */
  protected async handleError(interaction: CommandInteraction, error: any): Promise<void> {
    const errorMessage = error instanceof Error ? error.message : String(error);
    logger.error(`Error handling chart command: ${errorMessage}`, {
      error:
        error instanceof Error
          ? {
              name: error.name,
              message: error.message,
              stack: error.stack,
            }
          : error,
      interactionId: interaction.id,
      commandName: interaction.commandName,
      subcommand: interaction.isChatInputCommand() ? interaction.options.getSubcommand(false) : 'unknown',
      replied: interaction.replied,
      deferred: interaction.deferred,
    });

    try {
      // Reply with error message
      const content = ` Error generating chart: ${errorMessage}`;

      if (interaction.deferred) {
        await interaction.editReply({ content });
      } else if (!interaction.replied) {
        await interaction.reply({
          content,
          flags: MessageFlags.Ephemeral,
        });
      } else {
        await interaction.followUp({
          content,
          flags: MessageFlags.Ephemeral,
        });
      }
    } catch (replyError) {
      logger.error('Error sending error response:', replyError);
    }
  }
}

import { BaseChartHandler } from './BaseChartHandler';
import { CommandInteraction } from 'discord.js';
import { ChartData, ChartOptions } from '../../../../types/chart';
import { ChartRenderer } from '../../../../services/ChartRenderer';
import { logger } from '../../../logger';
import { ChartFactory } from '../../../../services/charts';

/**
 * Handler for the /charts corps command
 */
export class CorpsHandler extends BaseChartHandler {
  constructor() {
    super();
  }

  /**
   * Handle the corps chart command
   */
  async handle(interaction: CommandInteraction): Promise<void> {
    if (!interaction.isChatInputCommand()) return;

    try {
      await interaction.deferReply();

      // Get time period from command options
      const time = interaction.options.getString('time') ?? '7';
      const { startDate, endDate } = this.getTimeRange(time);

      logger.info(`Generating enemy corporations chart for ${time} days`);

      // Get character groups
      const groups = await this.getCharacterGroups();

      if (groups.length === 0) {
        await interaction.editReply({
          content: 'No character groups found. Please add characters to groups first.',
        });
        return;
      }

      // Get the chart generator from the factory
      const corpsGenerator = ChartFactory.createGenerator('corps');

      // Check if view option is specified (horizontalBar, verticalBar, or pie)
      const displayType = interaction.options.getString('view') ?? 'horizontalBar';

      // Generate chart data
      const chartData = await corpsGenerator.generateChart({
        characterGroups: groups,
        startDate,
        endDate,
        displayType: displayType,
      });

      // Render chart to buffer
      logger.info(`Rendering enemy corporations chart with ${displayType} view`);
      const buffer = await this.renderChart(chartData);

      // Send the chart with summary
      await interaction.editReply({
        content: chartData.summary ?? 'Enemy Corporations Chart',
        files: [{ attachment: buffer, name: 'corps-chart.png' }],
      });

      logger.info('Successfully sent enemy corporations chart');
    } catch (error) {
      await this.handleError(interaction, error);
    }
  }

  /**
   * Render chart to buffer using appropriate options
   */
  private async renderChart(chartData: ChartData): Promise<Buffer> {
    // Create options object based on chartData.options or use defaults
    const options: ChartOptions = chartData.options ?? {
      responsive: true,
      maintainAspectRatio: false,
    };

    // Use different canvas sizes based on chart type
    if (chartData.displayType === 'pie') {
      return new ChartRenderer(2400, 2400).renderToBuffer(chartData, options);
    } else if (chartData.displayType === 'bar') {
      // Vertical bar chart
      return new ChartRenderer(3000, 1800).renderToBuffer(chartData, options);
    } else {
      // Horizontal bar chart (default)
      return new ChartRenderer(3200, 1600).renderToBuffer(chartData, options);
    }
  }
}

import { BaseChartHandler } from './BaseChartHandler';
import { CommandInteraction } from 'discord.js';
import { ChartOptions } from '../../../../types/chart';
import { ChartRenderer } from '../../../../services/ChartRenderer';
import { logger } from '../../../logger';
import { ChartFactory } from '../../../../services/charts';

/**
 * Handler for the /charts distribution command
 */
export class DistributionHandler extends BaseChartHandler {
  private chartRenderer: ChartRenderer;

  constructor() {
    super();
    this.chartRenderer = new ChartRenderer();
  }

  /**
   * Handle a distribution chart command
   */
  async handle(interaction: CommandInteraction): Promise<void> {
    if (!interaction.isChatInputCommand()) return;

    try {
      await interaction.deferReply();

      // Extract options from the command
      const time = interaction.options.getString('time') ?? '7';
      const displayOption = interaction.options.getString('display') ?? 'pie';

      // Get the time range
      const { startDate, endDate } = this.getTimeRange(time);

      // Get all character groups
      const characterGroups = await this.characterRepository.getAllCharacterGroups();

      if (characterGroups.length === 0) {
        await interaction.editReply('No character groups found. Please add characters first.');
        return;
      }

      // Transform character groups into the format expected by the chart generator
      const transformedGroups = characterGroups.map(group => ({
        groupId: group.id,
        name: group.name,
        characters: group.characters.map(char => ({
          eveId: char.eveId,
          name: char.name,
        })),
        mainCharacterId: group.mainCharacterId,
      }));

      // Get the chart generator
      const generator = ChartFactory.createGenerator('distribution');

      // Generate the chart data
      const chartData = await generator.generateChart({
        startDate,
        endDate,
        characterGroups: transformedGroups,
        displayType: displayOption,
      });

      // Render the chart
      const buffer = await this.chartRenderer.renderToBuffer(chartData, chartData.options as ChartOptions);

      // Send the chart image
      await interaction.editReply({
        content: chartData.summary,
        files: [{ attachment: buffer, name: 'distribution-chart.png' }],
      });
