import { logger } from '../../../logger';
import { ChartFactory } from '../../../../services/charts/ChartFactory';
import { theme } from '../../../../services/charts/config/theme';

/**
 * Handler for the /charts kills command
 */
export class KillsHandler extends BaseChartHandler {
  constructor() {
    super();
  }

  /**
   * Handle the kills chart command
   */
  async handle(interaction: CommandInteraction): Promise<void> {
    if (!interaction.isChatInputCommand()) return;

    try {
      await interaction.deferReply();

      // Get time period from command options
      const time = interaction.options.getString('time') ?? '7';
      const { startDate, endDate } = this.getTimeRange(time);

      logger.info(
        `Generating kills chart for ${time} days - Date range: ${startDate.toISOString()} to ${endDate.toISOString()}`
      );

      // Get character groups
      const groups = await this.getCharacterGroups();

      logger.info(`Found ${groups.length} character groups for chart generation`);
      logger.info(
        `First few groups: ${groups
          .slice(0, 3)
          .map(g => g.name)
          .join(', ')}${groups.length > 3 ? '...' : ''}`
      );

      // Log total character count across all groups
      const totalCharacters = groups.reduce((sum, group) => sum + group.characters.length, 0);
      logger.info(`Total character count across all groups: ${totalCharacters}`);

      if (groups.length === 0) {
        await interaction.editReply({
          content: 'No character groups found. Please add characters to groups first.',
        });
        return;
      }

      // Get the appropriate chart generator
      const generator = ChartFactory.createGenerator('kills');

      // Generate chart data
      logger.info(`Generating chart data with ${groups.length} groups...`);
      const chartData = await generator.generateChart({
        characterGroups: groups,
        startDate,
        endDate,
        displayType: 'horizontalBar',
      });

      // Log chart results
      logger.info(`Chart data generated with ${chartData.labels?.length ?? 0} labels`);
      if (chartData.datasets?.length > 0) {
        logger.info(
          `First dataset (${chartData.datasets[0].label}) has ${chartData.datasets[0].data.length} data points`
        );
      }

      // Render chart to buffer
      logger.info('Rendering kills chart');
      const buffer = await this.renderChart(chartData);

      // Send the chart with summary - make sure not to return this value
      await interaction.editReply({
        content: chartData.summary ?? 'Kills chart',
        files: [{ attachment: buffer, name: 'kills-chart.png' }],
      });

      logger.info('Successfully sent kills chart');
    } catch (error) {
      await this.handleError(interaction, error);
    }
  }

  /**
   * Render chart to buffer using appropriate options
   */
  private async renderChart(chartData: ChartData): Promise<Buffer> {
    // Create a simpler options object that conforms to the ChartOptions interface
    const options: ChartOptions = {
      indexAxis: 'y', // Horizontal bar chart
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        title: {
          display: true,
          text: chartData.title ?? 'Kills by Character Group',
          font: {
            size: 40, // Extra large title
            weight: 'bold',
          },
        },
        legend: {
          position: 'top',
          labels: {
            color: theme.text.primary,
            font: {
              size: 12,
            },
          },
        },
        tooltip: {
          backgroundColor: theme.colors.background,
          titleColor: theme.text.primary,
          bodyColor: theme.text.primary,
          borderColor: theme.colors.primary,
          borderWidth: 1,
          padding: 10,
          displayColors: true,
          callbacks: {
            label: context => {
              const label = context.dataset.label ?? '';
              const parsed = context.parsed as { x?: number; y?: number } | number;
              const value = typeof parsed === 'object' ? (parsed.x ?? parsed.y ?? 0) : parsed;
              return `${label}: ${value}`;
            },
          },
        },
      },
      scales: {
        x: {
          grid: {
            color: theme.grid.color,
          },
          ticks: {
            color: theme.text.primary,
            font: {
              size: 12,
            },
          },
        },
        y: {
          grid: {
            color: theme.grid.color,
          },
          ticks: {
            color: theme.text.primary,
            font: {
              size: 12,
            },
          },
        },
      },
    };

    // Use a very wide canvas for horizontal bar chart display in Discord
    const renderer = new ChartRenderer(3200, 1280);

    // Set the data point styling
    if (chartData.datasets.length >= 2) {
      // Set distinct colors for total kills vs solo kills
      // Use solid colors for better visibility in Discord
      if (chartData.datasets[0].label === 'Total Kills') {
        chartData.datasets[0].backgroundColor = '#3366CC'; // Blue for total kills
      }

      if (chartData.datasets[1].label === 'Solo Kills') {
        chartData.datasets[1].backgroundColor = '#DC3912'; // Red for solo kills
      }

      // Add borders for better definition
      chartData.datasets[0].borderColor = '#1A478F'; // Darker blue border
      chartData.datasets[1].borderColor = '#8F1A1A'; // Darker red border

      // Log the solo kill values
      logger.debug('Solo kills dataset', {
        data: chartData.datasets[1].data,
      });
    }

    // Pass additional styling options to the renderer
    return renderer.renderToBuffer(chartData, options);
  }
}

import { BaseChartHandler } from './BaseChartHandler';
import { CommandInteraction, EmbedBuilder } from 'discord.js';
import { logger } from '../../../logger';

/**
 * Handler for the /charts list command
 * Displays a list of all available chart types
 */
export class ListHandler extends BaseChartHandler {
  constructor() {
    super();
  }

  /**
   * Handle the list command interaction
   */
  async handle(interaction: CommandInteraction): Promise<void> {
    try {
      logger.info('Handling list subcommand');

      // Define available subcommands directly to avoid circular dependency
      const subcommands = [
        'list',
        'kills',
        'map',
        'loss',
        'ratio',
        'shipkill',
        'shiploss',
        'distribution',
        'corps',
        'heatmap',
        'efficiency',
      ];

      // Map subcommands to friendly names/descriptions
      const chartDescriptions: Record<string, { name: string; value: string }> = {
        kills: {
          name: ' /charts kills [time]',
          value: 'Show kill activity by character group with stacked horizontal bars',
        },
        map: {
          name: ' /charts map [time]',
          value: 'Show map activity by character group with stacked horizontal bars',
        },
        loss: {
          name: ' /charts loss [time]',
          value: 'Show ship loss activity by character group',
        },
        ratio: {
          name: ' /charts ratio [time]',
          value: 'Show kill-death ratio by character group',
        },
        shipkill: {
          name: ' /charts shipkill [time]',
          value: 'Show top ship types destroyed by your group(s) in the selected period.',
        },
        shiploss: {
          name: ' /charts shiploss [time]',
          value: 'Show top ship types lost by your group(s) in the selected period.',
        },
        distribution: {
          name: ' /charts distribution [time]',
          value: 'Show pie chart of solo vs. group kills',
        },
        corps: {
          name: ' /charts corps [time]',
          value: 'Show kills per enemy corporation',
        },
        heatmap: {
          name: ' /charts heatmap [time]',
          value: 'Show heatmap of kill activity by hour and day of week',
        },
        efficiency: {
          name: ' /charts efficiency [time]',
          value: 'Show efficiency metrics with gauge charts',
        },
      };

      // Build fields for the embed
      const fields = subcommands
        .filter(cmd => chartDescriptions[cmd] && cmd !== 'heatmap' && cmd !== 'trend' && cmd !== 'shiptypes')
        .map(cmd => chartDescriptions[cmd]);

      const embed = new EmbedBuilder()
        .setTitle('Available Chart Types')
        .setColor('#0099ff')
        .setDescription('Here are all the available chart types:')
        .addFields(fields)
        .setFooter({
          text: 'Use the specific command to generate that chart type',
        });

      await interaction.reply({ embeds: [embed] });
      logger.info('Successfully sent list of chart types');
    } catch (error) {
      logger.error('Error handling list command:', error);

      // Send a generic error message to the user
      await interaction
        .reply({
          content: 'Sorry, there was an error processing your request. Please try again later.',
          ephemeral: true,
        })
        .catch(e => {
          logger.error('Error sending error response:', e);
        });
    }
  }
}

import { BaseChartHandler } from './BaseChartHandler';
import { CommandInteraction } from 'discord.js';
import { ChartData } from '../../../../types/chart';
import { ChartRenderer } from '../../../../services/ChartRenderer';
import { logger } from '../../../logger';
import { TooltipItem, Scale } from 'chart.js';
import { ChartFactory } from '../../../../services/charts';

/**
 * Handler for the /charts loss command
 */
export class LossHandler extends BaseChartHandler {
  constructor() {
    super();
  }

  /**
   * Handle the ship loss chart command
   */
  async handle(interaction: CommandInteraction): Promise<void> {
    if (!interaction.isChatInputCommand()) return;

    try {
      await interaction.deferReply();

      // Get time period from command options
      const time = interaction.options.getString('time') ?? '7';
      const { startDate, endDate } = this.getTimeRange(time);

      logger.info(`Generating loss chart for ${time} days`);

      // Get character groups
      const groups = await this.getCharacterGroups();

      if (groups.length === 0) {
        await interaction.editReply({
          content: 'No character groups found. Please add characters to groups first.',
        });
        return;
      }

      // Get the chart generator from the factory
      const lossGenerator = ChartFactory.createGenerator('loss');

      // Generate chart data
      const chartData = await lossGenerator.generateChart({
        characterGroups: groups,
        startDate,
        endDate,
        displayType: 'horizontalBar',
      });

      // Render chart to buffer
      const buffer = await this.renderChart(chartData);

      // Send the chart with summary
      await interaction.editReply({
        content: chartData.summary ?? 'Loss chart',
        files: [{ attachment: buffer, name: 'loss-chart.png' }],
      });

      logger.info('Successfully sent loss chart');
    } catch (error) {
      logger.error('Error generating loss chart:', error);
      await this.handleError(interaction, error);
    }
  }

  /**
   * Render chart to buffer using appropriate options
   */
  private async renderChart(chartData: ChartData): Promise<Buffer> {
    const options = {
      indexAxis: 'y' as const,
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        title: {
          display: true,
          text: chartData.title ?? 'Losses by Character Group',
          font: {
            size: 40,
            weight: 'bold',
          },
        },
        legend: {
          display: true,
          position: 'top' as const,
        },
        tooltip: {
          callbacks: {
            label: (context: TooltipItem<'bar'>) => {
              const label = context.dataset.label ?? '';
              const value = context.parsed.y;
              if (label.includes('ISK')) {
                return `${label}: ${value.toLocaleString()} B`;
              }
              return `${label}: ${value.toLocaleString()}`;
            },
          },
        },
      },
      scales: {
        x: {
          stacked: true,
          title: {
            display: true,
            text: 'ISK Lost',
            font: {
              size: 20,
            },
          },
          ticks: {
            callback: function (this: Scale, value: string | number) {
              if (typeof value === 'number') {
                return value.toLocaleString();
              }
              return value;
            },
          },
        },
        y: {
          stacked: true,
          beginAtZero: true,
          title: {
            display: true,
            text: 'Character',
            font: {
              size: 20,
            },
          },
        },
      },
    };

    // Use a much larger canvas for better display in Discord without needing to click
    const renderer = new ChartRenderer(2200, 1400);
    return renderer.renderToBuffer(chartData, options);
  }
}

import { BaseChartHandler } from './BaseChartHandler';
