      // Get time period from command options
      const time = interaction.options.getString('time') ?? '7';
      const { startDate, endDate } = this.getTimeRange(time);

      logger.info(`Generating trend chart for ${time} days`);

      // Get character groups
      const groups = await this.getCharacterGroups();

      if (groups.length === 0) {
        await interaction.editReply({
          content: 'No character groups found. Please add characters to groups first.',
        });
        return;
      }

      // Get the chart generator from the factory
      const trendGenerator = ChartFactory.createGenerator('trend');

      // Check if view option is specified (line, area, or dual)
      const displayType = interaction.options.getString('view') ?? 'line';

      // Generate chart data
      const chartData = await trendGenerator.generateChart({
        characterGroups: groups,
        startDate,
        endDate,
        displayType: displayType,
      });

      // Render chart to buffer
      logger.info(`Rendering trend chart with ${displayType} view`);
      const buffer = await this.renderChart(chartData);

      // Send the chart with summary
      await interaction.editReply({
        content: chartData.summary ?? 'Trend Chart',
        files: [{ attachment: buffer, name: 'trend-chart.png' }],
      });

      logger.info('Successfully sent trend chart');
    } catch (error) {
      await this.handleError(interaction, error);
    }
  }

  /**
   * Render chart to buffer using appropriate options
   */
  private async renderChart(chartData: ChartData): Promise<Buffer> {
    // Create options object based on chartData.options or use defaults
    const options: ChartOptions =
      chartData.options ??
      ({
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          title: {
            display: true,
            text: chartData.title ?? 'Kill Activity Over Time',
            font: {
              size: 40,
              weight: 'bold',
            },
          },
          legend: {
            display: true,
            position: 'top',
          },
        },
        scales: {
          y: {
            beginAtZero: true,
            title: {
              display: true,
              text: 'Kill Count',
            },
          },
          x: {
            type: 'time',
            time: {
              unit: 'day',
              displayFormats: {
                hour: 'MMM d, HH:mm',
                day: 'MMM d',
                week: 'MMM d, yyyy',
                month: 'MMM yyyy',
              },
            },
            title: {
              display: true,
              text: 'Date',
            },
          },
        },
      } as ChartOptions); // Cast to ChartOptions to ensure type compatibility

    // For dual-axis charts, use wide format
    if (chartData.options?.scales?.y2) {
      return new ChartRenderer(3200, 1600).renderToBuffer(chartData, options);
    }

    // For area charts, use slightly taller format
    if (chartData.datasets.some(d => d.fill === true)) {
      return new ChartRenderer(3000, 1800).renderToBuffer(chartData, options);
    }

    // Default format for line charts
    return new ChartRenderer(3000, 1500).renderToBuffer(chartData, options);
  }
}

import { CommandInteraction, MessageFlags } from 'discord.js';
import { BaseChartHandler } from './BaseChartHandler';
import { validateCommand, validateInteraction, logCommandUsage, CommandName } from '../../validation';
import { logger } from '../../../logger';

/**
 * Enhanced base class for chart handlers with built-in validation and rate limiting
 */
export abstract class ValidatedBaseChartHandler extends BaseChartHandler {
  /**
   * The command name for validation purposes
   */
  protected abstract readonly commandName: CommandName;

  /**
   * Whether to use strict rate limiting for this command
   */
  protected readonly useStrictRateLimit: boolean = false;

  /**
   * Handle the command with validation and rate limiting
   */
  override async handle(interaction: CommandInteraction): Promise<void> {
    const startTime = Date.now();
    let success = false;
    let error: string | undefined;

    try {
      // Perform validation and rate limiting
      const validationResult = await validateInteraction(interaction, {
        customRateLimitConfig: this.useStrictRateLimit
          ? {
              maxRequests: 3,
              windowMs: 60000,
            }
          : undefined,
      });

      if (!validationResult.valid) {
        error = validationResult.error;
        return;
      }

      // Validate command parameters
      const commandValidation = await validateCommand(interaction, this.commandName);

      if (!commandValidation.success) {
        error = commandValidation.error ?? 'Invalid parameters';

        // Send user-friendly error message
        const errorDetails = commandValidation.details?.errors
          .map(err => ` ${err.path.join('.')}: ${err.message}`)
          .join('\n');

        await interaction.reply({
          content: ` Invalid command parameters:\n${errorDetails ?? error}`,
          flags: MessageFlags.Ephemeral,
        });
        return;
      }

      // Call the implementation with validated data
      await this.handleValidated(interaction, commandValidation.data);
      success = true;
    } catch (err) {
      error = err instanceof Error ? err.message : String(err);
      await this.handleError(interaction, err);
    } finally {
      // Log command usage
      logCommandUsage(interaction, success, error);

      // Log performance metrics
      const duration = Date.now() - startTime;
      logger.info(`Command execution completed in ${duration}ms`, {
        command: this.commandName,
        duration,
        success,
        userId: interaction.user.id,
        guildId: interaction.guildId,
      });
    }
  }

  /**
   * Handle the command with validated data
   * This method must be implemented by subclasses
   */
  protected abstract handleValidated(interaction: CommandInteraction, validatedData: any): Promise<void>;

  /**
   * Enhanced error handler with better error messages
   */
  protected override async handleError(interaction: CommandInteraction, error: any): Promise<void> {
    const errorMessage = error instanceof Error ? error.message : String(error);

    // Log detailed error information
    logger.error(`Error in ${this.commandName} command:`, {
      error:
        error instanceof Error
          ? {
              name: error.name,
              message: error.message,
              stack: error.stack,
            }
          : error,
      command: this.commandName,
      userId: interaction.user.id,
      guildId: interaction.guildId,
      interactionId: interaction.id,
    });

    // Determine user-friendly error message
    let userMessage = ' An error occurred while generating the chart.';

    // Common error patterns
    if (errorMessage.includes('timeout')) {
      userMessage = ' The request timed out. Please try again with a smaller time range.';
    } else if (errorMessage.includes('rate limit')) {
      userMessage = ' You are being rate limited. Please try again later.';
    } else if (errorMessage.includes('no data')) {
      userMessage = ' No data found for the specified time period.';
    } else if (errorMessage.includes('permission')) {
      userMessage = ' You do not have permission to use this command.';
    }

    try {
      const content = userMessage;

      if (interaction.deferred) {
        await interaction.editReply({ content });
      } else if (!interaction.replied) {
        await interaction.reply({
          content,
          flags: MessageFlags.Ephemeral,
        });
      } else {
        await interaction.followUp({
          content,
          flags: MessageFlags.Ephemeral,
        });
      }
    } catch (replyError) {
      logger.error('Error sending error response:', replyError);
    }
  }
}

import { CommandInteraction } from 'discord.js';
import { ValidatedBaseChartHandler } from './ValidatedBaseChartHandler';
import { ChartData, ChartOptions } from '../../../../types/chart';
import { ChartRenderer } from '../../../../services/ChartRenderer';
import { logger } from '../../../logger';
import { ChartFactory } from '../../../../services/charts/ChartFactory';
import { theme } from '../../../../services/charts/config/theme';
import { CommandName, CommandSchema } from '../../validation/schemas';

/**
 * Validated handler for the /charts kills command
 */
export class ValidatedKillsHandler extends ValidatedBaseChartHandler {
  protected readonly commandName: CommandName = 'kills';

  constructor() {
    super();
  }

  /**
   * Handle the kills chart command with validated data
   */
  protected async handleValidated(
    interaction: CommandInteraction,
    validatedData: CommandSchema<'kills'>
  ): Promise<void> {
    if (!interaction.isChatInputCommand()) return;

    await interaction.deferReply();

    // Use validated time parameter
    const { startDate, endDate } = this.getTimeRange(validatedData.time.toString());

    logger.info(
      `Generating kills chart for ${validatedData.time} days - Date range: ${startDate.toISOString()} to ${endDate.toISOString()}`
    );

    // Get character groups
    const groups = await this.getCharacterGroups();

    logger.info(`Found ${groups.length} character groups for chart generation`);

    // Log total character count across all groups
    const totalCharacters = groups.reduce((sum, group) => sum + group.characters.length, 0);
    logger.info(`Total character count across all groups: ${totalCharacters}`);

    if (groups.length === 0) {
      await interaction.editReply({
        content: 'No character groups found. Please add characters to groups first.',
      });
      return;
    }

    // Get the appropriate chart generator
    const generator = ChartFactory.createGenerator('kills');

    // Generate chart data
    logger.info(`Generating chart data with ${groups.length} groups...`);
    const chartData = await generator.generateChart({
      characterGroups: groups,
      startDate,
      endDate,
      displayType: 'horizontalBar',
    });

    // Log chart results
    logger.info(`Chart data generated with ${chartData.labels?.length ?? 0} labels`);

    // Render chart to buffer
    logger.info('Rendering kills chart');
    const buffer = await this.renderChart(chartData);

    // Send the chart with summary
    await interaction.editReply({
      content: chartData.summary ?? 'Kills chart',
      files: [{ attachment: buffer, name: 'kills-chart.png' }],
    });

    logger.info('Successfully sent kills chart');
  }

  /**
   * Render chart to buffer using appropriate options
   */
  private async renderChart(chartData: ChartData): Promise<Buffer> {
    const options: ChartOptions = {
      indexAxis: 'y', // Horizontal bar chart
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        title: {
          display: true,
          text: chartData.title ?? 'Kills by Character Group',
          font: {
            size: 40,
            weight: 'bold',
          },
        },
        legend: {
          position: 'top',
          labels: {
            color: theme.text.primary,
            font: {
              size: 12,
            },
          },
        },
        tooltip: {
          backgroundColor: theme.colors.background,
          titleColor: theme.text.primary,
          bodyColor: theme.text.primary,
          borderColor: theme.colors.primary,
          borderWidth: 1,
          padding: 10,
          displayColors: true,
          callbacks: {
            label: context => {
              const label = context.dataset.label ?? '';
              const parsed = context.parsed as { x?: number; y?: number } | number;
              const value = typeof parsed === 'object' ? (parsed.x ?? parsed.y ?? 0) : parsed;
              return `${label}: ${value}`;
            },
          },
        },
      },
      scales: {
        x: {
          grid: {
            color: theme.grid.color,
          },
          ticks: {
            color: theme.text.primary,
            font: {
              size: 12,
            },
          },
        },
        y: {
          grid: {
            color: theme.grid.color,
          },
          ticks: {
            color: theme.text.primary,
            font: {
              size: 12,
            },
          },
        },
      },
    };

    // Use a very wide canvas for horizontal bar chart display in Discord
    const renderer = new ChartRenderer(3200, 1280);

    // Set the data point styling
    if (chartData.datasets.length >= 2) {
      // Set distinct colors for total kills vs solo kills
      if (chartData.datasets[0].label === 'Total Kills') {
        chartData.datasets[0].backgroundColor = '#3366CC'; // Blue for total kills
      }

      if (chartData.datasets[1].label === 'Solo Kills') {
        chartData.datasets[1].backgroundColor = '#DC3912'; // Red for solo kills
      }

      // Add borders for better definition
      chartData.datasets[0].borderColor = '#1A478F'; // Darker blue border
      chartData.datasets[1].borderColor = '#8F1A1A'; // Darker red border

      // Log the solo kill values
      logger.debug('Solo kills dataset', {
        data: chartData.datasets[1].data,
      });
    }

    // Pass additional styling options to the renderer
    return renderer.renderToBuffer(chartData, options);
  }
}

import { CommandInteraction } from 'discord.js';
import { BaseChartHandler } from './BaseChartHandler';
import { ValidatedBaseChartHandler } from './ValidatedBaseChartHandler';
import { CommandName } from '../../validation/schemas';

/**
 * Factory function to create a validated handler from an existing handler
 * This allows gradual migration of existing handlers to use validation
 */
export function createValidatedHandler(
  Handler: typeof BaseChartHandler,
  commandName: CommandName,
  useStrictRateLimit: boolean = false
): new () => BaseChartHandler {
  return class ConcreteValidatedHandler extends ValidatedBaseChartHandler {
    protected override readonly commandName = commandName;
    protected override readonly useStrictRateLimit = useStrictRateLimit;
    private innerHandler: BaseChartHandler;

    constructor() {
      super();
      // @ts-ignore - Handler might have different constructor signature
      this.innerHandler = new Handler();
    }

    protected override async handleValidated(interaction: CommandInteraction, validatedData: any): Promise<void> {
      // Monkey-patch the interaction to use validated data
      if (interaction.isChatInputCommand()) {
        const originalGetString = interaction.options.getString.bind(interaction.options);

        // Override getString to return validated data
        interaction.options.getString = (name: string) => {
          if (name in validatedData) {
            return validatedData[name]?.toString() || null;
          }
          return originalGetString(name);
        };
      }

      // Call the original handler
      await this.innerHandler.handle(interaction);
    }
  };
}

import { CommandInteraction, MessageFlags } from 'discord.js';
import { z } from 'zod';
import { logger } from '../../logger';
import { commandSchemas, CommandName } from './schemas';
import { rateLimiters, rateLimitConfigs } from './rateLimiter';
import { securityMonitor } from './securityMonitor';

export interface ValidationResult<T> {
  success: boolean;
  data?: T;
  error?: string;
  details?: z.ZodError;
}

export interface SecurityCheckResult {
  passed: boolean;
  reason?: string;
  suspicious?: boolean;
}

/**
 * Validates command inputs using Zod schemas
 */
export async function validateCommand<T extends CommandName>(
  interaction: CommandInteraction,
  commandName: T
): Promise<ValidationResult<z.infer<(typeof commandSchemas)[T]>>> {
  try {
    const schema = commandSchemas[commandName];
    if (!schema) {
      return {
        success: false,
        error: `No validation schema found for command: ${commandName}`,
      };
    }

    // Extract options from interaction
    const options: Record<string, any> = {};

    if (interaction.isChatInputCommand()) {
      interaction.options.data.forEach(option => {
        options[option.name] = option.value;
      });
    }

    // Validate with Zod
    const result = schema.safeParse(options);

    if (result.success) {
      return {
        success: true,
        data: result.data,
      };
    } else {
      logger.warn(`Validation failed for command ${commandName}:`, {
        errors: result.error.errors,
        userId: interaction.user.id,
        guildId: interaction.guildId,
      });

      // Track failed validation
      await securityMonitor.trackFailedValidation(interaction.user.id);

      return {
        success: false,
        error: 'Invalid command parameters',
        details: result.error,
      };
    }
  } catch (error) {
    logger.error(`Validation error for command ${commandName}:`, error);
    return {
      success: false,
      error: 'An error occurred during validation',
    };
  }
}

/**
 * Performs security checks on the interaction
 */
export async function performSecurityChecks(interaction: CommandInteraction): Promise<SecurityCheckResult> {
  const checks: SecurityCheckResult = { passed: true };

  // Check 1: User account age
  const accountAge = Date.now() - interaction.user.createdTimestamp;
  const minAccountAge = 7 * 24 * 60 * 60 * 1000; // 7 days

  if (accountAge < minAccountAge) {
    logger.warn('New account attempting to use commands:', {
      userId: interaction.user.id,
      accountAge: Math.floor(accountAge / 1000 / 60 / 60), // hours
      username: interaction.user.username,
    });
    checks.suspicious = true;
  }

  // Check 2: Guild member duration (if in a guild)
  if (interaction.inGuild() && interaction.member) {
    const member = interaction.guild?.members.cache.get(interaction.user.id);
    if (member?.joinedTimestamp) {
      const memberDuration = Date.now() - member.joinedTimestamp;
      const minMemberDuration = 10 * 60 * 1000; // 10 minutes

      if (memberDuration < minMemberDuration) {
        logger.warn('Very new guild member attempting to use commands:', {
          userId: interaction.user.id,
          guildId: interaction.guildId,
          memberDuration: Math.floor(memberDuration / 1000), // seconds
          username: interaction.user.username,
        });
        checks.suspicious = true;
      }
    }
  }

  // Check 3: Detect suspicious patterns in username
  const suspiciousPatterns = [
    /discord\.gg/i,
    /bit\.ly/i,
    /tinyurl/i,
    /free.*nitro/i,
    /steam.*gift/i,
    /@everyone/,
    /@here/,
  ];

  const username = interaction.user.username;
  const hasSuspiciousUsername = suspiciousPatterns.some(pattern => pattern.test(username));

  if (hasSuspiciousUsername) {
    logger.warn('Suspicious username pattern detected:', {
      userId: interaction.user.id,
      username: interaction.user.username,
      guildId: interaction.guildId,
    });
    checks.suspicious = true;
  }

  // Check 4: Bot detection
  if (interaction.user.bot) {
    checks.passed = false;
    checks.reason = 'Bots cannot use this command';
  }

  return checks;
}

/**
 * Main validation middleware for Discord commands
 */
export async function validateInteraction(
  interaction: CommandInteraction,
  options?: {
    skipRateLimit?: boolean;
    skipSecurity?: boolean;
    customRateLimitConfig?: any;
  }
): Promise<{ valid: boolean; error?: string }> {
  try {
    // Check if user is blocked
    const blockStatus = await securityMonitor.isUserBlocked(interaction.user.id);
    if (blockStatus.blocked) {
      await interaction.reply({
        content: `You have been temporarily blocked. Reason: ${blockStatus.reason ?? 'Suspicious activity detected'}`,
        flags: MessageFlags.Ephemeral,
      });
      return { valid: false, error: 'User blocked' };
    }

    // Track command usage
    await securityMonitor.trackCommandUsage(interaction);
    // Security checks
    if (!options?.skipSecurity) {
      const securityCheck = await performSecurityChecks(interaction);

      if (!securityCheck.passed) {
        await interaction.reply({
          content: securityCheck.reason ?? 'Security check failed',
          flags: MessageFlags.Ephemeral,
        });
        return { valid: false, error: securityCheck.reason };
      }

      // Apply stricter rate limits for suspicious users
      if (securityCheck.suspicious && !options?.skipRateLimit) {
        const suspiciousLimit = await rateLimiters.user.checkLimit(interaction.user.id, {
          ...rateLimitConfigs.suspicious,
          keyPrefix: 'suspicious',
        });

        if (!suspiciousLimit.allowed) {
          logger.warn('Suspicious user rate limited:', {
            userId: interaction.user.id,
            username: interaction.user.username,
            retryAfter: suspiciousLimit.retryAfter,
          });

          await interaction.reply({
            content: `You are being rate limited. Please try again in ${suspiciousLimit.retryAfter} seconds.`,
            flags: MessageFlags.Ephemeral,
          });
          return { valid: false, error: 'Rate limited (suspicious activity)' };
        }
      }
    }

    // Check for abuse patterns
    const abusePatterns = await securityMonitor.checkAbusePatterns(interaction);
    if (abusePatterns.length > 0 && abusePatterns.some(p => p.severity === 'high')) {
      logger.warn('High severity abuse pattern detected:', {
        userId: interaction.user.id,
        patterns: abusePatterns,
      });
    }

    // Rate limiting
    if (!options?.skipRateLimit) {
      // Per-user rate limit
      const userRateLimit = await rateLimiters.user.checkLimit(
        interaction.user.id,
        options?.customRateLimitConfig ?? rateLimitConfigs.charts
      );

      if (!userRateLimit.allowed) {
        await interaction.reply({
          content: `You are being rate limited. Please try again in ${userRateLimit.retryAfter} seconds.`,
          flags: MessageFlags.Ephemeral,
        });
        return { valid: false, error: 'User rate limited' };
      }

      // Per-guild rate limit (if in a guild)
      if (interaction.guildId) {
        const guildRateLimit = await rateLimiters.guild.checkLimit(interaction.guildId, {
          ...rateLimitConfigs.guild,
          keyPrefix: 'guild',
        });

        if (!guildRateLimit.allowed) {
          await interaction.reply({
            content: 'This server is being rate limited. Please try again later.',
            flags: MessageFlags.Ephemeral,
          });
          return { valid: false, error: 'Guild rate limited' };
        }
      }
    }

    return { valid: true };
  } catch (error) {
    logger.error('Error in validation middleware:', error);
    return { valid: false, error: 'Validation error occurred' };
  }
}

/**
 * Sanitizes user input to prevent injection attacks
 */
export function sanitizeInput(input: string, maxLength: number = 100): string {
  // Remove null bytes
  let sanitized = input.replace(/\0/g, '');

  // Trim whitespace
  sanitized = sanitized.trim();

  // Limit length
  if (sanitized.length > maxLength) {
    sanitized = sanitized.substring(0, maxLength);
  }

  // Remove control characters except newlines and tabs
  // Remove control characters except newlines and tabs
  // eslint-disable-next-line no-control-regex
  sanitized = sanitized.replace(/[\x00-\x08\x0B\x0C\x0E-\x1F\x7F]/g, '');

  return sanitized;
}

/**
 * Logs command usage for monitoring
 */
export function logCommandUsage(interaction: CommandInteraction, success: boolean, error?: string): void {
  const logData = {
    userId: interaction.user.id,
    username: interaction.user.username,
    command: interaction.commandName,
    subcommand: interaction.isChatInputCommand() ? interaction.options.getSubcommand(false) : undefined,
    guildId: interaction.guildId,
    guildName: interaction.guild?.name,
    channelId: interaction.channelId,
    success,
    error,
    timestamp: new Date().toISOString(),
  };

  if (success) {
    logger.info('Command executed successfully:', logData);
  } else {
    logger.warn('Command execution failed:', logData);
  }

  // Log suspicious patterns for analysis
  if (error?.includes('suspicious') || error?.includes('rate limit')) {
    logger.warn('Potential abuse detected:', {
      ...logData,
      userCreatedAt: interaction.user.createdAt,
      memberJoinedAt: interaction.inGuild()
        ? interaction.guild?.members.cache.get(interaction.user.id)?.joinedAt
        : undefined,
    });
  }
}

import { Redis } from 'ioredis';
import { logger } from '../../logger';
import { redis } from '../../../infrastructure/cache/redis-client';

export interface RateLimitConfig {
  maxRequests: number;
  windowMs: number;
  keyPrefix?: string;
}

export interface RateLimitResult {
  allowed: boolean;
  limit: number;
  remaining: number;
  resetAt: Date;
  retryAfter?: number;
}

export class RateLimiter {
  private redis: Redis;
  private defaultConfig: RateLimitConfig = {
    maxRequests: 10,
    windowMs: 60000, // 1 minute
    keyPrefix: 'ratelimit',
  };

  constructor(redisClient?: Redis) {
    this.redis = redisClient ?? redis;
  }

  /**
   * Check if a request is allowed based on rate limits
   */
  async checkLimit(identifier: string, config?: Partial<RateLimitConfig>): Promise<RateLimitResult> {
    const finalConfig = { ...this.defaultConfig, ...config };
    const key = `${finalConfig.keyPrefix}:${identifier}`;
    const now = Date.now();
    const windowStart = now - finalConfig.windowMs;

    try {
      // Use Redis pipeline for atomic operations
      const pipeline = this.redis.pipeline();

      // Remove old entries outside the window
      pipeline.zremrangebyscore(key, '-inf', windowStart);

      // Count current requests in window
      pipeline.zcard(key);

      // Add current request
      pipeline.zadd(key, now, `${now}-${Math.random()}`);

      // Set expiry
      pipeline.expire(key, Math.ceil(finalConfig.windowMs / 1000));

      const results = await pipeline.exec();

      if (!results) {
        throw new Error('Redis pipeline execution failed');
      }

      const count = (results[1][1] as number) + 1; // Current count + new request
      const allowed = count <= finalConfig.maxRequests;

      // If not allowed, remove the request we just added
      if (!allowed) {
        await this.redis.zrem(key, `${now}-${Math.random()}`);
      }

      const remaining = Math.max(0, finalConfig.maxRequests - count);
      const oldestEntry = await this.redis.zrange(key, 0, 0, 'WITHSCORES');
      const resetAt =
        oldestEntry.length > 1
          ? new Date(parseInt(oldestEntry[1]) + finalConfig.windowMs)
          : new Date(now + finalConfig.windowMs);

      const result: RateLimitResult = {
        allowed,
        limit: finalConfig.maxRequests,
        remaining: allowed ? remaining : remaining + 1,
        resetAt,
      };

      if (!allowed) {
        result.retryAfter = Math.ceil((resetAt.getTime() - now) / 1000);
      }

      return result;
    } catch (error) {
      logger.error('Rate limiter error:', error);
      // On error, allow the request but log it
      return {
        allowed: true,
        limit: finalConfig.maxRequests,
        remaining: finalConfig.maxRequests,
        resetAt: new Date(now + finalConfig.windowMs),
      };
    }
  }

  /**
   * Get current rate limit status without incrementing
   */
  async getStatus(identifier: string, config?: Partial<RateLimitConfig>): Promise<RateLimitResult> {
    const finalConfig = { ...this.defaultConfig, ...config };
    const key = `${finalConfig.keyPrefix}:${identifier}`;
    const now = Date.now();
    const windowStart = now - finalConfig.windowMs;

    try {
      // Remove old entries and count current
      await this.redis.zremrangebyscore(key, '-inf', windowStart);
      const count = await this.redis.zcard(key);

      const remaining = Math.max(0, finalConfig.maxRequests - count);
      const oldestEntry = await this.redis.zrange(key, 0, 0, 'WITHSCORES');
      const resetAt =
        oldestEntry.length > 1
          ? new Date(parseInt(oldestEntry[1]) + finalConfig.windowMs)
          : new Date(now + finalConfig.windowMs);

      return {
        allowed: count < finalConfig.maxRequests,
        limit: finalConfig.maxRequests,
        remaining,
        resetAt,
      };
    } catch (error) {
      logger.error('Rate limiter status error:', error);
      return {
        allowed: true,
        limit: finalConfig.maxRequests,
        remaining: finalConfig.maxRequests,
        resetAt: new Date(now + finalConfig.windowMs),
      };
    }
  }

  /**
   * Reset rate limit for an identifier
   */
  async reset(identifier: string, keyPrefix?: string): Promise<void> {
    const prefix = keyPrefix ?? this.defaultConfig.keyPrefix;
    const key = `${prefix}:${identifier}`;

    try {
      await this.redis.del(key);
      logger.info(`Rate limit reset for ${identifier}`);
    } catch (error) {
      logger.error('Rate limiter reset error:', error);
    }
  }
}

