   */
  async getCharacterGroup(groupId: string): Promise<CharacterGroup | null> {
    return this.characterRepository.getCharacterGroup(groupId);
  }

  /**
   * Get all character groups
   */
  async getAllCharacterGroups(): Promise<CharacterGroup[]> {
