    // This method needs to be implemented in CharacterRepository
    // TODO: Implement using groupId parameter
    void groupId; // Suppress unused variable warning
    throw new Error('deleteCharacterGroup not implemented');
  }

  /**
   * Set a character as the main character in a group
   */
  async setMainCharacter(characterId: string): Promise<CharacterGroup> {
    // This method needs to be implemented in CharacterRepository
    // TODO: Implement using characterId parameter
    void characterId; // Suppress unused variable warning
    throw new Error('setMainCharacter not implemented');
  }

  /**
   * Remove a character from a group
   */
  async removeFromGroup(characterId: string): Promise<Character> {
    // This method needs to be implemented in CharacterRepository
    // TODO: Implement using characterId parameter
    void characterId; // Suppress unused variable warning
    throw new Error('removeFromGroup not implemented');
  }

  /**
   * Sync character information from Map API data
   */
  async syncCharacter(
    characterId: string,
    mapData: {
      corporationTicker: string;
      allianceTicker?: string | null;
      corporationId: number;
    }
  ): Promise<Character> {
    try {
      logger.info(`Syncing character ${characterId} from Map API data...`);

      // Create or update character using only map data
      const character = new Character({
        eveId: characterId,
        name: characterId, // We don't need the name since it can't change
        allianceTicker: mapData.allianceTicker ?? undefined,
        corporationTicker: mapData.corporationTicker,
        corporationId: mapData.corporationId,
      });

      logger.debug(`Created character object:`, character);

      const saved = await this.saveCharacter(character);
      logger.info(`Successfully synced character ${characterId}`);
      return saved;
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      const errorStack = error instanceof Error ? error.stack : undefined;

      logger.error(
        {
          error,
          characterId,
          errorMessage,
          errorStack,
        },
        `Failed to sync character ${characterId}: ${errorMessage}`
      );
      throw error;
    }
  }

  /**
   * Sync multiple characters from ESI
   */
  async syncCharacters(characterIds: string[]): Promise<Character[]> {
    const results: Character[] = [];
    const errors: Array<{ characterId: string; error: any }> = [];

    for (const characterId of characterIds) {
      try {
        // Get character data from ESI
        const esiData = await this.esiService.getCharacter(parseInt(characterId, 10));
        if (!esiData) {
          throw new Error(`No ESI data found for character ${characterId}`);
        }

        const character = await this.syncCharacter(characterId, {
          corporationTicker: esiData.corporation_ticker,
          allianceTicker: esiData.alliance_ticker,
          corporationId: esiData.corporation_id,
        });
        results.push(character);
      } catch (error) {
        errors.push({ characterId, error });
        logger.error(`Failed to sync character ${characterId}:`, error);
      }
    }

    if (errors.length > 0) {
      logger.warn(`Completed character sync with ${errors.length} errors out of ${characterIds.length} characters`);
    } else {
      logger.info(`Successfully synced all ${characterIds.length} characters`);
    }

    return results;
  }
}

// Use require() for CommonJS modules with ESM dependencies
// @ts-ignore - Ignoring type checking for Chart.js imports
const chartJS = require('chart.js');
const { Chart, registerables } = chartJS;

import { createCanvas } from 'canvas';
import { logger } from '../lib/logger';
import { theme, chartPalette } from './charts/config/theme';
import { ChartData as CustomChartData, ChartOptions as CustomChartOptions } from '../types/chart';

// Register Chart.js components in non-test environments
if (process.env.NODE_ENV !== 'test') {
  try {
    Chart.register(...registerables);
  } catch (e) {
    logger.warn('Failed to register Chart.js components', e);
  }
}

export class ChartRenderer {
  // These dimensions are used by the constructor but not referenced elsewhere
  // @ts-expect-error - Used in constructor
  private width: number;
  // @ts-expect-error - Used in constructor
  private height: number;
  private colors: string[] = chartPalette;
  private chart: any = null;

  constructor(width: number = 1200, height: number = 800) {
    this.width = width;
    this.height = height;
  }

  /**
   * Convert custom chart data to Chart.js format
   */
  private convertToChartJSData(data: CustomChartData): any {
    const datasets = data.datasets.map((dataset, index) => {
      const type = this.getChartType(dataset.displayType ?? dataset.type);
      const baseDataset = {
        ...dataset,
        type,
        backgroundColor: dataset.backgroundColor ?? this.getBackgroundColor(index, type),
        borderColor: dataset.borderColor ?? this.adjustColorOpacity(this.getBackgroundColor(index, type), 0.8),
        borderWidth: dataset.borderWidth ?? 1,
      };

      // Add type-specific properties
      switch (type) {
        case 'line':
          return {
            ...baseDataset,
            fill: dataset.fill ?? false,
            tension: dataset.tension ?? 0.4,
            pointBackgroundColor: this.getBackgroundColor(index, type),
            pointBorderColor: '#fff',
            pointBorderWidth: 1,
            pointRadius: 3,
            pointHoverRadius: 5,
            pointHoverBackgroundColor: this.getBackgroundColor(index, type),
            pointHoverBorderColor: '#fff',
            pointHoverBorderWidth: 2,
          };
        case 'bar':
          return {
            ...baseDataset,
            barPercentage: 0.8,
            categoryPercentage: 0.9,
            stack: 'stack0',
          };
        case 'pie':
          return {
            ...baseDataset,
            hoverOffset: 4,
          };
        case 'doughnut':
          return {
            ...baseDataset,
            hoverOffset: 4,
          };
        default:
          return baseDataset;
      }
    });

    return {
      labels: data.labels,
      datasets,
    };
  }

  /**
   * Get the appropriate Chart.js type for a custom chart type
   */
  private getChartType(type?: string): string {
    // First handle our internal source types
    if (type === 'kills' || type === 'map_activity') {
      return 'line'; // Default to line chart for these data sources
    }

    // Then handle display types
    switch (type) {
      case 'boxplot':
      case 'violin':
      case 'heatmap':
      case 'calendar':
        return 'bar';
      default:
        return type ?? 'bar';
    }
  }

  /**
   * Get background color for a dataset
   */
  private getBackgroundColor(index: number, _type?: string): string {
    const colorIndex = index % this.colors.length;
    return this.colors[colorIndex];
  }

  /**
   * Adjust color opacity
   */
  private adjustColorOpacity(color: string, opacity: number): string {
    if (color.startsWith('#')) {
      const r = parseInt(color.slice(1, 3), 16);
      const g = parseInt(color.slice(3, 5), 16);
      const b = parseInt(color.slice(5, 7), 16);
      return `rgba(${r}, ${g}, ${b}, ${opacity})`;
    }
    return color;
  }

  /**
   * Create chart configuration
   */
  private createChartConfig(data: CustomChartData, options: CustomChartOptions = {}): any {
    const chartData = this.convertToChartJSData(data);
    const chartOptions: any = {
      responsive: options.responsive ?? true,
      maintainAspectRatio: options.maintainAspectRatio ?? false,
      plugins: {
        legend: {
          position: 'top',
          labels: {
            color: theme.text.primary,
            font: {
              size: 12,
            },
          },
        },
        tooltip: {
          backgroundColor: theme.colors.background,
          titleColor: theme.text.primary,
          bodyColor: theme.text.primary,
          borderColor: theme.colors.primary,
          borderWidth: 1,
          padding: 10,
          displayColors: true,
          callbacks: {
            label: (context: any) => {
              const label = context.dataset.label ?? '';
              const value = context.parsed.y;
              return `${label}: ${value}`;
            },
          },
        },
      },
      scales: {
        x: {
          grid: {
            color: theme.grid.color,
          },
          ticks: {
            color: theme.text.primary,
            font: {
              size: 12,
            },
          },
          stacked: true,
        },
        y: {
          grid: {
            color: theme.grid.color,
          },
          ticks: {
            color: theme.text.primary,
            font: {
              size: 12,
            },
          },
          stacked: true,
        },
      },
    };

    // Use the chart type but assign to a variable that will be used
    const chartType = this.getChartType(data.displayType);

    return {
      type: chartType,
      data: chartData,
      options: chartOptions,
    };
  }

  /**
   * Render chart to buffer
   */
  async renderToBuffer(data: CustomChartData, options: CustomChartOptions = {}): Promise<Buffer> {
    try {
      const config = this.createChartConfig(data, options);
      const canvas = createCanvas(options.width ?? 800, options.height ?? 400);

      // Set background color
      const ctx = canvas.getContext('2d');
      ctx.fillStyle = theme.colors.background;
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Create and render the chart
      this.chart = new Chart(canvas as unknown as HTMLCanvasElement, config);
      await this.chart.render();

      // Use the correct toBuffer call
      return canvas.toBuffer();
    } catch (error) {
      logger.error('Error rendering chart to buffer:', error);
      throw error;
    }
  }

  /**
   * Render chart to base64 string
   */
  async renderToBase64(data: CustomChartData, options: CustomChartOptions = {}): Promise<string> {
    try {
      const buffer = await this.renderToBuffer(data, options);
      return buffer.toString('base64');
    } catch (error) {
      logger.error('Error rendering chart to base64:', error);
      throw error;
    }
  }
}

import { CharacterRepository } from '../infrastructure/repositories/CharacterRepository';
import { KillRepository } from '../infrastructure/repositories/KillRepository';
import { MapActivityRepository } from '../infrastructure/repositories/MapActivityRepository';
import { RepositoryManager } from '../infrastructure/repositories/RepositoryManager';
import { logger } from '../lib/logger';
import { Character } from '../domain/character/Character';
import { CharacterGroup } from '../domain/character/CharacterGroup';
import { Killmail } from '../domain/killmail/Killmail';
import { MapActivity } from '../domain/activity/MapActivity';
import { ChartConfigInput, ChartData, ChartDisplayType, ChartMetric } from '../types/chart';
import { format } from 'date-fns';
import { BaseRepository } from '../infrastructure/repositories/BaseRepository';
import { PrismaClient } from '@prisma/client';

interface KillData {
  killTime: Date;
  totalValue: bigint;
  points: number;
  attackerCount: number;
  characters: Array<{ characterId: bigint }>;
}

interface ActivityData {
  timestamp: Date;
  signatures: number;
  characterId: bigint;
}

export class ChartService extends BaseRepository {
  private readonly characterRepository: CharacterRepository;
  private readonly killRepository: KillRepository;
  private readonly mapActivityRepository: MapActivityRepository;
  private colors: string[] = [
    '#3366CC', // deep blue
    '#DC3912', // red
    '#FF9900', // orange
    '#109618', // green
    '#990099', // purple
    '#0099C6', // teal
    '#DD4477', // pink
    '#66AA00', // lime
    '#B82E2E', // dark red
    '#316395', // navy
    '#994499', // violet
    '#22AA99', // seafoam
    '#AAAA11', // olive
    '#6633CC', // indigo
    '#E67300', // amber
    '#8B0707', // maroon
  ];
  protected override prisma: PrismaClient;

  constructor() {
    super('Chart');
    const repositoryManager = new RepositoryManager();
    this.characterRepository = repositoryManager.getCharacterRepository();
    this.killRepository = repositoryManager.getKillRepository();
    this.mapActivityRepository = repositoryManager.getMapActivityRepository();
    this.prisma = new PrismaClient();
  }

  async generateChart(config: ChartConfigInput): Promise<ChartData> {
    const {
      type,
      characterIds,
      period,
      groupBy = 'hour',
      displayType = 'line',
      displayMetric = 'value', // Default to value, but can be "kills", "value", "points", "attackers"
      limit = 10, // Limit number of characters to display
    } = config;

    // Calculate start date based on period
    const startDate = new Date();
    switch (period) {
      case '24h':
        startDate.setHours(startDate.getHours() - 24);
        break;
      case '7d':
        startDate.setDate(startDate.getDate() - 7);
        break;
      case '30d':
        startDate.setDate(startDate.getDate() - 30);
        break;
      case '90d':
        startDate.setDate(startDate.getDate() - 90);
        break;
      default:
        throw new Error(`Invalid period: ${period}`);
    }

    // Generate chart title
    const metricLabel =
      displayMetric === 'value'
        ? 'ISK Value'
        : displayMetric === 'kills'
          ? 'Kill Count'
          : displayMetric === 'points'
            ? 'Points'
            : 'Attacker Count';

    const chartTitle = `${type === 'kills' ? 'Kills' : 'Map Activity'} - ${metricLabel} - Last ${
      period === '24h' ? '24 Hours' : period === '7d' ? '7 Days' : period === '30d' ? '30 Days' : '90 Days'
    }`;

    // Generate chart based on type
    let chartData;
    switch (type) {
      case 'kills':
        chartData = await this.generateKillsChart(characterIds, startDate, groupBy, displayMetric, limit);
        break;
      case 'map_activity':
        chartData = await this.generateMapActivityChart(characterIds, startDate, groupBy);
        break;
      default:
        throw new Error(`Invalid chart type: ${type}`);
    }

    // Add title and displayType to chart data
    chartData.title = chartTitle;
    chartData.displayType = displayType;

    return chartData;
  }

  private async generateKillsChart(
    characterIds: bigint[],
    startDate: Date,
    groupBy: 'hour' | 'day' | 'week',
    displayMetric: ChartMetric = 'value',
    limit: number = 10
  ): Promise<ChartData> {
    // Convert character IDs to strings for query
    const characterIdStrings = characterIds.map(id => id.toString());

    logger.info(`Generating kills chart for ${characterIds.length} characters from ${startDate.toISOString()}`);
    logger.info(`Character IDs: ${characterIdStrings.join(', ')}`);

    try {
      // Find all related characters via character groups
      const allCharactersNested = await Promise.all(
        characterIdStrings.map(async (id: string) => {
          const character = await this.characterRepository.getCharacter(BigInt(id));
          if (character?.characterGroupId) {
            return this.characterRepository.getCharactersByGroup(character.characterGroupId);
          }
          return character ? [character] : [];
        })
      );
      const allCharacters = allCharactersNested.flat();
      const allCharacterIdStrings = allCharacters.map((c: Character) => c.eveId);
      logger.info(`Including all characters in same groups: ${allCharacters.length} characters total`);

      // Get kills for each character using the updated schema
      logger.info('Querying killFact table with expanded character list...');
      const killsQuery = await this.killRepository.getKillsForCharacters(
        allCharacterIdStrings.map(id => BigInt(id)),
        startDate,
        new Date()
      );

      logger.info(`Found ${killsQuery.length} kill records in database`);

      if (killsQuery.length === 0) {
        logger.warn('No kills found for the specified characters and time period');
        // Return empty chart data but with datasets for the requested characters
        const emptyDatasets = await Promise.all(
          characterIds.slice(0, limit).map(async (characterId, index) => {
            // Try to get character name
            try {
              const character = await this.characterRepository.getCharacter(characterId);

              return {
                label: character?.name ?? `Character ${characterId}`,
                data: [],
                borderColor: this.getColorForIndex(index),
                fill: false,
              };
            } catch (err) {
              return {
                label: `Character ${characterId}`,
                data: [],
                borderColor: this.getColorForIndex(index),
                fill: false,
              };
            }
          })
        );

        // Generate at least some dummy time labels for the empty chart
        const labels = [];
        const today = new Date();
        // Determine appropriate number of days based on the groupBy parameter
        const daysToGenerate = groupBy === 'hour' ? 1 : groupBy === 'day' ? 7 : 4; // 1 day for hourly, 7 for daily, 4 weeks for weekly
        for (let i = daysToGenerate - 1; i >= 0; i--) {
          const date = new Date(today);
          date.setDate(date.getDate() - i);
          labels.push(format(date, this.getDateFormat(groupBy)));
        }

        return {
          labels,
          datasets: emptyDatasets,
          title: '',
          displayType: 'line' as ChartDisplayType,
        };
      }

      const kills = killsQuery.map((kill: any) => {
        logger.debug(`Processing kill: ID ${kill.killmail_id}, time ${kill.kill_time}, character ${kill.character_id}`);
        return {
          killTime: kill.kill_time,
          totalValue: kill.total_value,
          points: kill.points ?? 0,
          attackerCount: kill.attacker_count ?? 1,
          characters: [{ characterId: BigInt(kill.character_id) }],
        };
      }) as KillData[];

      // Group kills by time period
      logger.info(`Grouping ${kills.length} kills by ${groupBy}`);
      const groupedData = this.groupDataByTime(kills, groupBy, (kill: KillData) => ({
        timestamp: kill.killTime,
        value:
          displayMetric === 'value'
            ? Number(kill.totalValue)
            : displayMetric === 'kills'
              ? 1
              : displayMetric === 'points'
                ? kill.points
                : kill.attackerCount,
      }));

      logger.info(`Created ${groupedData.length} time groups`);

      // Sort character IDs by activity (total value, kill count, etc.) to show the most active characters
      const characterActivity: { id: bigint; activity: number; name: string }[] = [];

      for (const characterId of characterIds) {
        const character = await this.characterRepository.getCharacter(characterId);

        if (!character) {
          logger.warn(`Character ${characterId} not found in database`);
          continue;
        }

        // Find all alts for this character
        const alts = character.characterGroupId
          ? await this.characterRepository.getCharactersByGroup(character.characterGroupId)
          : [];

        // Get all character IDs (main + alts)
        const allIds = [BigInt(character.eveId), ...alts.map(alt => BigInt(alt.eveId))];

        // Calculate activity based on displayMetric
        const characterKills = kills.filter((kill: KillData) =>
          kill.characters.some(kc => allIds.includes(BigInt(kc.characterId)))
        );

        const activity = characterKills.reduce((total, kill) => {
          if (displayMetric === 'value') return total + Number(kill.totalValue);
          if (displayMetric === 'kills') return total + 1;
          if (displayMetric === 'points') return total + kill.points;
          if (displayMetric === 'attackers') return total + kill.attackerCount;
          return total;
        }, 0);

        // Only include characters with some activity
        if (activity > 0) {
          characterActivity.push({
            id: characterId,
            activity,
            name: character.name,
          });
        }
      }

      // Sort by activity descending and take the top 'limit' characters
      characterActivity.sort((a, b) => b.activity - a.activity);
      const topCharacterIds = characterActivity.slice(0, limit);

      logger.info(
        `Selected top ${topCharacterIds.length} most active characters for display: ${topCharacterIds
          .map(c => c.name)
          .join(', ')}`
      );

      // If no characters have activity, return empty chart
      if (topCharacterIds.length === 0) {
        return {
          labels: [],
          datasets: [],
          title: '',
          displayType: 'line' as ChartDisplayType,
        };
      }

      // Create datasets for each character - now include all alts for each main character
      logger.info('Creating datasets for each main character including their alts');
      const datasets = await Promise.all(
        topCharacterIds.map(async (charItem, index) => {
          const characterId = charItem.id;
          const character = await this.characterRepository.getCharacter(characterId);

          if (!character) {
            logger.warn(`Character ${characterId} not found in database`);
            return {
              label: `Character ${characterId}`,
              data: [],
              borderColor: this.getColorForIndex(index),
              fill: false,
            };
          }

          logger.info(`Processing main character ${character.name} (${character.eveId})`);

          // Find all alts for this character
          const alts = character.characterGroupId
            ? await this.characterRepository.getCharactersByGroup(character.characterGroupId)
            : [];

          logger.info(`Found ${alts.length} alts for ${character.name}`);

          // Get all character IDs (main + alts)
          const allIds = [BigInt(character.eveId), ...alts.map(alt => BigInt(alt.eveId))];

          // Filter kills for this character and all its alts
          const characterKills = kills.filter((kill: KillData) =>
            kill.characters.some(kc => allIds.includes(BigInt(kc.characterId)))
          );

          logger.info(`Found ${characterKills.length} kills for character ${character.name} and alts`);

          // Create a display name that shows activity level
          const activityText =
            displayMetric === 'value' ? this.formatValue(charItem.activity) : charItem.activity.toLocaleString();

          const metricLabel =
            displayMetric === 'value'
              ? 'ISK'
              : displayMetric === 'kills'
                ? 'kills'
                : displayMetric === 'points'
                  ? 'pts'
                  : 'attackers';

          const displayName = `${character.name} (${activityText} ${metricLabel})`;

          const characterData = this.groupDataByTime(characterKills, groupBy, (kill: KillData) => ({
            timestamp: kill.killTime,
            value:
              displayMetric === 'value'
                ? Number(kill.totalValue)
                : displayMetric === 'kills'
                  ? 1
                  : displayMetric === 'points'
                    ? kill.points
                    : kill.attackerCount,
          }));

          logger.info(`Created ${characterData.length} data points for character ${character.name}`);

          return {
            label: displayName,
            data: characterData.map(d => d.value),
            borderColor: this.getColorForIndex(index),
            fill: false,
          };
        })
      );

      logger.info(`Created ${datasets.length} datasets with labels: ${datasets.map(d => d.label).join(', ')}`);
      logger.info(`Chart will have ${groupedData.length} labels and ${datasets.length} datasets`);

      return {
        labels: groupedData.map(d => format(d.timestamp, this.getDateFormat(groupBy))),
        datasets,
        title: '',
        displayType: 'line' as ChartDisplayType,
      };
    } catch (error) {
      logger.error('Error generating kills chart:', error);
      // Return empty chart on error
      return {
        labels: [],
        datasets: [],
        title: '',
        displayType: 'line' as ChartDisplayType,
      };
    }
  }

  // Helper function to format values with K/M/B suffixes
  private formatValue(value: number): string {
    if (value >= 1_000_000_000) {
      return `${(value / 1_000_000_000).toFixed(1)}B`;
    } else if (value >= 1_000_000) {
      return `${(value / 1_000_000).toFixed(1)}M`;
    } else if (value >= 1_000) {
      return `${(value / 1_000).toFixed(1)}K`;
    }
    return value.toString();
  }

  // Helper method to get a color from the color array
  private getColorForIndex(index: number): string {
    return this.colors[index % this.colors.length];
  }

  private async generateMapActivityChart(
    characterIds: bigint[],
    startDate: Date,
    groupBy: 'hour' | 'day' | 'week'
  ): Promise<ChartData> {
    // Convert character IDs to strings for query
    const characterIdStrings = characterIds.map(id => id.toString());

    logger.info(`Generating map activity chart for ${characterIds.length} characters from ${startDate.toISOString()}`);
    logger.info(`Character IDs: ${characterIdStrings.join(', ')}`);

    try {
      // First, get all characters including alts for the requested main characters
      const allCharacters = await Promise.all(
        characterIdStrings.map(async id => {
          const character = await this.characterRepository.getCharacter(BigInt(id));
          if (character?.characterGroupId) {
            return this.characterRepository.getCharactersByGroup(character.characterGroupId);
          }
          return character ? [character] : [];
        })
      ).then(results => results.flat());

      const allCharacterIdStrings = allCharacters.flat().map((c: Character) => c.eveId);
      logger.info(`Including all characters and alts: ${allCharacters.length} characters total`);

      // Get map activity for each character
      logger.info('Querying mapActivity table with expanded character list...');
      const rawActivities = await this.mapActivityRepository.getActivityForGroup(
        allCharacterIdStrings.join(','),
        startDate,
        new Date()
      );

      logger.info(`Found ${rawActivities.length} map activity records in database`);

      if (rawActivities.length === 0) {
        logger.warn('No map activity found for the specified characters and time period');
        // Let's check if there are any activity records at all in the database
        const totalActivityCount = await this.mapActivityRepository.getActivityForGroup(
          characterIdStrings[0],
          startDate,
          new Date()
        );
        logger.info(`Total map activities in database: ${totalActivityCount.length}`);

        // Sample a few records to understand the data structure
        if (totalActivityCount.length > 0) {
          const sampleRecords = totalActivityCount;
          logger.info(`Sample map activity records: ${JSON.stringify(sampleRecords)}`);
        }
      }

      // Convert to ActivityData
      const activities: ActivityData[] = rawActivities.map(activity => ({
        timestamp: activity.timestamp,
        signatures: activity.signatures,
        characterId: BigInt(activity.characterId),
      }));

      // Group activities by time period
      logger.info(`Grouping ${activities.length} activities by ${groupBy}`);
      const groupedData = this.groupDataByTime(activities, groupBy, (activity: ActivityData) => ({
        timestamp: activity.timestamp,
        value: activity.signatures,
      }));

      logger.info(`Created ${groupedData.length} time groups`);

      // Create datasets for each main character
      logger.info('Creating datasets for each main character including their alts');
      const datasets = await Promise.all(
        characterIds.map(async characterId => {
          const character = await this.characterRepository.getCharacter(characterId);

          if (!character) {
            logger.warn(`Character ${characterId} not found in database`);
            return {
              label: `Character ${characterId}`,
              data: [],
              borderColor: this.getRandomColor(),
              fill: false,
            };
          }

          logger.info(`Processing main character ${character.name} (${character.eveId})`);

          // Find all alts for this character
          const alts = character.characterGroupId
            ? await this.characterRepository.getCharactersByGroup(character.characterGroupId)
            : [];

          logger.info(`Found ${alts.length} alts for ${character.name}`);

          // Get all character IDs (main + alts)
          const allIds = [BigInt(character.eveId), ...alts.map(alt => BigInt(alt.eveId))];

          // Filter activities for this character and all its alts
          const characterActivities = activities.filter((activity: ActivityData) =>
            allIds.includes(activity.characterId)
          );

          logger.info(`Found ${characterActivities.length} activities for character ${character.name} and alts`);

          const characterData = this.groupDataByTime(characterActivities, groupBy, (activity: ActivityData) => ({
            timestamp: activity.timestamp,
            value: activity.signatures,
          }));

          logger.info(`Created ${characterData.length} data points for character ${character.name}`);

          return {
            label: character.name,
            data: characterData.map(d => d.value),
            borderColor: this.getRandomColor(),
            fill: false,
          };
        })
      );

      logger.info(`Created ${datasets.length} datasets with labels: ${datasets.map(d => d.label).join(', ')}`);
      logger.info(`Chart will have ${groupedData.length} labels and ${datasets.length} datasets`);

      return {
        labels: groupedData.map(d => format(d.timestamp, this.getDateFormat(groupBy))),
        datasets,
        title: '',
        displayType: 'line' as ChartDisplayType,
      };
    } catch (error) {
      logger.error('Error generating map activity chart:', error);
      // Return empty chart on error
      return {
        labels: [],
        datasets: [],
        title: '',
        displayType: 'line' as ChartDisplayType,
      };
    }
  }

  private groupDataByTime<T>(
    data: T[],
    groupBy: 'hour' | 'day' | 'week',
    getTimestampAndValue: (item: T) => { timestamp: Date; value: number }
  ): { timestamp: Date; value: number }[] {
    const grouped = new Map<string, { timestamp: Date; value: number }>();

    // If no data, return an empty array
    if (data.length === 0) {
      return [];
    }

    // Create a dictionary for quick lookup when grouping data
    for (const item of data) {
      const { timestamp, value } = getTimestampAndValue(item);
      const key = format(timestamp, this.getGroupByFormat(groupBy));

      if (!grouped.has(key)) {
        grouped.set(key, { timestamp, value: 0 });
      }

      const current = grouped.get(key)!;
      current.value += value;
    }

    // Sort by timestamp
    return Array.from(grouped.values()).sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());
  }

  private getGroupByFormat(groupBy: 'hour' | 'day' | 'week'): string {
    switch (groupBy) {
      case 'hour':
        return 'yyyy-MM-dd HH:00';
      case 'day':
        return 'yyyy-MM-dd';
      case 'week':
        return "yyyy-'W'ww";
      default:
        throw new Error(`Invalid groupBy: ${groupBy}`);
    }
  }

  private getDateFormat(groupBy: 'hour' | 'day' | 'week'): string {
    switch (groupBy) {
      case 'hour':
        return 'MMM d, HH:mm';
      case 'day':
        return 'MMM d';
      case 'week':
        return "'Week' w";
      default:
        throw new Error(`Invalid groupBy: ${groupBy}`);
    }
  }

  private getRandomColor(): string {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }

  /**
   * Generates a grouped kills chart by character group for kills data
   */
  async generateGroupedKillsChart(config: {
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
      mainCharacterId?: string;
    }>;
    startDate: Date;
    endDate: Date;
    displayType: string;
  }): Promise<ChartData> {
    const { characterGroups, startDate, endDate } = config;

    logger.info(`Generating grouped kills chart from ${startDate.toISOString()} to ${endDate.toISOString()}`);
    logger.info(`Received ${characterGroups.length} total character groups to process`);

    // Enhanced group data with main character information
    const enhancedGroups = await Promise.all(
      characterGroups.map(async group => {
        // Try to find main character in the group
        let mainCharacter = null;

        // First, check if any character in the group is set as a main character
        // Get all characters in this group using the group ID
        const groupCharacters = await this.characterRepository.getCharactersByGroup(group.groupId);
        const hasCharacters = groupCharacters.length > 0;
        if (hasCharacters && group.characters.length > 0) {
          mainCharacter = group.characters[0];
        }

        // If still no main was found, just use the first character
        if (!mainCharacter && group.characters.length > 0) {
          mainCharacter = group.characters[0];
        }

        // Use just the character name as the display name
        const displayName = mainCharacter
          ? mainCharacter.name
          : group.characters.length > 0
            ? group.characters[0].name
            : group.name;

        return {
          ...group,
          mainCharacter,
          displayName,
        };
      })
    );

    // Get displayable groups with at least one character
    const displayGroups = enhancedGroups.filter(group => group.characters.length > 0);

    // Print the detailed group information for debugging
    if (displayGroups.length > 0) {
      logger.info(`Found ${displayGroups.length} valid character groups with at least one character`);
      displayGroups.forEach((group, index) => {
        logger.info(
          `Group ${index + 1}: "${group.displayName}" (Main: ${
            group.mainCharacter?.name
          }) - ${group.characters.length} characters`
        );
      });
    } else {
      logger.warn('No valid character groups found with characters');
    }

    // First, collect all groups with their kill data and filter out those without kills
    const groupsWithData = [];
    const groupsWithoutKills = [];
    let totalGroupsProcessed = 0;

    try {
      logger.info(`Processing ${displayGroups.length} groups to fetch kill data...`);

      // For each group, get the kill statistics using enhanced stats
      for (const group of displayGroups) {
        totalGroupsProcessed++;

        // Get all character IDs in this group
        const characterIds = group.characters.map(c => BigInt(c.eveId));

        if (characterIds.length === 0) {
          logger.info(`No characters in group ${group.displayName}, skipping`);
          continue;
        }

        // Count kills for these characters (faster than fetching all data)
        const kills = await this.killRepository.getKillsForCharacters(characterIds, startDate, endDate);
        const killCount = kills.length;

        if (killCount === 0) {
          logger.info(`No kills found for group ${group.displayName}, skipping`);
          groupsWithoutKills.push(group.displayName);
          continue;
        }

        // Calculate solo kills (where all attackers are from this group)
        let soloKills = 0;
        for (const kill of kills) {
          // Skip kills with no attackers
          if (!kill.attackers || kill.attackers.length === 0) continue;

          // Get all player attackers (those with character IDs)
          const playerAttackers = kill.attackers.filter((a: { character_id?: string }) => a.character_id);
          if (playerAttackers.length === 0) continue;

          // Check if all attackers are from this group
          const allFromGroup = playerAttackers.every((attacker: { character_id?: string }) => {
            if (!attacker.character_id) return false;
            return characterIds.includes(BigInt(attacker.character_id));
          });

          if (allFromGroup) {
            soloKills++;
          }
        }

        // Only add to our results if there are actually kills
        if (kills.length > 0) {
          // Always use the main character's name as the display name
          const displayName = group.mainCharacter ? group.mainCharacter.name : group.displayName;

          groupsWithData.push({
            group: {
              ...group,
              displayName,
            },
            totalKills: kills.length,
            soloKills: soloKills,
          });

          logger.info(`Group ${displayName}: ${kills.length} total kills, ${soloKills} solo kills`);
        }
      }

      // Log stats about our filtering
      logger.info(`Processed ${totalGroupsProcessed}/${displayGroups.length} groups`);
      logger.info(
        `Found ${groupsWithData.length} groups with kills and ${groupsWithoutKills.length} groups without kills`
      );

      // If no groups have kills, return empty chart
      if (groupsWithData.length === 0) {
        logger.info('No groups with kills found, returning empty chart');
        return {
          labels: [],
          datasets: [],
          displayType: 'horizontalBar' as ChartDisplayType,
          summary: 'No kills found in the specified time period',
        };
      }

      // Sort groups by total kills in descending order
      groupsWithData.sort((a, b) => b.totalKills - a.totalKills);

      logger.info(`After sorting, top 3 groups by kill count:`);
      groupsWithData.slice(0, 3).forEach((item, i) => {
        logger.info(`${i + 1}. ${item.group.displayName}: ${item.totalKills} kills`);
      });

      // Create chart data structure from our filtered and sorted groups
      const groupLabels = groupsWithData.map(item => item.group.displayName);
      const totalKillsData = groupsWithData.map(item => item.totalKills);
      const soloKillsData = groupsWithData.map(item => item.soloKills);

      // Calculate overall statistics
      const overallTotalKills = totalKillsData.reduce((a, b) => a + b, 0);
      const overallSoloKills = soloKillsData.reduce((a, b) => a + b, 0);

      logger.info(`Final chart will include ${groupLabels.length} groups`);

      return {
        labels: groupLabels,
        datasets: [
          {
            label: 'Total Kills',
            data: totalKillsData,
            backgroundColor: '#3366CC',
          },
          {
            label: 'Solo Kills',
            data: soloKillsData,
            backgroundColor: '#DC3912',
          },
        ],
        displayType: 'horizontalBar' as ChartDisplayType,
        summary: `Total kills: ${overallTotalKills.toLocaleString()}\nSolo kills: ${overallSoloKills.toLocaleString()} (${
          overallTotalKills > 0 ? Math.round((overallSoloKills / overallTotalKills) * 100) : 0
        }%)`,
      };
    } catch (error) {
      logger.error('Error generating kills chart:', error);
      throw error;
    }
  }

  /**
   * Generates a grouped map activity chart by character group
   */
  async generateGroupedMapActivityChart(config: {
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>;
    startDate: Date;
    endDate: Date;
    displayType: string;
  }): Promise<ChartData> {
    const { characterGroups, startDate, endDate, displayType } = config;

    logger.info(`Generating grouped map activity chart from ${startDate.toISOString()} to ${endDate.toISOString()}`);

    // If no character groups provided, get all characters
    if (!characterGroups || characterGroups.length === 0) {
      logger.info('No character groups provided, creating default group with all characters');
      try {
        const allCharacters = await this.characterRepository.getAllCharacters();

        if (allCharacters.length === 0) {
          logger.warn('No characters found in database');
          return {
            labels: ['No Characters'],
            datasets: [
              {
                label: 'Signatures',
                data: [0],
                backgroundColor: '#3366CC',
              },
              {
                label: 'Cosmic Anomalies',
                data: [0],
                backgroundColor: '#DC3912',
              },
              {
                label: 'Wormholes',
                data: [0],
                backgroundColor: '#FF9900',
              },
            ],
            displayType: 'horizontalBar' as ChartDisplayType,
          };
        }

        // Continue with default group
        return this.generateGroupedMapActivityChart({
          characterGroups: [
            {
              groupId: 'default',
              name: 'All Characters',
              characters: allCharacters,
            },
          ],
          startDate,
          endDate,
          displayType,
        });
      } catch (error) {
        logger.error('Error creating default character group:', error);
        throw new Error('No character groups available and could not create default group');
      }
    }

    // Enhanced group data with main character information
    const enhancedGroups = await Promise.all(
      characterGroups.map(async group => {
        // Try to find main character in the group
        let mainCharacter = null;

        // First, check if any character in the group is set as a main character
        // Get all characters in this group using the group ID
        const groupCharacters = await this.characterRepository.getCharactersByGroup(group.groupId);
        const hasCharacters = groupCharacters.length > 0;
        if (hasCharacters && group.characters.length > 0) {
          mainCharacter = group.characters[0];
        }

        // If no main was found, just use the first character
        if (!mainCharacter && group.characters.length > 0) {
          mainCharacter = group.characters[0];
        }

        // Use just the character name as the display name
        const displayName = mainCharacter
          ? mainCharacter.name
          : group.characters.length > 0
            ? group.characters[0].name
            : group.name;

        return {
          ...group,
          mainCharacter,
          displayName,
        };
      })
    );

    // Get displayable groups with at least one character
    const displayGroups = enhancedGroups.filter(group => group.characters.length > 0);

    // Print the detailed group information for debugging
    if (displayGroups.length > 0) {
      logger.info(`Found ${displayGroups.length} valid character groups with at least one character`);
      displayGroups.forEach((group, index) => {
        logger.info(`Group ${index + 1}: "${group.displayName}" - ${group.characters.length} characters`);
      });
    } else {
      logger.warn('No valid character groups found with characters');
    }

    // Collect all groups with their activity data first, so we can filter out empty ones
    const groupsWithData = [];

    try {
      // For each group, get the map activity statistics
      for (const group of displayGroups) {
        // Debug log all characters in this group
        logger.info(`Processing group ${group.displayName}:`);
        group.characters.forEach((char, i) => {
          logger.info(`  Character ${i}: eveId=${char.eveId}, name=${char.name}, eveIdType=${typeof char.eveId}`);
        });

        // Filter out characters with undefined eveId and convert valid ones to BigInt
        const validCharacters = group.characters.filter(c => {
          if (!c.eveId || c.eveId === undefined) {
            logger.warn(`Skipping character with undefined eveId: ${JSON.stringify(c)}`);
            return false;
          }
          return true;
        });

        logger.info(
          `After filtering, group ${group.displayName} has ${validCharacters.length} valid characters out of ${group.characters.length} total`
        );

        if (validCharacters.length === 0) {
          logger.info(`No valid characters in group ${group.displayName}, skipping`);
          continue;
        }

        const characterIds = validCharacters.map(c => BigInt(c.eveId));

        logger.info(
          `Valid character IDs for group ${group.displayName}: ${characterIds.map(id => id.toString()).join(', ')}`
        );

        // Get all map activities for these characters
        const activities = await this.mapActivityRepository.getActivityForCharacters(
          characterIds.map(id => id.toString()),
          startDate,
          endDate
        );

        if (activities.length === 0) {
          logger.info(`No map activities found for group ${group.displayName}`);
          continue;
        }

        // Sum up activity values
        const signatures = activities.reduce((sum, act) => sum + act.signatures, 0);
        const connections = activities.reduce((sum, act) => sum + act.connections, 0);
        const passages = activities.reduce((sum, act) => sum + act.passages, 0);

        // Only add to our results if there is actually activity
        const totalActivity = signatures + connections + passages;
        if (totalActivity > 0) {
          groupsWithData.push({
            group,
            signatures,
            connections,
            passages,
            totalActivity,
          });
        } else {
          logger.info(`Skipping group ${group.displayName} with no activity data`);
        }
      }

      // Exit early if no groups have data
      if (groupsWithData.length === 0) {
        logger.warn('No character groups have any map activity data in the specified time range');
        return {
          labels: ['No Data Available'],
          datasets: [
            {
              label: 'Signatures',
              data: [0],
              backgroundColor: '#3366CC',
            },
            {
              label: 'Connections',
              data: [0],
              backgroundColor: '#DC3912',
            },
            {
              label: 'Passages',
              data: [0],
              backgroundColor: '#FF9900',
            },
          ],
          displayType: 'horizontalBar' as ChartDisplayType,
        };
      }

      // Sort groups by total activity (highest first)
      groupsWithData.sort((a, b) => b.totalActivity - a.totalActivity);

      // Create chart data structure from our filtered and sorted groups
      const groupLabels = groupsWithData.map(item => item.group.displayName);
      const signaturesData = groupsWithData.map(item => item.signatures);
      const connectionsData = groupsWithData.map(item => item.connections);
      const passagesData = groupsWithData.map(item => item.passages);

      // Return the final chart data
      return {
        labels: groupLabels,
        datasets: [
          {
            label: 'Signatures',
            data: signaturesData,
            backgroundColor: '#3366CC',
          },
          {
            label: 'Cosmic Anomalies',
            data: connectionsData,
            backgroundColor: '#DC3912',
          },
          {
            label: 'Wormholes',
            data: passagesData,
            backgroundColor: '#FF9900',
          },
        ],
        displayType: 'horizontalBar' as ChartDisplayType,
      };
    } catch (error: unknown) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('Error generating grouped map activity chart:', error);
      throw new Error(`Failed to generate grouped map activity chart: ${errorMessage}`);
    }
  }

  /**
   * Gets all tracked characters
   */
  async getTrackedCharacters(): Promise<Array<{ eveId: string; name: string }>> {
    try {
      // Find main characters (where they are referenced by CharacterGroup.mainCharacterId)
      const groups = await this.characterRepository.getAllCharacterGroups();

      // Extract characters from groups
      const characters = groups
        .map(g => (g.mainCharacterId ? g.characters.find((c: Character) => c.eveId === g.mainCharacterId) : null))
        .filter((c): c is Character => c !== null);

      logger.info(`Found ${characters.length} tracked characters (main characters)`);
      return characters;
    } catch (error: unknown) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('Error fetching tracked characters:', errorMessage);
      return [];
    }
  }

  /**
   * Gets all character groups with their members
   */
  async getCharacterGroups(): Promise<
    Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
      mainCharacterId?: string;
    }>
  > {
    try {
      // First, count the total number of character groups
      const totalGroups = (await this.characterRepository.getAllCharacterGroups()).length;

      logger.info(`Found ${totalGroups} character groups in database`);

      // If we have a suspiciously large number of groups, use a different approach
      if (totalGroups > 1000) {
        logger.warn(`Unusually high number of character groups (${totalGroups}). Using optimized approach.`);

        // Get only groups that have at least one character (using a JOIN)
        const groupsWithCharacters = (await this.characterRepository.getAllCharacterGroups()).filter(
          g => g.characters.length > 0
        );

        logger.info(`Found ${groupsWithCharacters.length} groups with at least one character`);

        // Process these groups in batches to avoid memory issues
        const result = [];
        const batchSize = 50;

        for (let i = 0; i < groupsWithCharacters.length; i += batchSize) {
          const batch = groupsWithCharacters.slice(i, i + batchSize);
          logger.info(`Processing batch ${i / batchSize + 1} of ${Math.ceil(groupsWithCharacters.length / batchSize)}`);

          for (const group of batch) {
            // For each group, get its characters
            const characters = await Promise.all(
              group.characters.map((c: Character) =>
                c.characterGroupId
                  ? this.characterRepository.getCharactersByGroup(c.characterGroupId)
                  : Promise.resolve([])
              )
            ).then(results => results.flat());

            if (characters.length > 0) {
              result.push({
                groupId: group.id,
                name: group.name ?? `Group ${group.id.substring(0, 8)}`,
                characters,
              });
            }
          }
        }

        logger.info(`Successfully processed ${result.length} valid character groups`);
        return result;
      }

      // Standard approach for a reasonable number of groups
      const groups = await this.characterRepository.getAllCharacterGroups();

      logger.info(`Found ${groups.length} character groups`);

      // Filter out groups with no characters
      const validGroups = groups.filter(group => group.characters.length > 0);
      logger.info(`${validGroups.length} groups have at least one character`);

      return validGroups
        .map(group => {
          // Debug log the raw group data
          logger.debug(`Processing group ${group.id}:`, {
            groupId: group.id,
            name: group.name,
            characterCount: group.characters?.length ?? 0,
            firstCharacter: group.characters?.[0]
              ? {
                  eveId: group.characters[0].eveId,
                  eveIdType: typeof group.characters[0].eveId,
                  name: group.characters[0].name,
                }
              : null,
          });

          // Filter and transform characters, ensuring eveId is a valid string
          const validCharacters = group.characters
            .map(char => {
              // Ensure eveId is properly converted to string
              const eveId = char.eveId?.toString?.() ?? String(char.eveId ?? '');
              const name = char.name ?? `Character ${eveId}`;

              return { eveId, name };
            })
            .filter(char => {
              // Filter out characters with invalid eveId
              if (!char.eveId || char.eveId === '' || char.eveId === 'undefined' || char.eveId === 'null') {
                logger.warn(`Skipping character with invalid eveId:`, char);
                return false;
              }
              return true;
            });

          logger.debug(`Group ${group.id} has ${validCharacters.length} valid characters`);

          return {
            groupId: group.id,
            name: group.name ?? `Group ${group.id.substring(0, 8)}`,
            characters: validCharacters,
            mainCharacterId: group.mainCharacterId?.toString?.() ?? String(group.mainCharacterId ?? ''),
          };
        })
        .filter(group => group.characters.length > 0); // Filter out groups with no valid characters
    } catch (error: unknown) {
      logger.error('Error fetching character groups:', {
        error: error instanceof Error ? error.message : String(error),
        stack: error instanceof Error ? error.stack : undefined,
      });

      // If we encounter an error, try a different approach to get at least some valid groups
      try {
        logger.info('Attempting alternative approach to fetch character groups');

        // Get characters that belong to a group
        const allCharacters = await this.characterRepository.getAllCharacters();
        const characters = allCharacters.filter(c => c.characterGroupId !== null);

        // Manually group them
        const groupMap = new Map<
          string,
          {
            characters: Array<{ eveId: string; name: string }>;
            name?: string;
            mainCharacterId?: string;
          }
        >();

        for (const char of characters) {
          if (!char.characterGroupId) continue;

          if (!groupMap.has(char.characterGroupId)) {
            groupMap.set(char.characterGroupId, { characters: [] });
          }

          // Ensure eveId is properly converted to string
          const eveId = char.eveId?.toString?.() ?? String(char.eveId ?? '');
          const name = char.name ?? `Character ${eveId}`;

          // Only add if eveId is valid
          if (eveId && eveId !== '' && eveId !== 'undefined' && eveId !== 'null') {
            groupMap.get(char.characterGroupId)!.characters.push({
              eveId,
              name,
            });
          }
        }

        // Convert to the expected format
        const result = Array.from(groupMap.entries())
          .map(([groupId, data]) => ({
            groupId,
            name: data.name ?? `Group ${groupId.substring(0, 8)}`,
            characters: data.characters,
            mainCharacterId: data.mainCharacterId?.toString?.() ?? String(data.mainCharacterId ?? ''),
          }))
          .filter(group => group.characters.length > 0); // Filter out groups with no valid characters

        logger.info(`Alternative approach found ${result.length} character groups`);
        return result;
      } catch (fallbackError) {
        logger.error('Error with alternative character group approach:', fallbackError);

        // Last resort: return an empty array instead of a default group
        logger.info('No valid character groups found, returning empty array');
        return [];
      }
    }
  }

  /**
   * Get kills for a character group within a date range
   */
  async getKillsForGroup(groupId: string, startDate: Date, endDate: Date): Promise<Killmail[]> {
    const kills = await this.killRepository.getKillsForGroup(groupId, startDate, endDate);
    return kills.map(kill => new Killmail(kill));
  }

  /**
   * Get map activity for a character group within a date range
   */
  async getMapActivityForGroup(groupId: string, startDate: Date, endDate: Date): Promise<MapActivity[]> {
    const activities = await this.mapActivityRepository.getActivityForGroup(groupId, startDate, endDate);
    return activities.map(
      activity =>
        new MapActivity({
          ...activity,
          characterId: BigInt(activity.characterId),
        })
    );
  }

  /**
   * Get all character groups
   */
  async getAllCharacterGroups(): Promise<CharacterGroup[]> {
    return this.characterRepository.getAllCharacterGroups();
  }

  /**
   * Get a character group by ID
   */
  async getCharacterGroup(groupId: string): Promise<CharacterGroup | null> {
    return this.characterRepository.getCharacterGroup(groupId);
  }

  /**
   * Get all characters
   */
  async getAllCharacters(): Promise<Character[]> {
    return this.characterRepository.getAllCharacters();
  }

  /**
   * Get characters by group ID
   */
  async getCharactersByGroup(groupId: string): Promise<Character[]> {
    return this.characterRepository.getCharactersByGroup(groupId);
  }

  /**
   * Get map activity statistics for a character group
   */
  async getGroupActivityStats(
    groupId: string,
    startDate: Date,
    endDate: Date
  ): Promise<{
    totalSystems: number;
    totalSignatures: number;
    averageSignaturesPerSystem: number;
  }> {
    return this.mapActivityRepository.getGroupActivityStats(groupId, startDate, endDate);
  }

  /**
   * Get kill statistics for a character group
   */
  async getGroupKillStats(
    groupId: string,
    startDate: Date,
    endDate: Date
  ): Promise<{
    totalKills: number;
    totalValue: bigint;
    averageValue: number;
    soloKills: number;
  }> {
    const kills = await this.killRepository.getKillsForGroup(groupId, startDate, endDate);

    const totalKills = kills.length;
    const totalValue = kills.reduce((sum, kill) => sum + BigInt(kill.total_value ?? 0), BigInt(0));
    const averageValue = totalKills > 0 ? Number(totalValue) / totalKills : 0;

    // Count solo kills (simplified - just check if solo flag is true)
    const soloKills = kills.filter(kill => kill.solo).length;

    return {
      totalKills,
      totalValue,
      averageValue,
      soloKills,
    };
  }

  async getActivityData(characterIds: string[], startDate: Date, endDate: Date): Promise<ActivityData[]> {
    return this.executeQuery(async () => {
      const activity = await this.prisma.mapActivity.findMany({
        where: {
          characterId: {
            in: characterIds.map(id => BigInt(id)),
          },
          timestamp: {
            gte: startDate,
            lte: endDate,
          },
        },
        select: {
          timestamp: true,
          signatures: true,
          characterId: true,
        },
        orderBy: {
          timestamp: 'asc',
        },
      });

      return activity.map(
        (item: { timestamp: Date; signatures: number; characterId: bigint }): ActivityData => ({
          timestamp: item.timestamp,
          signatures: item.signatures,
          characterId: item.characterId,
        })
      );
    });
  }
}

import { UnifiedESIClient } from '../infrastructure/http/UnifiedESIClient';
import { CacheRedisAdapter } from '../cache/CacheRedisAdapter';
import { logger } from '../lib/logger';
import { Configuration } from '../config';

/**
 * Service for interacting with EVE Online's ESI API
 * Provides caching and error handling around ESI data
 */
export class ESIService {
  private esiClient: UnifiedESIClient;
  private cache: CacheRedisAdapter;

  /**
   * Create a new ESI service
   */
  constructor() {
    // Initialize the ESI client with Redis caching
    this.cache = new CacheRedisAdapter(Configuration.redis.url, Configuration.redis.cacheTtl);
    this.esiClient = new UnifiedESIClient({}, this.cache);
  }

  /**
   * Fetch killmail data with caching
   */
  async getKillmail(killmailId: number, hash: string): Promise<any> {
    try {
      return await this.esiClient.fetchKillmail(killmailId, hash);
    } catch (error) {
      logger.error(`Error fetching killmail ${killmailId}:`, error);
      throw error;
    }
  }

  /**
   * Fetch character information with caching
   */
  async getCharacter(characterId: number): Promise<any> {
    try {
      return await this.esiClient.fetchCharacter(characterId);
    } catch (error) {
      logger.error(`Error fetching character ${characterId}:`, error);
      throw error;
    }
  }

  /**
   * Fetch corporation information with caching
   */
  async getCorporation(corporationId: number): Promise<any> {
    try {
      return await this.esiClient.fetchCorporation(corporationId);
    } catch (error) {
      logger.error(`Error fetching corporation ${corporationId}:`, error);
      throw error;
    }
  }

  /**
   * Fetch alliance information with caching
   */
  async getAlliance(allianceId: number): Promise<any> {
    try {
      return await this.esiClient.fetchAlliance(allianceId);
    } catch (error) {
      logger.error(`Error fetching alliance ${allianceId}:`, error);
      throw error;
    }
  }

  /**
   * Fetch ship type information with caching
   */
  async getShipType(typeId: number): Promise<any> {
    try {
      return await this.esiClient.fetchType(typeId);
    } catch (error) {
      logger.error(`Error fetching type ${typeId}:`, error);
      throw error;
    }
  }

  /**
   * Fetch solar system information with caching
   */
  async getSolarSystem(systemId: number): Promise<any> {
    try {
      return await this.esiClient.fetchSolarSystem(systemId);
    } catch (error) {
      logger.error(`Error fetching system ${systemId}:`, error);
      throw error;
    }
  }

  /**
   * Map a list of ship type IDs to their names
   * Uses batched requests and caching to optimize performance
   */
  async getShipTypeNames(typeIds: number[]): Promise<Record<number, string>> {
    try {
      // Deduplicate type IDs
      const uniqueTypeIds = Array.from(new Set(typeIds));

      // Create caching key for this list
      const cacheKey = `ship-types-${uniqueTypeIds.sort().join('-')}`;

      // Try to get from cache
      const cached = await this.cache.get<Record<number, string>>(cacheKey);
      if (cached) {
        return cached;
      }

      // Not in cache, need to fetch from ESI
      const result: Record<number, string> = {};

      // Process in batches of 20 to avoid rate limits
      const batchSize = 20;
      for (let i = 0; i < uniqueTypeIds.length; i += batchSize) {
        const batch = uniqueTypeIds.slice(i, i + batchSize);

        // Fetch each type in parallel
        const promises = batch.map(typeId => this.getShipType(typeId));
        const types = await Promise.all(promises);

        // Map type IDs to names
        for (let j = 0; j < batch.length; j++) {
          const typeId = batch[j];
          const type = types[j];
          result[typeId] = type?.name || `Unknown Type ${typeId}`;
        }
      }

      // Cache the result for future use
      await this.cache.set(cacheKey, result, 60 * 60 * 24); // 24 hour cache for ship types

      return result;
    } catch (error) {
      logger.error('Error mapping ship type IDs to names:', error);
      throw error;
    }
  }

  /**
   * Map a list of corporation IDs to their names and tickers
   */
  async getCorporationDetails(corpIds: number[]): Promise<Record<number, { name: string; ticker: string }>> {
    try {
      // Deduplicate corp IDs
      const uniqueCorpIds = Array.from(new Set(corpIds));

      // Create caching key for this list
      const cacheKey = `corp-details-${uniqueCorpIds.sort().join('-')}`;

      // Try to get from cache
      const cached = await this.cache.get<Record<number, { name: string; ticker: string }>>(cacheKey);
      if (cached) {
        return cached;
      }

      // Not in cache, need to fetch from ESI
      const result: Record<number, { name: string; ticker: string }> = {};

      // Process in batches of 20 to avoid rate limits
      const batchSize = 20;
      for (let i = 0; i < uniqueCorpIds.length; i += batchSize) {
        const batch = uniqueCorpIds.slice(i, i + batchSize);

        // Fetch each corporation in parallel
        const promises = batch.map(corpId => this.getCorporation(corpId));
        const corps = await Promise.all(promises);

        // Map corporation IDs to names and tickers
        for (let j = 0; j < batch.length; j++) {
          const corpId = batch[j];
          const corp = corps[j];
          result[corpId] = {
            name: corp?.name || `Unknown Corp ${corpId}`,
            ticker: corp?.ticker || '????',
          };
        }
      }

      // Cache the result for future use
      await this.cache.set(cacheKey, result, 60 * 60 * 24); // 24 hour cache for corporation details

      return result;
    } catch (error) {
      logger.error('Error mapping corporation IDs to details:', error);
      throw error;
    }
  }
}

import { ChartData, ChartOptions } from '../../types/chart';
import { RepositoryManager } from '../../infrastructure/repositories/RepositoryManager';
import { FormatUtils } from './utils/FormatUtils';
import { TimeUtils } from './utils/TimeUtils';
import { logger } from '../../lib/logger';

/**
 * Base class for all chart generators
 * Provides common functionality for generating charts
 */
export abstract class BaseChartGenerator {
  protected colors: string[];
  protected repoManager: RepositoryManager;

  /**
   * Create a new chart generator with dependencies injected
   * @param repoManager Repository manager for data access
   * @param colors Color palette for charts
   */
  constructor(repoManager: RepositoryManager, colors?: string[]) {
    this.repoManager = repoManager;
    this.colors = colors ?? [
      '#3366CC',
      '#DC3912',
      '#FF9900',
      '#109618',
      '#990099',
      '#0099C6',
      '#DD4477',
      '#66AA00',
      '#B82E2E',
      '#316395',
    ];
  }

  /**
   * Generate chart data based on input options
   * This method must be implemented by all chart generators
   */
  abstract generateChart(options: {
    startDate: Date;
    endDate: Date;
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{
        eveId: string;
        name: string;
        mainCharacterId?: string;
      }>;
      mainCharacterId?: string;
    }>;
    displayType: string;
  }): Promise<ChartData>;

  /**
   * Get color for a specific index (cycles through the color array)
   */
  protected getColorForIndex(index: number): string {
    return this.colors[index % this.colors.length];
  }

  /**
   * Adjust the brightness of a color by a percentage
   * @param hexColor Hex color string (e.g., "#FFFFFF")
   * @param percent Percentage to adjust (-100 to 100)
   */
  protected adjustColorBrightness(hexColor: string, percent: number): string {
    const hex = hexColor.replace('#', '');
    const r = parseInt(hex.substring(0, 2), 16);
    const g = parseInt(hex.substring(2, 4), 16);
    const b = parseInt(hex.substring(4, 6), 16);

    const adjustValue = (value: number): number => {
      const adjustedValue = value * (1 + percent / 100);
      return Math.min(255, Math.max(0, Math.round(adjustedValue)));
    };

    const rr = adjustValue(r).toString(16).padStart(2, '0');
    const gg = adjustValue(g).toString(16).padStart(2, '0');
    const bb = adjustValue(b).toString(16).padStart(2, '0');

    return `#${rr}${gg}${bb}`;
  }

  /**
   * Format value for readability (e.g., 1000 -> 1K)
   * @deprecated Use FormatUtils.formatValue instead
   */
  protected formatValue(value: number): string {
    return FormatUtils.formatValue(value);
  }

  /**
   * Format BigInt value for readability
   * @deprecated Use FormatUtils.formatBigIntValue instead
   */
  protected formatBigIntValue(value: bigint): string {
    return FormatUtils.formatBigIntValue(value);
  }

  /**
   * Get format string for a time group (hour, day, week)
   * @deprecated Use TimeUtils.getGroupByFormat instead
   */
  protected getGroupByFormat(groupBy: 'hour' | 'day' | 'week'): string {
    return TimeUtils.getGroupByFormat(groupBy);
  }

  /**
   * Get a date format based on the group by setting
   */
  protected getDateFormat(groupBy: 'hour' | 'day' | 'week'): string {
    switch (groupBy) {
      case 'hour':
        return 'yyyy-MM-dd HH:mm';
      case 'week':
        return "yyyy-MM-dd 'week'";
      case 'day':
      default:
        return 'yyyy-MM-dd';
    }
  }

  /**
   * Generate a random color
   */
  protected getRandomColor(): string {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }

  // Add additional utility functions for consistent color handling
  protected getStandardColor(index: number, opacity: number = 1): string {
    const hexColor = this.colors[index % this.colors.length];
    if (opacity === 1) return hexColor;

    // Convert hex to rgba for opacity
    const r = parseInt(hexColor.slice(1, 3), 16);
    const g = parseInt(hexColor.slice(3, 5), 16);
    const b = parseInt(hexColor.slice(5, 7), 16);
    return `rgba(${r}, ${g}, ${b}, ${opacity})`;
  }

  /**
   * Get display name for a character group
   * Prioritizes main character name if available
   */
  protected getGroupDisplayName(group: {
    name: string;
    characters: Array<{ eveId: string; name: string }>;
    mainCharacterId?: string;
  }): string {
    // Debug log: print group info
    const charList = group.characters.map(c => `${c.eveId}:${c.name}`).join(', ');
    logger.debug('getGroupDisplayName called', {
      groupName: group.name,
      mainCharacterId: group.mainCharacterId,
      characters: charList,
    });
    if (group.mainCharacterId) {
      const main = group.characters.find(c => c.eveId === group.mainCharacterId);
      if (main) {
        logger.debug('Returning main character name', {
          mainCharacterName: main.name,
        });
        return main.name;
      } else {
        logger.debug('Main character ID not found in characters', {
          mainCharacterId: group.mainCharacterId,
        });
      }
    }
    if (group.characters.length > 0) {
      logger.debug('Returning first character name', {
        firstCharacterName: group.characters[0].name,
      });
      return group.characters[0].name;
    }
    logger.debug('Returning group name', {
      groupName: group.name,
    });
    return group.name;
  }

  /**
   * Get dataset colors for a specific chart type
   */
  protected getDatasetColors(type: string): {
    primary: string;
    secondary: string;
  } {
    switch (type) {
      case 'kills':
        return {
          primary: this.colors[0],
          secondary: this.colors[1],
        };
      case 'loss':
        return {
          primary: this.colors[2],
          secondary: this.colors[3],
        };
      case 'map':
        return {
          primary: this.colors[4],
          secondary: this.colors[5],
        };
      default:
        return {
          primary: this.colors[0],
          secondary: this.colors[1],
        };
    }
  }

  // Helper to make lower-value bars more visible by ensuring brightness
  protected getVisibleColors(values: number[], colorSet: string[]): string[] {
    return values.map((value, i) => {
      // For low values, increase brightness to ensure visibility
      if (value > 0 && value < 5) {
        return this.adjustColorBrightness(colorSet[i % colorSet.length], 20);
      }
      return colorSet[i % colorSet.length];
    });
  }

  /**
   * Create default chart options
   */
  protected getDefaultOptions(title: string): ChartOptions {
    return {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        title: {
          display: true,
          text: title,
          font: {
            size: 16,
            weight: 'bold',
          },
        },
        legend: {
          display: true,
          position: 'top',
        },
        tooltip: {
          callbacks: {
            label: function (context: any) {
              const label = context.dataset.label ?? '';
              const value = context.parsed.y !== undefined ? context.parsed.y : context.parsed;
              return `${label}: ${value.toLocaleString()}`;
            },
          },
        },
      },
      scales: {
        x: {
          beginAtZero: true,
          title: {
            display: true,
            text: 'Time',
          },
        },
        y: {
          beginAtZero: true,
          title: {
            display: true,
            text: 'Value',
          },
        },
      },
    };
  }
}

import { BaseRepository } from '../../infrastructure/repositories/BaseRepository';
import { CharacterRepository } from '../../infrastructure/repositories/CharacterRepository';
import { KillRepository } from '../../infrastructure/repositories/KillRepository';
import { MapActivityRepository } from '../../infrastructure/repositories/MapActivityRepository';
import { Character } from '../../domain/character/Character';
import { CharacterGroup } from '../../domain/character/CharacterGroup';
import { PrismaClient } from '@prisma/client';

export abstract class BaseChartService extends BaseRepository {
  protected readonly characterRepository: CharacterRepository;
  protected readonly killRepository: KillRepository;
  protected readonly mapActivityRepository: MapActivityRepository;

  protected colors: string[] = [
    '#3366CC', // deep blue
    '#DC3912', // red
    '#FF9900', // orange
    '#109618', // green
    '#990099', // purple
    '#0099C6', // teal
    '#DD4477', // pink
    '#66AA00', // lime
    '#B82E2E', // dark red
    '#316395', // navy
    '#994499', // violet
    '#22AA99', // seafoam
    '#6633CC', // blue violet
    '#E67300', // dark orange
    '#8B0707', // dark red
    '#651067', // dark purple
    '#329262', // forest green
    '#5574A6', // steel blue
    '#3B3EAC', // royal blue
    '#B77322', // brownish orange
  ];

  constructor(prisma: PrismaClient) {
    super('ChartService'); // BaseRepository expects a model name string
    this.characterRepository = new CharacterRepository(prisma);
    this.killRepository = new KillRepository(prisma);
    this.mapActivityRepository = new MapActivityRepository();
  }

  protected calculateStartDate(period: string): Date {
    const startDate = new Date();
    switch (period) {
      case '24h':
        startDate.setHours(startDate.getHours() - 24);
        break;
      case '7d':
        startDate.setDate(startDate.getDate() - 7);
        break;
      case '30d':
        startDate.setDate(startDate.getDate() - 30);
        break;
      case '90d':
        startDate.setDate(startDate.getDate() - 90);
        break;
      default:
        throw new Error(`Invalid period: ${period}`);
    }
    return startDate;
  }

  protected generateChartTitle(type: string, displayMetric: string, period: string): string {
    const metricLabel =
      displayMetric === 'value'
        ? 'ISK Value'
        : displayMetric === 'kills'
          ? 'Kill Count'
          : displayMetric === 'points'
            ? 'Points'
            : 'Attacker Count';

    return `${type === 'kills' ? 'Kills' : 'Map Activity'} - ${metricLabel} - Last ${
      period === '24h' ? '24 Hours' : period === '7d' ? '7 Days' : period === '30d' ? '30 Days' : '90 Days'
    }`;
  }

  protected getColor(index: number): string {
    return this.colors[index % this.colors.length];
  }

  async getTrackedCharacters(): Promise<Array<{ id: string; name: string; active: boolean }>> {
    const characters = await this.characterRepository.getAllCharacters();
    return characters.map(char => ({
      id: char.eveId.toString(),
      name: char.name,
      active: true, // Assume all tracked characters are active
    }));
  }

  async getCharacterGroups(): Promise<
    Array<{
      id: string;
      name: string;
      description: string;
      active: boolean;
      characters: Array<{ id: string; name: string }>;
    }>
  > {
    const groups = await this.characterRepository.getAllCharacterGroups();
    return groups.map(group => ({
      id: group.id,
      name: group.name,
      description: group.name, // Use name as description since description property doesn't exist
      active: true, // Assume all groups are active
      characters:
        group.characters?.map(char => ({
          id: char.eveId.toString(),
          name: char.name,
        })) || [],
    }));
  }

  async getAllCharacterGroups(): Promise<CharacterGroup[]> {
    return this.characterRepository.getAllCharacterGroups();
  }

  async getCharacterGroup(groupId: string): Promise<CharacterGroup | null> {
    return this.characterRepository.getCharacterGroup(groupId);
  }

  async getAllCharacters(): Promise<Character[]> {
    return this.characterRepository.getAllCharacters();
  }

  async getCharactersByGroup(groupId: string): Promise<Character[]> {
    return this.characterRepository.getCharactersByGroup(groupId);
  }
}

