import { BaseChartGenerator } from '../BaseChartGenerator';
import { ChartData } from '../../../types/chart';
import { CorpsChartConfig } from '../config';
import { KillRepository } from '../../../infrastructure/repositories/KillRepository';
import { format } from 'date-fns';
import { logger } from '../../../lib/logger';
import axios from 'axios';
import { RepositoryManager } from '../../../infrastructure/repositories/RepositoryManager';

/**
 * Generator for enemy corporation charts
 */
export class CorpsChartGenerator extends BaseChartGenerator {
  private killRepository: KillRepository;
  // Helper to fetch corp ticker from ESI and cache it
  private corpTickerCache: Record<string, string> = {};

  /**
   * Create a new corps chart generator
   * @param repoManager Repository manager for data access
   */
  constructor(repoManager: RepositoryManager) {
    super(repoManager);
    this.killRepository = this.repoManager.getKillRepository();
  }

  /**
   * Generate an enemy corporation chart based on the provided options
   */
  async generateChart(options: {
    startDate: Date;
    endDate: Date;
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>;
    displayType: string;
  }): Promise<ChartData> {
    try {
      const { startDate, endDate, characterGroups, displayType } = options;
      logger.info(`Generating enemy corps chart from ${startDate.toISOString()} to ${endDate.toISOString()}`);
      logger.debug(`Chart type: ${displayType}, Groups: ${characterGroups.length}`);

      // Select chart generation function based on display type
      if (displayType === 'verticalBar') {
        return this.generateVerticalBarChart(characterGroups, startDate, endDate);
      } else if (displayType === 'pie') {
        return this.generatePieChart(characterGroups, startDate, endDate);
      } else {
        // Default to horizontal bar chart
        return this.generateHorizontalBarChart(characterGroups, startDate, endDate);
      }
    } catch (error) {
      logger.error('Error generating enemy corps chart:', error);
      throw error;
    }
  }

  // Helper to fetch corp ticker from ESI and cache it
  private async getCorpTicker(corpId: string): Promise<string> {
    if (this.corpTickerCache[corpId]) return this.corpTickerCache[corpId];
    try {
      const resp = await axios.get(`https://esi.evetech.net/latest/corporations/${corpId}/?datasource=tranquility`);
      const ticker = resp.data.ticker || corpId;
      this.corpTickerCache[corpId] = ticker;
      return ticker;
    } catch {
      return corpId;
    }
  }

  /**
   * Generate a horizontal bar chart showing top enemy corporations
   */
  private async generateHorizontalBarChart(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    // Extract all character IDs from the groups
    const characterIds = characterGroups.flatMap(group => group.characters).map(character => BigInt(character.eveId));

    if (characterIds.length === 0) {
      throw new Error('No characters found in the provided groups');
    }

    // Get the top enemy corporations data
    const corpsData = await this.killRepository.getTopEnemyCorporations(
      characterIds,
      startDate,
      endDate,
      15 // Limit to top 15 enemy corporations
    );

    if (corpsData.length === 0) {
      throw new Error('No enemy corporation data found for the specified time period');
    }

    // Lookup tickers for all corpIds
    const labelTickers = await Promise.all(corpsData.map(corp => this.getCorpTicker(corp.corpId)));

    // Sort by kill count in descending order
    corpsData.sort((a, b) => b.killCount - a.killCount);

    // Calculate total kills
    const totalKills = corpsData.reduce((sum, corp) => sum + corp.killCount, 0);

    // Get top corporation information for summary
    const topCorp = corpsData[0];
    const topCorpTicker = labelTickers[0];

    // Create chart data
    const chartData: ChartData = {
      labels: labelTickers,
      datasets: [
        {
          label: 'Kills',
          data: corpsData.map(corp => corp.killCount),
          backgroundColor: corpsData.map((_, i) => CorpsChartConfig.colors[i % CorpsChartConfig.colors.length]),
          borderColor: corpsData.map((_, i) =>
            this.adjustColorBrightness(CorpsChartConfig.colors[i % CorpsChartConfig.colors.length], -20)
          ),
        },
      ],
      displayType: 'bar',
      options: {
        indexAxis: 'y',
      },
      title: `${CorpsChartConfig.title} - ${format(startDate, 'MMM d')} to ${format(endDate, 'MMM d, yyyy')}`,
      summary: CorpsChartConfig.getDefaultSummary(corpsData.length, totalKills, topCorpTicker, topCorp.killCount),
    };

    return chartData;
  }

  /**
   * Generate a vertical bar chart showing top enemy corporations
   */
  private async generateVerticalBarChart(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    // This is similar to horizontal bar chart but with a different orientation
    const chartData = await this.generateHorizontalBarChart(characterGroups, startDate, endDate);

    // Override options for vertical orientation
    chartData.options = CorpsChartConfig.verticalBarOptions;
    chartData.displayType = 'bar';
    return chartData;
  }

  /**
   * Generate a pie chart showing distribution of kills among top enemy corporations
   */
  private async generatePieChart(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    // Extract all character IDs from the groups
    const characterIds = characterGroups.flatMap(group => group.characters).map(character => BigInt(character.eveId));

    if (characterIds.length === 0) {
      throw new Error('No characters found in the provided groups');
    }

    // Get the top enemy corporations data - limit to a smaller number for better pie visualization
    const corpsData = await this.killRepository.getTopEnemyCorporations(
      characterIds,
      startDate,
      endDate,
      10 // Limit to top 10 corps for pie chart
    );

    if (corpsData.length === 0) {
      throw new Error('No enemy corporation data found for the specified time period');
    }

    // Sort by kill count in descending order
    corpsData.sort((a, b) => b.killCount - a.killCount);

    // Calculate total kills for the top corporations
    const totalTopCorpsKills = corpsData.reduce((sum, corp) => sum + corp.killCount, 0);

    // Get total kills for all corporations to calculate "Others" category
    // Note: This is currently a placeholder implementation
    await this.killRepository.getTopEnemyCorporations(characterIds, startDate, endDate);

    // Since this is a placeholder, use the top corps total as the total for now
    const totalAllCorpsKills = totalTopCorpsKills;

    // Calculate "Others" if there's a significant difference
    const otherCorpsKills = totalAllCorpsKills - totalTopCorpsKills;

    // Only add "Others" category if it's more than 1% of total kills
    const othersThreshold = totalAllCorpsKills * 0.01;

    // Copy the data and add "Others" if needed
    const pieLabels = corpsData.map(corp => corp.corpName || corp.corpId);
    const pieData = corpsData.map(corp => corp.killCount);
    const pieColors = corpsData.map((_, i) => CorpsChartConfig.colors[i % CorpsChartConfig.colors.length]);

    if (otherCorpsKills > othersThreshold) {
      pieLabels.push('Others');
      pieData.push(otherCorpsKills);
      pieColors.push('#999999'); // Gray for "Others"
    }

    // Get top corporation information for summary
    const topCorp = corpsData[0];

    // Create chart data
    const chartData: ChartData = {
      labels: pieLabels,
      datasets: [
        {
          label: 'Kills',
          data: pieData,
          backgroundColor: pieColors,
          borderColor: pieColors.map(color => this.adjustColorBrightness(color, -20)),
        },
      ],
      displayType: 'pie',
      title: `Kill Distribution by Enemy Corporation - ${format(
        startDate,
        'MMM d'
      )} to ${format(endDate, 'MMM d, yyyy')}`,
      options: CorpsChartConfig.pieOptions,
      summary: CorpsChartConfig.getDefaultSummary(
        corpsData.length,
        totalAllCorpsKills,
        topCorp.corpName || topCorp.corpId,
        topCorp.killCount
      ),
    };

    return chartData;
  }
}

import { BaseChartGenerator } from '../BaseChartGenerator';
import { ChartData, ChartDisplayType } from '../../../types/chart';
import { KillRepository } from '../../../infrastructure/repositories/KillRepository';
import { LossRepository } from '../../../infrastructure/repositories/LossRepository';
import { logger } from '../../../lib/logger';
import { RepositoryManager } from '../../../infrastructure/repositories/RepositoryManager';

/**
 * Generator for efficiency charts
 */
export class EfficiencyChartGenerator extends BaseChartGenerator {
  private killRepository: KillRepository;
  private lossRepository: LossRepository;

  /**
   * Create a new efficiency chart generator
   * @param repoManager Repository manager for data access
   */
  constructor(repoManager: RepositoryManager) {
    super(repoManager);
    this.killRepository = this.repoManager.getKillRepository();
    this.lossRepository = this.repoManager.getLossRepository();
  }

  async generateChart(options: {
    startDate: Date;
    endDate: Date;
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
      mainCharacterId?: string;
    }>;
    displayType: string;
  }): Promise<ChartData> {
    const { startDate, endDate, characterGroups } = options;
    logger.info('Generating efficiency chart');

    // Get all character IDs from all groups
    const characterIds = characterGroups.flatMap(group => group.characters.map(c => BigInt(c.eveId)));

    // Get kills and losses for all characters
    const [kills, losses] = await Promise.all([
      this.killRepository.getKillsForCharacters(
        characterIds.map(id => BigInt(id)),
        startDate,
        endDate
      ),
      this.lossRepository.getLossesByTimeRange(startDate, endDate),
    ]);

    // Group data by character group
    const groupData = characterGroups.map(group => {
      const groupCharacterIds = group.characters.map(c => BigInt(c.eveId));
      const groupKills = kills.filter(kill => groupCharacterIds.includes(BigInt(kill.character_id)));
      const groupLosses = losses.filter(loss => groupCharacterIds.includes(BigInt(loss.characterId)));

      const totalKills = groupKills.length;
      const totalLosses = groupLosses.length;
      const efficiency = totalKills + totalLosses > 0 ? (totalKills / (totalKills + totalLosses)) * 100 : 0;

      return {
        group,
        kills: groupKills,
        losses: groupLosses,
        totalKills,
        totalLosses,
        efficiency,
      };
    });

    // Sort groups by efficiency
    groupData.sort((a, b) => b.efficiency - a.efficiency);

    // Create chart data
    return {
      labels: groupData.map(data => this.getGroupDisplayName(data.group)),
      datasets: [
        {
          label: 'Efficiency (%)',
          data: groupData.map(data => data.efficiency),
          backgroundColor: this.getDatasetColors('kills').primary,
        },
        {
          label: 'Total Kills',
          data: groupData.map(data => data.totalKills),
          backgroundColor: this.getDatasetColors('kills').secondary,
        },
        {
          label: 'Total Losses',
          data: groupData.map(data => data.totalLosses),
          backgroundColor: this.getDatasetColors('loss').primary,
        },
      ],
      displayType: 'horizontalBar' as ChartDisplayType,
    };
  }
}

import { BaseChartGenerator } from '../BaseChartGenerator';
import { ChartData } from '../../../types/chart';
import { HeatmapChartConfig } from '../config';
import { KillRepository } from '../../../infrastructure/repositories/KillRepository';
import { format } from 'date-fns';
import { logger } from '../../../lib/logger';
import { RepositoryManager } from '../../../infrastructure/repositories/RepositoryManager';

/**
 * Generator for heatmap charts showing activity by time of day
 */
export class HeatmapChartGenerator extends BaseChartGenerator {
  private killRepository: KillRepository;

  /**
   * Create a new heatmap chart generator
   * @param repoManager Repository manager for data access
   */
  constructor(repoManager: RepositoryManager) {
    super(repoManager);
    this.killRepository = this.repoManager.getKillRepository();
  }

  /**
   * Generate a heatmap chart based on the provided options
   */
  async generateChart(options: {
    startDate: Date;
    endDate: Date;
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>;
    displayType: string;
  }): Promise<ChartData> {
    try {
      const { startDate, endDate, characterGroups, displayType } = options;
      logger.info(`Generating heatmap chart from ${startDate.toISOString()} to ${endDate.toISOString()}`);
      logger.debug(`Chart type: ${displayType}, Groups: ${characterGroups.length}`);

      // Select chart generation function based on display type
      if (displayType === 'calendar') {
        return this.generateCalendarHeatmap(characterGroups, startDate, endDate);
      } else {
        // Default to basic heatmap
        return this.generateBasicHeatmap(characterGroups, startDate, endDate);
      }
    } catch (error) {
      logger.error('Error generating heatmap chart:', error);
      throw error;
    }
  }

  /**
   * Generate a basic heatmap showing activity by hour and day of week
   */
  private async generateBasicHeatmap(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    // Combine all character IDs from all groups
    const allCharacterIds: bigint[] = [];
    for (const group of characterGroups) {
      allCharacterIds.push(...group.characters.map(c => BigInt(c.eveId)));
    }

    if (allCharacterIds.length === 0) {
      throw new Error('No characters found in the provided groups');
    }

    // Get activity data grouped by hour and day of week
    const activityData = await this.killRepository.getKillActivityByTimeOfDay(allCharacterIds, startDate, endDate);

    if (activityData.length === 0) {
      throw new Error('No activity data found for the specified time period');
    }

    // Calculate total kills and find the peak activity
    let totalKills = 0;
    let peakKills = 0;
    let peakDay = 0;
    let peakHour = 0;

    for (const data of activityData) {
      totalKills += data.kills;
      if (data.kills > peakKills) {
        peakKills = data.kills;
        peakDay = data.dayOfWeek;
        peakHour = data.hourOfDay;
      }
    }

    // Create a 2D matrix for the heatmap: rows = hours, columns = days
    const matrix: number[][] = [];
    for (let hour = 0; hour < 24; hour++) {
      const row: number[] = [];
      for (let day = 0; day < 7; day++) {
        const activity = activityData.find(d => d.hourOfDay === hour && d.dayOfWeek === day);
        row.push(activity ? activity.kills : 0);
      }
      matrix.push(row);
    }

    // Only include hours with activity
    const filteredMatrix: number[][] = [];
    const filteredHourLabels: string[] = [];
    for (let hour = 0; hour < 24; hour++) {
      if (matrix[hour].some(count => count > 0)) {
        filteredMatrix.push(matrix[hour]);
        filteredHourLabels.push(HeatmapChartConfig.hours[hour]);
      }
    }

    // Convert the 2D matrix to ComplexDataPoint[] for heatmap plugin compatibility
    const heatmapData = [];
    for (let i = 0; i < filteredMatrix.length; i++) {
      for (let j = 0; j < filteredMatrix[i].length; j++) {
        heatmapData.push({
          x: HeatmapChartConfig.shortDaysOfWeek[j],
          y: filteredHourLabels[i],
          v: filteredMatrix[i][j],
        });
      }
    }

    const datasets = [
      {
        label: 'Kill Activity',
        data: heatmapData,
        backgroundColor: undefined, // Let the plugin handle coloring
      },
    ];

    // Create chart data
    const chartData: ChartData = {
      labels: HeatmapChartConfig.shortDaysOfWeek,
      datasets,
      displayType: 'heatmap', // Custom type for heatmap
      title: `${HeatmapChartConfig.title} - ${format(startDate, 'MMM d')} to ${format(endDate, 'MMM d, yyyy')}`,
      options: HeatmapChartConfig.heatmapOptions,
      summary: HeatmapChartConfig.getDefaultSummary(
        totalKills,
        HeatmapChartConfig.daysOfWeek[peakDay],
        HeatmapChartConfig.hours[peakHour],
        peakKills
      ),
    };

    return chartData;
  }

  /**
   * Generate a calendar-style heatmap showing activity over time
   */
  private async generateCalendarHeatmap(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    // Combine all character IDs from all groups
    const allCharacterIds: bigint[] = [];
    for (const group of characterGroups) {
      allCharacterIds.push(...group.characters.map(c => BigInt(c.eveId)));
    }

    if (allCharacterIds.length === 0) {
      throw new Error('No characters found in the provided groups');
    }

    // For calendar view, we need kills grouped by date
    const killData = await this.killRepository.getKillsGroupedByTime(allCharacterIds, startDate, endDate, 'day');

    if (killData.length === 0) {
      throw new Error('No activity data found for the specified time period');
    }

    // Calculate totals and find peak activity
    let totalKills = 0;
    let peakKills = 0;
    let peakDate = new Date();

    for (const data of killData) {
      totalKills += data.kills;
      if (data.kills > peakKills) {
        peakKills = data.kills;
        peakDate = data.timestamp;
      }
    }

    // Format data for calendar heatmap
    const calendarData = killData.map(data => ({
      x: format(data.timestamp, 'yyyy-MM-dd'),
      y: data.timestamp.getDay(), // Day of week (0-6)
      v: data.kills, // Value (kill count)
      date: format(data.timestamp, 'EEEE, MMMM d, yyyy'),
    }));

    // Create datasets
    const datasets = [
      {
        label: 'Kill Activity',
        data: calendarData,
        backgroundColor: (context: any) => {
          const value = context.dataset.data[context.dataIndex].v;
          return this.getHeatmapColor(value, peakKills);
        },
      },
    ];

    // Create chart data
    const chartData: ChartData = {
      labels: HeatmapChartConfig.shortDaysOfWeek,
      datasets,
      displayType: 'calendar', // Custom type for calendar heatmap
      title: `${HeatmapChartConfig.title} - ${format(startDate, 'MMM d')} to ${format(endDate, 'MMM d, yyyy')}`,
      options: HeatmapChartConfig.calendarOptions,
      summary: HeatmapChartConfig.getDefaultSummary(
        totalKills,
        format(peakDate, 'EEEE'),
        format(peakDate, 'HH:mm'),
        peakKills
      ),
    };

    return chartData;
  }

  /**
   * Get color for a heatmap cell based on value intensity
   */
  private getHeatmapColor(value: number, maxValue: number): string {
    if (value === 0) return HeatmapChartConfig.colorGradient[0]; // No activity

    const normalizedValue = value / maxValue; // 0 to 1
    const gradient = HeatmapChartConfig.colorGradient;

    if (normalizedValue < 0.25) {
      return gradient[1]; // Low activity
    } else if (normalizedValue < 0.5) {
      return gradient[2]; // Medium activity
    } else if (normalizedValue < 0.75) {
      return gradient[3]; // High activity
    } else {
      return gradient[4]; // Very high activity
    }
  }
}

import { BaseChartGenerator } from '../BaseChartGenerator';
import { ChartData, ChartDisplayType } from '../../../types/chart';
import { logger } from '../../../lib/logger';
import { KillsChartConfig } from '../config';
import { KillRepository } from '../../../infrastructure/repositories/KillRepository';
import { RepositoryManager } from '../../../infrastructure/repositories/RepositoryManager';

interface Kill {
  killmailId: bigint;
  killTime: Date;
  victim?: { characterId?: bigint };
  attackers?: Array<{ characterId?: bigint }>;
  solo: boolean;
  [key: string]: any; // For other properties
}

interface Attacker {
  characterId?: bigint;
}

/**
 * Generator for kill-related charts
 */
export class KillsChartGenerator extends BaseChartGenerator {
  private killRepository: KillRepository;

  /**
   * Create a new kills chart generator
   * @param repoManager Repository manager for data access
   */
  constructor(repoManager: RepositoryManager) {
    super(repoManager);
    this.killRepository = this.repoManager.getKillRepository();
  }

  /**
   * Generate a kills chart based on the provided options
   */
  async generateChart(options: {
    startDate: Date;
    endDate: Date;
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
      mainCharacterId?: string;
    }>;
    displayType: string;
  }): Promise<ChartData> {
    const { startDate, endDate, characterGroups } = options;

    logger.info(`KillsChartGenerator: Processing ${characterGroups.length} character groups`);
    logger.info(`Date range: ${startDate.toISOString()} to ${endDate.toISOString()}`);

    // Get all character IDs from all groups
    const characterIds = characterGroups.flatMap(group => group.characters.map(c => BigInt(c.eveId)));

    logger.info(`Total characters across all groups: ${characterIds.length}`);

    // Get kills for all characters - use the new method that includes participation as attackers
    const kills = await this.killRepository.getAllKillsForCharacters(characterIds, startDate, endDate);

    logger.info(`Found ${kills.length} total kills across all characters (including as attackers)`);

    // Debug: Log the first few kills to see their structure
    if (kills.length > 0) {
      logger.info('Sample kill data structure:');
      kills.slice(0, 3).forEach((kill, index) => {
        logger.info(`Kill ${index + 1}:`);
        logger.info(`  killmailId: ${kill.killmailId} (type: ${typeof kill.killmailId})`);
        logger.info(`  victim: ${JSON.stringify(kill.victim)} (type: ${typeof kill.victim})`);
        logger.info(`  attackers_count: ${kill.attackers?.length ?? 0}`);
        logger.info(`  all_keys: ${Object.keys(kill).join(', ')}`);
        if (kill.attackers?.[0]) {
          logger.info(`  first_attacker: ${JSON.stringify(kill.attackers[0])}`);
          logger.info(`  first_attacker_keys: ${Object.keys(kill.attackers[0]).join(', ')}`);
        }
      });
    }

    // Debug: Log character IDs we're looking for
    logger.info(
      `Character IDs we're looking for: ${characterIds
        .slice(0, 5)
        .map(id => id.toString())
        .join(', ')}`
    );

    // Log distribution of kills across characters
    const killsByCharacter = new Map<string, number>();
    kills.forEach(kill => {
      // Count kills by attackers (those who participated in the kill)
      if (kill.attackers) {
        kill.attackers.forEach((attacker: any) => {
          if (attacker.characterId != null) {
            const charId = attacker.characterId.toString();
            killsByCharacter.set(charId, (killsByCharacter.get(charId) ?? 0) + 1);
          }
        });
      }
    });

    logger.info(`Kill distribution: ${killsByCharacter.size} characters have kills as main killer`);
    logger.info(
      `Characters with most kills as main: ${Array.from(killsByCharacter.entries())
        .sort((a, b) => b[1] - a[1])
        .slice(0, 5)
        .map(([charId, count]) => `${charId}:${count}`)
        .join(', ')}`
    );

    // Group kills by character group, but now include any kill where a group member is on the killmail
    const groupData = characterGroups.map(group => {
      const groupName = this.getGroupDisplayName(group);
      const groupCharacterIds = group.characters.map(c => BigInt(c.eveId));

      // Use a Set to track unique killmail IDs to avoid double-counting
      const uniqueGroupKillIds = new Set<string>();

      // A kill belongs to the group if:
      // 1. The main character on the kill is in the group, OR
      // 2. Any of the attackers are in the group
      kills.forEach((kill: Kill) => {
        const killmailIdStr = kill.killmailId?.toString();
        if (!killmailIdStr) return;

        // Skip if we've already counted this killmail
        if (uniqueGroupKillIds.has(killmailIdStr)) {
          return;
        }

        // Debug for first group only to avoid spam
        const isFirstGroup = group === characterGroups[0];

        // Check if any attackers are in group (kills are attributed to attackers)
        if (kill.attackers && kill.attackers.length > 0) {
          for (const attacker of kill.attackers) {
            if (!attacker.characterId) continue;

            const attackerCharId = BigInt(attacker.characterId);
            if (groupCharacterIds.includes(attackerCharId)) {
              if (isFirstGroup && kills.indexOf(kill) < 3) {
                logger.info(`Found attacker match in kill ${killmailIdStr}: attacker=${attacker.characterId}`);
              }
              uniqueGroupKillIds.add(killmailIdStr);
              return;
            }
          }
        }

        // Also check if victim is in group (for loss tracking)
        if (kill.victim?.characterId != null) {
          const victimCharId = BigInt(kill.victim.characterId);
          const isVictimInGroup = groupCharacterIds.includes(victimCharId);

          if (isFirstGroup && kills.indexOf(kill) < 3) {
            logger.info(
              `Checking kill ${killmailIdStr}: victim=${kill.victim.characterId}, isInGroup=${isVictimInGroup}`
            );
          }

          if (isVictimInGroup) {
            uniqueGroupKillIds.add(killmailIdStr);
            return;
          }
        }
      });

      // Now get the actual kill objects for the unique IDs
      const groupKills = kills.filter((kill: Kill) => {
        const killmailIdStr = kill.killmailId?.toString();
        return killmailIdStr && uniqueGroupKillIds.has(killmailIdStr);
      });

      // Calculate total kills using the unique count
      const totalKills = uniqueGroupKillIds.size;

      logger.info(`Group ${groupName}: ${totalKills} unique kills, ${group.characters.length} characters`);

      if (groupKills.length > 0) {
        // List the first few character IDs that have kills in this group
        const characterIdsWithKills = new Set<string>();
        groupKills.forEach(kill => {
          if (kill.attackers) {
            kill.attackers.forEach((attacker: any) => {
              if (attacker.characterId != null) {
                characterIdsWithKills.add(attacker.characterId.toString());
              }
            });
          }
        });
        logger.info(
          `Characters with kills in group ${groupName}: ${Array.from(characterIdsWithKills)
            .slice(0, 3)
            .join(', ')}${characterIdsWithKills.size > 3 ? '...' : ''} (${characterIdsWithKills.size} total)`
        );
      }

      // Calculate solo kills - either true solo (1 attacker) or group solo (all attackers from same group)
      let soloKills = 0;
      for (const kill of groupKills) {
        // Skip kills with no attackers
        if (!kill.attackers || kill.attackers.length === 0) continue;

        // Get all player attackers (those with character IDs)
        const playerAttackers = kill.attackers.filter((a: Attacker) => a.characterId != null);
        if (playerAttackers.length === 0) continue;

        // Count as solo if either:
        // 1. It's a true solo kill (only one player attacker)
        // 2. All player attackers are from this group
        const isTrueSolo = playerAttackers.length === 1;
        const allFromGroup = playerAttackers.every((attacker: Attacker) => {
          if (!attacker.characterId) return false;
          return groupCharacterIds.includes(BigInt(attacker.characterId));
        });

        if (isTrueSolo || allFromGroup) {
          soloKills++;
          const killmailIdStr = kill.killmailId?.toString() ?? 'unknown';
          logger.info(
            `Found ${isTrueSolo ? 'true' : 'group'} solo kill for group ${groupName}: Kill ID ${killmailIdStr} - ${
              playerAttackers.length
            } player attackers`
          );
        }
      }

      return {
        group,
        kills: groupKills,
        totalKills,
        soloKills,
      };
    });

    // Log all group data before filtering
    groupData.forEach(data => {
      const groupName = this.getGroupDisplayName(data.group);
      logger.info(`Group ${groupName}: ${data.totalKills} total kills, ${data.soloKills} solo kills`);
    });

    // Filter out groups with no kills
    const groupsWithKills = groupData.filter(data => data.totalKills > 0);

    logger.info(`Filtered to ${groupsWithKills.length} groups with kills out of ${groupData.length} total groups`);

    // List the groups that were filtered out
    const filteredOutGroups = groupData
      .filter(data => data.totalKills === 0)
      .map(data => this.getGroupDisplayName(data.group));

    if (filteredOutGroups.length > 0) {
      logger.info(
        `Groups with no kills: ${filteredOutGroups.slice(0, 5).join(', ')}${
          filteredOutGroups.length > 5 ? '...' : ''
        } (${filteredOutGroups.length} total)`
      );
    }

    // If no groups have kills, return empty chart
    if (groupsWithKills.length === 0) {
      logger.info('No groups with kills found, returning empty chart');
      return {
        labels: [],
        datasets: [],
        displayType: 'horizontalBar' as ChartDisplayType,
        summary: 'No kills found in the specified time period',
      };
    }

    // Sort groups by total kills
    groupsWithKills.sort((a, b) => b.totalKills - a.totalKills);

    logger.info(
      `Top 5 groups by kill count: ${groupsWithKills
        .slice(0, 5)
        .map(g => `${this.getGroupDisplayName(g.group)}: ${g.totalKills}`)
        .join(', ')}`
    );

    // Create chart data
    return {
      labels: groupsWithKills.map(data => this.getGroupDisplayName(data.group)),
      datasets: [
        {
          label: 'Total Kills',
          data: groupsWithKills.map(data => data.totalKills),
          backgroundColor: this.getDatasetColors('kills').primary,
        },
        {
          label: 'Solo Kills',
          data: groupsWithKills.map(data => data.soloKills),
          backgroundColor: this.getDatasetColors('kills').secondary,
        },
      ],
      displayType: 'horizontalBar' as ChartDisplayType,
      options: {
        indexAxis: 'y',
        scales: {
          x: {
            stacked: true,
            beginAtZero: true,
          },
          y: {
            stacked: true,
          },
        },
        plugins: {
          legend: {
            position: 'top',
          },
        },
      },
      summary: KillsChartConfig.getDefaultSummary(
        groupsWithKills.reduce((total, data) => total + data.totalKills, 0),
        groupsWithKills.reduce((total, data) => total + data.soloKills, 0)
      ),
    };
  }
}

import { BaseChartGenerator } from '../BaseChartGenerator';
import { ChartData, ChartDisplayType } from '../../../types/chart';
import { LossChartConfig } from '../config';
import { logger } from '../../../lib/logger';
import { LossRepository } from '../../../infrastructure/repositories/LossRepository';
import { RepositoryManager } from '../../../infrastructure/repositories/RepositoryManager';
import { LossFact } from '../../../domain/killmail/LossFact';

/**
 * Generator for loss charts
 */
export class LossChartGenerator extends BaseChartGenerator {
  private lossRepository: LossRepository;

  /**
   * Create a new loss chart generator
   * @param repoManager Repository manager for data access
   */
  constructor(repoManager: RepositoryManager) {
    super(repoManager);
    this.lossRepository = this.repoManager.getLossRepository();
  }

  /**
   * Generate a loss chart
   */
  async generateChart(options: {
    startDate: Date;
    endDate: Date;
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
      mainCharacterId?: string;
    }>;
    displayType: string;
  }): Promise<ChartData> {
    const { startDate, endDate, characterGroups } = options;

    // Get all character IDs from all groups
    const characterIds = characterGroups.flatMap(group => group.characters.map(c => BigInt(c.eveId)));

    // Debug: Log character IDs and time range
    logger.info(`[LossChart] Character IDs: ${characterIds.map(String).join(', ')}`);
    logger.info(`[LossChart] Time range: ${startDate.toISOString()} to ${endDate.toISOString()}`);

    // Get losses for all characters
    const losses = await this.lossRepository.getLossesByTimeRange(startDate, endDate);

    // Debug: Log total number of losses returned
    logger.info(`[LossChart] Total losses returned from repository: ${losses.length}`);

    // Remove duplicate killmail_ids from losses
    const seenKillmails = new Set<bigint>();
    const dedupedLosses: LossFact[] = [];
    let duplicateCount = 0;
    for (const loss of losses) {
      if (!seenKillmails.has(loss.killmailId)) {
        seenKillmails.add(loss.killmailId);
        dedupedLosses.push(loss);
      } else {
        duplicateCount++;
      }
    }
    if (duplicateCount > 0) {
      logger.warn(`[LossChart] Removed ${duplicateCount} duplicate losses by killmail_id`);
    } else {
      logger.info(`[LossChart] No duplicate losses found by killmail_id`);
    }

    // Group losses by character group
    const groupData = characterGroups.map(group => {
      const groupCharacterIds = group.characters.map(c => BigInt(c.eveId));
      const groupLosses = dedupedLosses.filter((loss: LossFact) => groupCharacterIds.includes(loss.characterId));

      // Debug: Log number of losses for this group
      logger.info(`[LossChart] Group '${group.name}' (${group.groupId}) has ${groupLosses.length} losses`);

      // Calculate total losses and value
      const totalLosses = groupLosses.length;
      const totalValue = groupLosses.reduce((sum: bigint, loss: LossFact) => sum + loss.totalValue, BigInt(0));
      const highValueLosses = groupLosses.filter(loss => loss.totalValue >= BigInt(100000000)).length;

      return {
        group,
        losses: groupLosses,
        totalLosses,
        totalValue,
        highValueLosses,
      };
    });

    // Filter out groups with no losses
    const groupsWithLosses = groupData.filter(data => data.totalLosses > 0);

    // If no groups have losses, return empty chart
    if (groupsWithLosses.length === 0) {
      logger.info('No groups with losses found, returning empty chart');
      return {
        labels: [],
        datasets: [],
        displayType: 'horizontalBar' as ChartDisplayType,
        summary: 'No losses found in the specified time period',
      };
    }

    // Sort groups by total value
    groupsWithLosses.sort((a, b) => {
      if (a.totalValue > b.totalValue) return -1;
      if (a.totalValue < b.totalValue) return 1;
      return 0;
    });

    // Calculate totals for summary
    const totalLosses = groupsWithLosses.reduce((sum, data) => sum + data.totalLosses, 0);
    const totalHighValueLosses = groupsWithLosses.reduce((sum, data) => sum + data.highValueLosses, 0);
    const totalIskLost = groupsWithLosses.reduce((sum, data) => sum + data.totalValue, BigInt(0));

    // Format ISK value
    const formatIsk = (value: bigint): string => {
      const valueNumber = Number(value);
      if (valueNumber >= 1_000_000_000_000) {
        return `${(valueNumber / 1_000_000_000_000).toFixed(2)}T`;
      } else if (valueNumber >= 1_000_000_000) {
        return `${(valueNumber / 1_000_000_000).toFixed(2)}B`;
      } else if (valueNumber >= 1_000_000) {
        return `${(valueNumber / 1_000_000).toFixed(2)}M`;
      } else if (valueNumber >= 1_000) {
        return `${(valueNumber / 1_000).toFixed(2)}K`;
      } else {
        return valueNumber.toString();
      }
    };

    // Create chart data
    // Round ISK lost to billions for chart
    const valueLostBillions = groupsWithLosses.map(data => +(Number(data.totalValue) / 1_000_000_000).toFixed(2));

    return {
      labels: groupsWithLosses.map(data => this.getGroupDisplayName(data.group)),
      datasets: [
        {
          label: 'Number of Losses',
          data: groupsWithLosses.map(data => data.totalLosses),
          backgroundColor: this.getDatasetColors('loss').secondary,
          yAxisID: 'y',
          type: 'bar',
        },
        {
          label: 'Total Value Lost (B ISK)',
          data: valueLostBillions,
          backgroundColor: `${this.getDatasetColors('loss').primary}33`,
          borderColor: this.getDatasetColors('loss').primary,
          borderWidth: 2,
          tension: 0.3,
          yAxisID: 'y2',
          type: 'line',
        },
      ],
      displayType: 'horizontalBar' as ChartDisplayType,
      options: {
        indexAxis: 'y',
        scales: {
          x: {
            grid: {
              color: '#444',
            },
            ticks: {
              color: '#fff',
            },
          },
          y: {
            type: 'linear',
            position: 'left',
            title: {
              display: true,
              text: 'Loss Count',
            },
            beginAtZero: true,
            grid: {
              color: '#444',
            },
            ticks: {
              color: '#fff',
            },
          },
          y2: {
            type: 'linear',
            position: 'right',
            title: {
              display: true,
              text: 'ISK Lost (Billion)',
            },
            beginAtZero: true,
            grid: {
              drawOnChartArea: false,
              color: '#444',
            },
            ticks: {
              color: '#fff',
            },
          },
        },
        plugins: {
          legend: {
            position: 'top',
          },
        },
      },
      summary: LossChartConfig.getDefaultSummary(totalLosses, totalHighValueLosses, formatIsk(totalIskLost)),
    };
  }

  /**
   * Generate a chart showing daily losses over time
   * @param start Start date for the chart
   * @param end End date for the chart
   */
  async generateDailyLossesChart(start: string, end: string): Promise<ChartData> {
    const startDate = new Date(start);
    const endDate = new Date(end);

    // Get losses for the time range
    const losses = await this.lossRepository.getLossesByTimeRange(startDate, endDate);

    // Group losses by day
    const groupedLosses = losses.reduce(
      (acc, loss) => {
        const date = loss.killTime.toISOString().split('T')[0];
        if (!acc[date]) {
          acc[date] = [];
        }
        acc[date].push(loss);
        return acc;
      },
      {} as Record<string, LossFact[]>
    );

    // Calculate daily totals
    const data = Object.entries(groupedLosses).map(([date, dayLosses]) => ({
      date,
      value: dayLosses.reduce((sum, loss) => sum + Number(loss.totalValue), 0),
      count: dayLosses.length,
    }));

    // Sort by date
    data.sort((a, b) => a.date.localeCompare(b.date));

    return {
      title: 'Daily Losses',
      labels: data.map(d => d.date),
      datasets: [
        {
          label: 'Total Value Lost (ISK)',
          data: data.map(d => d.value),
          backgroundColor: 'rgba(255, 99, 132, 0.5)',
          borderColor: 'rgb(255, 99, 132)',
        },
        {
          label: 'Number of Losses',
          data: data.map(d => d.count),
          backgroundColor: 'rgba(54, 162, 235, 0.5)',
          borderColor: 'rgb(54, 162, 235)',
        },
      ],
      displayType: 'line' as ChartDisplayType,
    };
  }

  /**
   * Generate a chart showing losses by ship type
   * @param start Start date for the chart
   * @param end End date for the chart
   */
  async generateShipTypeLossesChart(start: string, end: string): Promise<ChartData> {
    const startDate = new Date(start);
    const endDate = new Date(end);

    // Get losses for the time range
    const losses = await this.lossRepository.getLossesByTimeRange(startDate, endDate);

    // Group losses by ship type
    const groupedLosses = losses.reduce(
      (acc, loss) => {
        if (!acc[loss.shipTypeId]) {
          acc[loss.shipTypeId] = [];
        }
        acc[loss.shipTypeId].push(loss);
        return acc;
      },
      {} as Record<number, LossFact[]>
    );

    // Calculate totals by ship type
    const data = Object.entries(groupedLosses).map(([shipTypeId, shipLosses]) => ({
      shipTypeId: Number(shipTypeId),
      value: shipLosses.reduce((sum, loss) => sum + Number(loss.totalValue), 0),
      count: shipLosses.length,
    }));

    // Sort by value
    data.sort((a, b) => b.value - a.value);

    return {
      title: 'Losses by Ship Type',
      labels: data.map(d => `Ship Type ${d.shipTypeId}`),
      datasets: [
        {
          label: 'Total Value Lost (ISK)',
          data: data.map(d => d.value),
          backgroundColor: 'rgba(255, 99, 132, 0.5)',
          borderColor: 'rgb(255, 99, 132)',
        },
        {
          label: 'Number of Losses',
          data: data.map(d => d.count),
          backgroundColor: 'rgba(54, 162, 235, 0.5)',
          borderColor: 'rgb(54, 162, 235)',
        },
      ],
      displayType: 'bar' as ChartDisplayType,
    };
  }
}

import { BaseChartGenerator } from '../BaseChartGenerator';
import { ChartData, ChartDisplayType } from '../../../types/chart';
import { MapActivityRepository } from '../../../infrastructure/repositories/MapActivityRepository';
import { RepositoryManager } from '../../../infrastructure/repositories/RepositoryManager';
import { logger } from '../../../lib/logger';

/**
 * Generator for map activity charts
 */
export class MapChartGenerator extends BaseChartGenerator {
  private mapActivityRepository: MapActivityRepository;

  /**
   * Create a new map activity chart generator
   * @param repoManager Repository manager for data access
   */
  constructor(repoManager: RepositoryManager) {
    super(repoManager);
    this.mapActivityRepository = this.repoManager.getMapActivityRepository();
  }

  /**
   * Generate a map activity chart based on the provided options
   */
  async generateChart(options: {
    startDate: Date;
    endDate: Date;
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
      mainCharacterId?: string;
    }>;
    displayType: string;
  }): Promise<ChartData> {
    const { startDate, endDate, characterGroups } = options;

    // Get all character IDs from all groups
    const characterIds = characterGroups.flatMap(group => group.characters.map(c => c.eveId));

    // Get map activity for all characters
    const activities = await this.mapActivityRepository.getActivityForCharacters(characterIds, startDate, endDate);

    // Group activities by character group
    const groupData = characterGroups.map(group => {
      // Filter out characters with invalid eveIds and convert valid ones to BigInt
      const validCharacters = group.characters.filter(c => {
        // Debug log the actual character data
        logger.info(`Character in group ${group.name}: ${JSON.stringify(c)}`);

        if (!c.eveId || c.eveId === '' || c.eveId === 'undefined' || c.eveId === 'null') {
          logger.warn(`Skipping character with invalid eveId in group ${group.name}: ${JSON.stringify(c)}`);
          return false;
        }
        return true;
      });

      const groupCharacterIds = validCharacters
        .map(c => {
          try {
            return BigInt(c.eveId);
          } catch (error) {
            logger.warn(`Failed to convert character eveId to BigInt: ${c.eveId}`, error);
            return null;
          }
        })
        .filter((id): id is bigint => id !== null);

      const groupActivities = activities.filter(activity => {
        if (activity.characterId == null || activity.characterId === undefined) {
          return false;
        }
        return groupCharacterIds.includes(activity.characterId);
      });

      // Calculate totals for each metric
      const totalSignatures = groupActivities.reduce((sum, activity) => sum + activity.signatures, 0);
      const totalConnections = groupActivities.reduce((sum, activity) => sum + activity.connections, 0);
      const totalPassages = groupActivities.reduce((sum, activity) => sum + activity.passages, 0);

      return {
        group,
        activities: groupActivities,
        totalSignatures,
        totalConnections,
        totalPassages,
      };
    });

    // Sort groups by total activity (sum of all metrics)
    groupData.sort(
      (a, b) =>
        b.totalSignatures +
        b.totalConnections +
        b.totalPassages -
        (a.totalSignatures + a.totalConnections + a.totalPassages)
    );

    // Create chart data
    return {
      labels: groupData.map(data => this.getGroupDisplayName(data.group)),
      datasets: [
        {
          label: 'Signatures',
          data: groupData.map(data => data.totalSignatures),
          backgroundColor: this.getDatasetColors('map').primary,
        },
        {
          label: 'Connections',
          data: groupData.map(data => data.totalConnections),
          backgroundColor: this.getDatasetColors('map').secondary,
        },
        {
          label: 'Passages',
          data: groupData.map(data => data.totalPassages),
          backgroundColor: this.getColorForIndex(2),
        },
      ],
      displayType: 'horizontalBar' as ChartDisplayType,
    };
  }
}

import { BaseChartGenerator } from '../BaseChartGenerator';
import { ChartData } from '../../../types/chart';
import { logger } from '../../../lib/logger';
import { KillRepository } from '../../../infrastructure/repositories/KillRepository';
import { LossRepository } from '../../../infrastructure/repositories/LossRepository';
import { RepositoryManager } from '../../../infrastructure/repositories/RepositoryManager';

/**
 * Generator for kill-death ratio charts
 */
export class RatioChartGenerator extends BaseChartGenerator {
  private killRepository: KillRepository;
  private lossRepository: LossRepository;

  /**
   * Create a new ratio chart generator
   * @param repoManager Repository manager for data access
   */
  constructor(repoManager: RepositoryManager) {
    super(repoManager);
    this.killRepository = this.repoManager.getKillRepository();
    this.lossRepository = this.repoManager.getLossRepository();
  }

  /**
   * Generate a kill-death ratio chart
   */
  async generateChart(options: {
    startDate: Date;
    endDate: Date;
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
      mainCharacterId?: string;
    }>;
    displayType: string;
  }): Promise<ChartData> {
    logger.info('Generating kill-death ratio chart');

    const { startDate, endDate, characterGroups } = options;

    // Prepare data arrays
    const filteredLabels: string[] = [];
    const kdRatios: number[] = [];
    const efficiencies: number[] = [];

    for (const group of characterGroups) {
      const characterIds = group.characters.map(char => BigInt(char.eveId));
      if (characterIds.length === 0) continue;
      // Compute stats manually
      const kills = await this.killRepository.getKillsForCharacters(characterIds, startDate, endDate);
      const lossesSummary = await this.lossRepository.getLossesSummaryByCharacters(characterIds, startDate, endDate);
      const totalKills = kills.length;
      const totalLosses = lossesSummary.totalLosses;
      // Only include groups with at least one kill or death
      if (totalKills > 0 || totalLosses > 0) {
        // Use main character name if available
        let label = group.name;
        if (group.mainCharacterId) {
          const mainChar = group.characters.find(c => c.eveId === group.mainCharacterId);
          if (mainChar) label = mainChar.name;
        } else if (group.characters.length > 0) {
          label = group.characters[0].name;
        }
        filteredLabels.push(label);
        let kdRatio = 0;
        if (totalLosses > 0) {
          kdRatio = totalKills / totalLosses;
        } else if (totalKills > 0) {
          kdRatio = totalKills;
        }
        let efficiency = 0;
        if (totalKills + totalLosses > 0) {
          efficiency = (totalKills / (totalKills + totalLosses)) * 100;
        }
        kdRatios.push(kdRatio);
        efficiencies.push(efficiency);
      }
    }

    // Generate summary text
    const timeRangeText = this.getTimeRangeText(startDate, endDate);
    let summary = `Kill-Death ratios for tracked characters (${timeRangeText})`;

    // Add top performer if there's data
    if (Math.max(...kdRatios) > 0) {
      const bestGroupIndex = kdRatios.indexOf(Math.max(...kdRatios));
      summary += `\nBest performer: ${
        filteredLabels[bestGroupIndex]
      } with K/D ratio of ${kdRatios[bestGroupIndex].toFixed(2)}`;
    }

    return {
      labels: filteredLabels,
      datasets: [
        {
          label: 'K/D Ratio',
          data: kdRatios,
          backgroundColor: '#3366CC',
          borderColor: '#3366CC',
        },
        {
          label: 'Efficiency %',
          data: efficiencies,
          backgroundColor: '#DC3912',
          borderColor: '#DC3912',
        },
      ],
      title: `Kill-Death Ratio - ${timeRangeText}`,
      summary,
      displayType: 'bar',
    };
  }

  /**
   * Get a formatted string describing the time range
   */
  private getTimeRangeText(startDate: Date, endDate: Date): string {
    const diffDays = Math.floor((endDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));

    if (diffDays <= 1) {
      return 'Last 24 hours';
    } else if (diffDays <= 7) {
      return 'Last 7 days';
    } else if (diffDays <= 30) {
      return 'Last 30 days';
    } else {
      return `${startDate.toLocaleDateString()} to ${endDate.toLocaleDateString()}`;
    }
  }
}

import { BaseChartGenerator } from '../BaseChartGenerator';
import { ChartData } from '../../../types/chart';
import { ShipTypesChartConfig } from '../config';
import { KillRepository } from '../../../infrastructure/repositories/KillRepository';
import { format } from 'date-fns';
import { logger } from '../../../lib/logger';
import axios from 'axios';
import { RepositoryManager } from '../../../infrastructure/repositories/RepositoryManager';

export class ShipKillChartGenerator extends BaseChartGenerator {
  private killRepository: KillRepository;
  private shipTypeNameCache: Record<string, string> = {};

  /**
   * Create a new ship kill chart generator
   * @param repoManager Repository manager for data access
   */
  constructor(repoManager: RepositoryManager) {
    super(repoManager);
    this.killRepository = this.repoManager.getKillRepository();
  }

  async generateChart(options: {
    startDate: Date;
    endDate: Date;
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>;
    displayType: string;
  }): Promise<ChartData> {
    try {
      const { startDate, endDate, characterGroups, displayType } = options;
      logger.info(`Generating ship kill chart from ${startDate.toISOString()} to ${endDate.toISOString()}`);
      logger.debug(`Chart type: ${displayType}, Groups: ${characterGroups.length}`);
      if (displayType === 'horizontalBar') {
        return this.generateHorizontalBarChart(characterGroups, startDate, endDate);
      } else if (displayType === 'verticalBar') {
        return this.generateVerticalBarChart(characterGroups, startDate, endDate);
      } else {
        return this.generateTimelineChart(characterGroups, startDate, endDate);
      }
    } catch (error) {
      logger.error('Error generating ship kill chart:', error);
      throw error;
    }
  }

  private async getShipTypeName(typeId: string): Promise<string> {
    if (this.shipTypeNameCache[typeId]) return this.shipTypeNameCache[typeId];
    try {
      const resp = await axios.get(`https://esi.evetech.net/latest/universe/types/${typeId}/?datasource=tranquility`);
      const name = resp.data.name ?? typeId;
      this.shipTypeNameCache[typeId] = name;
      return name;
    } catch {
      return typeId;
    }
  }

  private async generateHorizontalBarChart(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    const characterIds = characterGroups.flatMap(group => group.characters).map(character => BigInt(character.eveId));
    if (characterIds.length === 0) {
      throw new Error('No characters found in the provided groups');
    }
    const shipTypesData = await this.killRepository.getTopShipTypesDestroyed(characterIds, startDate, endDate, 15);
    if (shipTypesData.length === 0) {
      throw new Error('No ship type data found for the specified time period');
    }
    const shipTypeNames = await Promise.all(shipTypesData.map(type => this.getShipTypeName(type.shipTypeId)));
    const filtered = shipTypesData
      .map((type, i) => ({ ...type, name: shipTypeNames[i] }))
      .filter(entry => entry.name.toLowerCase() !== 'capsule');
    if (filtered.length === 0) {
      throw new Error('No ship type data found for the specified time period (after filtering capsules)');
    }
    filtered.sort((a, b) => b.count - a.count);
    const totalDestroyed = filtered.reduce((sum, shipType) => sum + shipType.count, 0);
    const chartData: ChartData = {
      labels: filtered.map(type => type.name),
      datasets: [
        {
          label: 'Ships Destroyed',
          data: filtered.map(type => type.count),
          backgroundColor: this.getVisibleColors(
            filtered.map(type => type.count),
            ShipTypesChartConfig.colors
          ),
          borderColor: ShipTypesChartConfig.colors.map(color => this.adjustColorBrightness(color, -20)),
        },
      ],
      displayType: 'bar',
      options: {
        indexAxis: 'y',
      },
      title: `${ShipTypesChartConfig.title} - ${format(startDate, 'MMM d')} to ${format(endDate, 'MMM d, yyyy')}`,
      summary: ShipTypesChartConfig.getDefaultSummary(filtered.length, totalDestroyed),
    };
    return chartData;
  }

  private async generateVerticalBarChart(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    const chartData = await this.generateHorizontalBarChart(characterGroups, startDate, endDate);
    chartData.options = ShipTypesChartConfig.verticalBarOptions;
    chartData.displayType = 'bar';
    return chartData;
  }

  private async generateTimelineChart(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    const characterIds = characterGroups.flatMap(group => group.characters).map(character => BigInt(character.eveId));
    if (characterIds.length === 0) {
      throw new Error('No characters found in the provided groups');
    }
    const shipTypesTimeData = await this.killRepository.getShipTypesOverTime(
      characterIds,
      startDate,
      endDate,
      'day' // interval should be a string
    );
    if (Object.keys(shipTypesTimeData).length === 0) {
      throw new Error('No ship type time data found for the specified period');
    }
    const dates = Object.keys(shipTypesTimeData).sort();
    const shipTypeTotals = new Map<string, { total: number }>();
    for (const date of dates) {
      const dateData = shipTypesTimeData[date];
      for (const [shipTypeId, data] of Object.entries(dateData)) {
        const current = shipTypeTotals.get(shipTypeId) ?? {
          total: 0,
        };
        if (data && typeof data === 'object' && 'count' in data) {
          current.total += (data as any).count;
        }
        shipTypeTotals.set(shipTypeId, current);
      }
    }
    const topShipTypes = Array.from(shipTypeTotals.entries())
      .sort((a, b) => b[1].total - a[1].total)
      .slice(0, 5)
      .map(([id]) => id);
    const shipTypeNames = await Promise.all(topShipTypes.map(typeId => this.getShipTypeName(typeId)));
    const datasets = topShipTypes.map((shipTypeId, index) => {
      const data = dates.map(date => {
        const dateData = shipTypesTimeData[date];
        return dateData[shipTypeId]?.count ?? 0;
      });
      return {
        label: shipTypeNames[index],
        data,
        backgroundColor: ShipTypesChartConfig.colors[index % ShipTypesChartConfig.colors.length],
        borderColor: ShipTypesChartConfig.colors[index % ShipTypesChartConfig.colors.length],
      };
    });
    let totalDestroyed = 0;
    for (const date of dates) {
      const dateData = shipTypesTimeData[date];
      for (const data of Object.values(dateData)) {
        if (data && typeof data === 'object' && 'count' in data) {
          totalDestroyed += (data as any).count;
        }
      }
    }
    const chartData: ChartData = {
      labels: dates.map(date => format(new Date(date), 'MMM d')),
      datasets,
      displayType: 'line',
      title: `${ShipTypesChartConfig.title} Over Time - ${format(
        startDate,
        'MMM d'
      )} to ${format(endDate, 'MMM d, yyyy')}`,
      options: ShipTypesChartConfig.timelineOptions,
      summary: `Showing top ${
        topShipTypes.length
      } ship types destroyed over time (${totalDestroyed.toLocaleString()} total kills)`,
    };
    return chartData;
  }
}

