import { TrendChartConfig } from '../config';
import { KillRepository } from '../../../infrastructure/repositories/KillRepository';
import { format } from 'date-fns';
import { logger } from '../../../lib/logger';
import { RepositoryManager } from '../../../infrastructure/repositories/RepositoryManager';

/**
 * Generator for trend charts showing kills over time
 */
export class TrendChartGenerator extends BaseChartGenerator {
  private killRepository: KillRepository;

  /**
   * Create a new trend chart generator
   * @param repoManager Repository manager for data access
   */
  constructor(repoManager: RepositoryManager) {
    super(repoManager);
    this.killRepository = this.repoManager.getKillRepository();
  }

  /**
   * Generate a trend chart based on the provided options
   */
  override async generateChart(options: {
    startDate: Date;
    endDate: Date;
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>;
    displayType: string;
  }): Promise<ChartData> {
    try {
      const { startDate, endDate, characterGroups, displayType } = options;
      logger.info(`Generating trend chart from ${startDate.toISOString()} to ${endDate.toISOString()}`);
      logger.debug(`Chart type: ${displayType}, Groups: ${characterGroups.length}`);

      // Select chart generation function based on display type
      if (displayType === 'area') {
        return this.generateAreaChart(characterGroups, startDate, endDate);
      } else if (displayType === 'dual') {
        return this.generateDualAxisChart(characterGroups, startDate, endDate);
      } else {
        // Default to line chart (timeline)
        return this.generateTimelineChart(characterGroups, startDate, endDate);
      }
    } catch (error) {
      logger.error('Error generating trend chart:', error);
      throw error;
    }
  }

  /**
   * Generate a timeline chart showing kills over time
   */
  private async generateTimelineChart(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    // First, determine the appropriate time grouping based on date range
    const dateRange = endDate.getTime() - startDate.getTime();
    const days = dateRange / (1000 * 60 * 60 * 24);

    let groupBy: 'hour' | 'day' | 'week' = 'day';
    if (days <= 2) {
      groupBy = 'hour';
    } else if (days > 30) {
      groupBy = 'week';
    }

    // Create a dataset for each character group
    const datasets = [];
    const timeLabels = new Set<string>();
    let overallTotalKills = 0;
    const allDataPoints: number[] = []; // Used for trend calculation

    // Process each group
    for (let i = 0; i < characterGroups.length; i++) {
      const group = characterGroups[i];

      // Get all character IDs for this group
      const characterIds = group.characters.map(char => BigInt(char.eveId));

      if (characterIds.length === 0) {
        continue;
      }

      // Get kill data grouped by time
      const killData = await this.killRepository.getKillsGroupedByTime(characterIds, startDate, endDate, groupBy);

      // Skip if no data
      if (killData.length === 0) {
        continue;
      }

      // Determine proper display name for the group
      let displayName = group.name;

      // Try to find the main character or use the first character
      if (group.characters.length > 0) {
        const mainCharacter = group.characters.find(char =>
          group.characters.some(c => c.eveId !== char.eveId && c.name.includes(char.name.split(' ')[0]))
        );

        if (mainCharacter) {
          displayName = mainCharacter.name;
        } else {
          displayName = group.characters[0].name;
        }
      }

      // Extract the data points and collect labels
      const dataPoints: number[] = [];
      const timePoints: string[] = [];

      let groupTotalKills = 0;

      for (const point of killData) {
        const formattedTime = format(point.timestamp, this.getDateFormat(groupBy));
        timePoints.push(formattedTime);
        timeLabels.add(formattedTime);

        dataPoints.push(point.kills);
        groupTotalKills += point.kills;
        allDataPoints.push(point.kills); // For trend calculation
      }

      // Update overall statistics
      overallTotalKills += groupTotalKills;

      // Add dataset for this group
      datasets.push({
        label: displayName,
        data: dataPoints,
        backgroundColor: TrendChartConfig.colors[i % TrendChartConfig.colors.length],
        borderColor: TrendChartConfig.colors[i % TrendChartConfig.colors.length],
        fill: false,
      });
    }

    // Sort the time labels chronologically
    const sortedLabels = Array.from(timeLabels).sort((a, b) => {
      const dateA = new Date(a);
      const dateB = new Date(b);
      return dateA.getTime() - dateB.getTime();
    });

    // Calculate average kills per day
    const dayCount = Math.ceil((endDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));
    const averageKillsPerDay = overallTotalKills / dayCount;

    // Calculate the trend (increasing, stable, or decreasing)
    const trend = this.calculateTrend(allDataPoints);

    // Create chart data
    const chartData: ChartData = {
      labels: sortedLabels,
      datasets,
      displayType: 'line',
      title: `${TrendChartConfig.title} - ${format(startDate, 'MMM d')} to ${format(endDate, 'MMM d, yyyy')}`,
      options: TrendChartConfig.timelineOptions,
      summary: TrendChartConfig.getDefaultSummary(overallTotalKills, averageKillsPerDay, trend),
    };

    return chartData;
  }

  /**
   * Generate an area chart showing cumulative kills over time
   */
  private async generateAreaChart(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    // First, determine the appropriate time grouping based on date range
    const dateRange = endDate.getTime() - startDate.getTime();
    const days = dateRange / (1000 * 60 * 60 * 24);

    let groupBy: 'hour' | 'day' | 'week' = 'day';
    if (days <= 2) {
      groupBy = 'hour';
    } else if (days > 30) {
      groupBy = 'week';
    }

    // Create a dataset for each character group
    const datasets = [];
    const timeLabels = new Set<string>();
    let overallTotalKills = 0;
    const allDataPoints: number[] = []; // Used for trend calculation

    // Process each group
    for (let i = 0; i < characterGroups.length; i++) {
      const group = characterGroups[i];

      // Get all character IDs for this group
      const characterIds = group.characters.map(char => BigInt(char.eveId));

      if (characterIds.length === 0) {
        continue;
      }

      // Get kill data grouped by time
      const killData = await this.killRepository.getKillsGroupedByTime(characterIds, startDate, endDate, groupBy);

      // Skip if no data
      if (killData.length === 0) {
        continue;
      }

      // Determine proper display name for the group
      let displayName = group.name;

      // Try to find the main character or use the first character
      if (group.characters.length > 0) {
        const mainCharacter = group.characters.find(char =>
          group.characters.some(c => c.eveId !== char.eveId && c.name.includes(char.name.split(' ')[0]))
        );

        if (mainCharacter) {
          displayName = mainCharacter.name;
        } else {
          displayName = group.characters[0].name;
        }
      }

      // Extract the data points and collect labels (cumulative sum)
      const dataPoints: number[] = [];
      const timePoints: string[] = [];

      let cumulativeKills = 0;
      let groupTotalKills = 0;

      for (const point of killData) {
        const formattedTime = format(point.timestamp, this.getDateFormat(groupBy));
        timePoints.push(formattedTime);
        timeLabels.add(formattedTime);

        cumulativeKills += point.kills;
        dataPoints.push(cumulativeKills);
        groupTotalKills += point.kills;
        allDataPoints.push(point.kills); // For trend calculation
      }

      // Update overall statistics
      overallTotalKills += groupTotalKills;

      // Add dataset for this group with area filling
      datasets.push({
        label: displayName,
        data: dataPoints,
        backgroundColor: this.adjustColorTransparency(TrendChartConfig.colors[i % TrendChartConfig.colors.length], 0.6),
        borderColor: TrendChartConfig.colors[i % TrendChartConfig.colors.length],
        fill: true,
      });
    }

    // Sort the time labels chronologically
    const sortedLabels = Array.from(timeLabels).sort((a, b) => {
      const dateA = new Date(a);
      const dateB = new Date(b);
      return dateA.getTime() - dateB.getTime();
    });

    // Calculate average kills per day
    const dayCount = Math.ceil((endDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));
    const averageKillsPerDay = overallTotalKills / dayCount;

    // Calculate the trend (increasing, stable, or decreasing)
    const trend = this.calculateTrend(allDataPoints);

    // Create chart data
    const chartData: ChartData = {
      labels: sortedLabels,
      datasets,
      displayType: 'line', // Still use line type but with fill
      title: `Cumulative ${TrendChartConfig.title} - ${format(
        startDate,
        'MMM d'
      )} to ${format(endDate, 'MMM d, yyyy')}`,
      options: TrendChartConfig.areaOptions,
      summary: TrendChartConfig.getDefaultSummary(overallTotalKills, averageKillsPerDay, trend),
    };

    return chartData;
  }

  /**
   * Generate a dual-axis chart showing kills and value over time
   */
  private async generateDualAxisChart(
    characterGroups: Array<{
      groupId: string;
      name: string;
      characters: Array<{ eveId: string; name: string }>;
    }>,
    startDate: Date,
    endDate: Date
  ): Promise<ChartData> {
    // Combine all groups for a total view
    const allCharacterIds: bigint[] = [];

    // Extract all character IDs from all groups
    for (const group of characterGroups) {
      allCharacterIds.push(...group.characters.map(char => BigInt(char.eveId)));
    }

    if (allCharacterIds.length === 0) {
      throw new Error('No characters found in the provided groups');
    }

    // Determine appropriate time grouping
    const dateRange = endDate.getTime() - startDate.getTime();
    const days = dateRange / (1000 * 60 * 60 * 24);

    let groupBy: 'hour' | 'day' | 'week' = 'day';
    if (days <= 2) {
      groupBy = 'hour';
    } else if (days > 30) {
      groupBy = 'week';
    }

    // Get kill data grouped by time
    const killData = await this.killRepository.getKillsGroupedByTime(allCharacterIds, startDate, endDate, groupBy);

    if (killData.length === 0) {
      throw new Error('No kill data found for the specified time period');
    }

    // Prepare data for the dual-axis chart
    const killsData: number[] = [];
    const valueData: number[] = []; // Convert bigint to number for charting
    const timeLabels: string[] = [];

    let totalKills = 0;
    const allKillsData: number[] = []; // For trend calculation

    // Process the kill data
    for (const point of killData) {
      const formattedTime = format(point.timestamp, this.getDateFormat(groupBy));
      timeLabels.push(formattedTime);

      killsData.push(point.kills);
      allKillsData.push(point.kills);
      totalKills += point.kills;

      // Convert bigint to number, handling potential overflow
      const valueInISK = Number(point.value) > Number.MAX_SAFE_INTEGER ? Number.MAX_SAFE_INTEGER : Number(point.value);
      valueData.push(valueInISK);
    }

    // Create datasets for kills (line) and values (bar)
    const datasets = [
      {
        label: 'Kills',
        data: killsData,
        backgroundColor: this.adjustColorTransparency('#3366CC', 0.7),
        borderColor: '#3366CC',
        fill: false,
        type: 'line' as 'line',
        yAxisID: 'y',
      },
      {
        label: 'Total Value (ISK)',
        data: valueData,
        backgroundColor: this.adjustColorTransparency('#DC3912', 0.7),
        borderColor: '#DC3912',
        fill: false,
        type: 'bar' as 'bar',
        yAxisID: 'y1',
      },
    ];

    // Calculate average kills per day
    const dayCount = Math.ceil((endDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));
    const averageKillsPerDay = totalKills / dayCount;

    // Calculate the trend (increasing, stable, or decreasing)
    const trend = this.calculateTrend(allKillsData);

    // Create chart data
    const chartData: ChartData = {
      labels: timeLabels,
      datasets,
      displayType: 'line', // Use line type for dual-axis chart
      title: `Kills vs. Value Over Time - ${format(startDate, 'MMM d')} to ${format(endDate, 'MMM d, yyyy')}`,
      options: TrendChartConfig.dualAxisOptions,
      summary: `${TrendChartConfig.getDefaultSummary(totalKills, averageKillsPerDay, trend)} with value metrics`,
    };

    return chartData;
  }

  /**
   * Calculate trend direction from a series of data points
   */
  private calculateTrend(dataPoints: number[]): 'increasing' | 'stable' | 'decreasing' {
    if (dataPoints.length < 3) {
      return 'stable'; // Not enough data to determine trend
    }

    // Simple linear regression to determine trend
    const n = dataPoints.length;
    const xValues = Array.from({ length: n }, (_, i) => i);

    // Calculate means
    const xMean = xValues.reduce((sum, x) => sum + x, 0) / n;
    const yMean = dataPoints.reduce((sum, y) => sum + y, 0) / n;

    // Calculate slope
    let numerator = 0;
    let denominator = 0;

    for (let i = 0; i < n; i++) {
      numerator += (xValues[i] - xMean) * (dataPoints[i] - yMean);
      denominator += Math.pow(xValues[i] - xMean, 2);
    }

    const slope = denominator !== 0 ? numerator / denominator : 0;

    // Determine trend direction
    if (slope > 0.05) {
      return 'increasing';
    } else if (slope < -0.05) {
      return 'decreasing';
    } else {
      return 'stable';
    }
  }

  /**
   * Get a date format string based on the grouping level
   */
  protected override getDateFormat(groupBy: 'hour' | 'day' | 'week'): string {
    switch (groupBy) {
      case 'hour':
        return 'MMM d, HH:mm';
      case 'day':
        return 'MMM d';
      case 'week':
        return "'Week' W, MMM yyyy";
      default:
        return 'MMM d';
    }
  }

  /**
   * Adjust color transparency (for area charts)
   */
  private adjustColorTransparency(color: string, alpha: number): string {
    if (color.startsWith('#')) {
      // Convert hex to rgb
      const r = parseInt(color.substring(1, 3), 16);
      const g = parseInt(color.substring(3, 5), 16);
      const b = parseInt(color.substring(5, 7), 16);
      return `rgba(${r}, ${g}, ${b}, ${alpha})`;
    } else if (color.startsWith('rgb(')) {
      // Convert rgb to rgba
      return color.replace('rgb(', 'rgba(').replace(')', `, ${alpha})`);
    } else if (color.startsWith('rgba(')) {
      // Replace existing alpha
      return color.replace(/,\s*[\d.]+\)$/, `, ${alpha})`);
    }
    return color;
  }
}

import { BaseChartService } from '../BaseChartService';
import { IKillsChartService } from '../interfaces/IKillsChartService';
import { ChartData, ChartDisplayType, ChartMetric } from '../../../types/chart';
import { Character } from '../../../domain/character/Character';
import { format } from 'date-fns';
import { logger } from '../../../lib/logger';

interface KillData {
  killTime: Date;
  totalValue: bigint;
  points: number;
  attackerCount: number;
  characters: Array<{ characterId: bigint }>;
}

export class KillsChartService extends BaseChartService implements IKillsChartService {
  async generateKillsChart(
    characterIds: string[],
    startDate: Date,
    groupBy: string,
    displayMetric: ChartMetric,
    limit: number
  ): Promise<ChartData> {
    const characterIdsBigInt = characterIds.map(id => BigInt(id));

    logger.info(`Generating kills chart for ${characterIds.length} characters from ${startDate.toISOString()}`);
    logger.info(`Character IDs: ${characterIds.join(', ')}`);

    try {
      // Find all related characters via character groups
      const allCharactersNested = await Promise.all(
        characterIds.map(async (id: string) => {
          const character = await this.characterRepository.getCharacter(BigInt(id));
          if (character?.characterGroupId) {
            return this.characterRepository.getCharactersByGroup(character.characterGroupId);
          }
          return character ? [character] : [];
        })
      );
      const allCharacters = allCharactersNested.flat();
      const allCharacterIds = allCharacters.map((c: Character) => c.eveId);

      logger.info(`Including all characters in same groups: ${allCharacters.length} characters total`);

      // Get kills for characters
      logger.info('Querying killFact table with expanded character list...');
      const killsQuery = await this.killRepository.getKillsForCharacters(
        allCharacterIds.map(id => BigInt(id)),
        startDate,
        new Date()
      );

      logger.info(`Found ${killsQuery.length} kill records in database`);

      if (killsQuery.length === 0) {
        return this.createEmptyKillsChart(characterIdsBigInt, groupBy, limit);
      }

      const kills = killsQuery.map((kill: any) => {
        logger.debug(`Processing kill: ID ${kill.killmail_id}, time ${kill.kill_time}, character ${kill.character_id}`);

        return {
          killTime: new Date(kill.kill_time),
          totalValue: BigInt(kill.total_value),
          points: kill.points,
          attackerCount: kill.attackers?.length ?? 0,
          characters:
            kill.characters?.map((char: any) => ({
              characterId: BigInt(char.character_id),
            })) ?? [],
        };
      });

      // Group data by time periods and character
      const groupedData = this.groupKillsByTimeAndCharacter(kills, groupBy, allCharacters);

      // Create chart datasets
      const datasets = await this.createKillsDatasets(groupedData, characterIdsBigInt, displayMetric, limit);

      // Generate time labels
      const labels = this.generateTimeLabels(startDate, new Date(), groupBy);

      return {
        labels,
        datasets,
        title: this.generateChartTitle('kills', displayMetric, this.periodFromDates(startDate)),
        displayType: 'line' as ChartDisplayType,
      };
    } catch (error) {
      logger.error('Error generating kills chart:', error);
      throw error;
    }
  }

  async generateGroupedKillsChart(config: {
    characterGroups: string[];
    period: string;
    groupBy: string;
    displayType: ChartDisplayType;
    displayMetric: ChartMetric;
    limit: number;
  }): Promise<ChartData> {
    const startDate = this.calculateStartDate(config.period);

    logger.info(
      `Generating grouped kills chart for ${config.characterGroups.length} groups from ${startDate.toISOString()}`
    );

    try {
      // Get all characters from the specified groups
      const characterGroups = await Promise.all(
        config.characterGroups.map(async groupId => {
          const group = await this.characterRepository.getCharacterGroup(groupId);
          return {
            group,
            characters: group ? await this.characterRepository.getCharactersByGroup(groupId) : [],
          };
        })
      );

      // Process each group
      const datasets = await Promise.all(
        characterGroups.map(async (groupData, index) => {
          const { group, characters } = groupData;

          if (!group || characters.length === 0) {
            return {
              label: group?.name ?? `Group ${index + 1}`,
              data: [],
              borderColor: this.getColor(index),
              fill: false,
            };
          }

          const characterIds = characters.map(c => BigInt(c.eveId));
          const killsQuery = await this.killRepository.getKillsForCharacters(characterIds, startDate, new Date());

          const kills = killsQuery.map((kill: any) => ({
            killTime: new Date(kill.kill_time),
            totalValue: BigInt(kill.total_value),
            points: kill.points,
            attackerCount: kill.attackers?.length ?? 0,
            characters:
              kill.characters?.map((char: any) => ({
                characterId: BigInt(char.character_id),
              })) ?? [],
          }));

          const groupedData = this.groupKillsByTime(kills, config.groupBy);
          const chartData = this.calculateDisplayMetricData(groupedData, config.displayMetric);

          return {
            label: group.name,
            data: chartData,
            borderColor: this.getColor(index),
            fill: false,
          };
        })
      );

      const labels = this.generateTimeLabels(startDate, new Date(), config.groupBy);

      return {
        labels,
        datasets,
        title: this.generateChartTitle('kills', config.displayMetric, config.period),
        displayType: config.displayType,
      };
    } catch (error) {
      logger.error('Error generating grouped kills chart:', error);
      throw error;
    }
  }

  private async createEmptyKillsChart(characterIds: bigint[], groupBy: string, limit: number): Promise<ChartData> {
    logger.warn('No kills found for the specified characters and time period');

    const emptyDatasets = await Promise.all(
      characterIds.slice(0, limit).map(async (characterId, index) => {
        try {
          const character = await this.characterRepository.getCharacter(characterId);
          return {
            label: character?.name ?? `Character ${characterId}`,
            data: [],
            borderColor: this.getColor(index),
            fill: false,
          };
        } catch (err) {
          return {
            label: `Character ${characterId}`,
            data: [],
            borderColor: this.getColor(index),
            fill: false,
          };
        }
      })
    );

    const labels = this.generateEmptyTimeLabels(groupBy);

    return {
      labels,
      datasets: emptyDatasets,
      title: '',
      displayType: 'line' as ChartDisplayType,
    };
  }

  private groupKillsByTimeAndCharacter(
    kills: KillData[],
    groupBy: string,
    characters: Character[]
  ): Map<string, Map<string, any>> {
    const grouped = new Map<string, Map<string, any>>();

    kills.forEach(kill => {
      const timeKey = this.getTimeKey(kill.killTime, groupBy);

      if (!grouped.has(timeKey)) {
        grouped.set(timeKey, new Map());
      }

      const timeGroup = grouped.get(timeKey)!;

      kill.characters.forEach(killChar => {
        const character = characters.find(c => BigInt(c.eveId) === killChar.characterId);
        if (character) {
          const charKey = character.eveId.toString();

          if (!timeGroup.has(charKey)) {
            timeGroup.set(charKey, {
              kills: 0,
              totalValue: BigInt(0),
              points: 0,
              attackers: 0,
            });
          }

          const charData = timeGroup.get(charKey);
          charData.kills += 1;
          charData.totalValue += kill.totalValue;
          charData.points += kill.points;
          charData.attackers += kill.attackerCount;
        }
      });
    });

    return grouped;
  }

  private groupKillsByTime(kills: KillData[], groupBy: string): Map<string, any> {
    const grouped = new Map<string, any>();

    kills.forEach(kill => {
      const timeKey = this.getTimeKey(kill.killTime, groupBy);

      if (!grouped.has(timeKey)) {
        grouped.set(timeKey, {
          kills: 0,
          totalValue: BigInt(0),
          points: 0,
          attackers: 0,
        });
      }

      const timeData = grouped.get(timeKey);
      timeData.kills += 1;
      timeData.totalValue += kill.totalValue;
      timeData.points += kill.points;
      timeData.attackers += kill.attackerCount;
    });

    return grouped;
  }

  private async createKillsDatasets(
    groupedData: Map<string, Map<string, any>>,
    characterIds: bigint[],
    displayMetric: ChartMetric,
    limit: number
  ): Promise<any[]> {
    const limitedCharacterIds = characterIds.slice(0, limit);

    return Promise.all(
      limitedCharacterIds.map(async (characterId, index) => {
        const character = await this.characterRepository.getCharacter(characterId);
        const charKey = characterId.toString();

        const data: number[] = [];

        for (const [, timeGroup] of groupedData) {
          const charData = timeGroup.get(charKey);
          if (charData) {
            data.push(this.getMetricValue(charData, displayMetric));
          } else {
            data.push(0);
          }
        }

        return {
          label: character?.name ?? `Character ${characterId}`,
          data,
          borderColor: this.getColor(index),
          fill: false,
        };
      })
    );
  }

  private calculateDisplayMetricData(groupedData: Map<string, any>, displayMetric: ChartMetric): number[] {
    const data: number[] = [];

    for (const [, timeData] of groupedData) {
      data.push(this.getMetricValue(timeData, displayMetric));
    }

    return data;
  }

  private getMetricValue(data: any, displayMetric: ChartMetric): number {
    switch (displayMetric) {
      case 'value':
        return Number(data.totalValue) / 1000000; // Convert to millions
      case 'kills':
        return data.kills;
      case 'points':
        return data.points;
      case 'attackers':
        return data.attackers;
      default:
        return 0;
    }
  }

  private getTimeKey(date: Date, groupBy: string): string {
    switch (groupBy) {
      case 'hour':
        return format(date, 'yyyy-MM-dd HH:00');
      case 'day':
        return format(date, 'yyyy-MM-dd');
      case 'week': {
        const weekStart = new Date(date);
        weekStart.setDate(date.getDate() - date.getDay());
        return format(weekStart, 'yyyy-MM-dd');
      }
      default:
        return format(date, 'yyyy-MM-dd');
    }
  }

  private generateTimeLabels(startDate: Date, endDate: Date, groupBy: string): string[] {
    const labels: string[] = [];
    const current = new Date(startDate);

    while (current <= endDate) {
      labels.push(this.formatLabel(current, groupBy));

      switch (groupBy) {
        case 'hour':
          current.setHours(current.getHours() + 1);
          break;
        case 'day':
          current.setDate(current.getDate() + 1);
          break;
        case 'week':
          current.setDate(current.getDate() + 7);
          break;
      }
    }

    return labels;
  }

  private generateEmptyTimeLabels(groupBy: string): string[] {
    const labels = [];
    const today = new Date();
    const daysToGenerate = groupBy === 'hour' ? 1 : groupBy === 'day' ? 7 : 4;

    for (let i = daysToGenerate - 1; i >= 0; i--) {
      const date = new Date(today);
      date.setDate(date.getDate() - i);
      labels.push(this.formatLabel(date, groupBy));
    }

    return labels;
  }

  private formatLabel(date: Date, groupBy: string): string {
    switch (groupBy) {
      case 'hour':
        return format(date, 'HH:mm');
      case 'day':
        return format(date, 'MMM dd');
      case 'week':
        return format(date, 'MMM dd');
      default:
        return format(date, 'MMM dd');
    }
  }

  private periodFromDates(startDate: Date): string {
    const now = new Date();
    const diffMs = now.getTime() - startDate.getTime();
    const diffHours = diffMs / (1000 * 60 * 60);
    const diffDays = diffHours / 24;

    if (diffHours <= 24) return '24h';
    if (diffDays <= 7) return '7d';
    if (diffDays <= 30) return '30d';
    return '90d';
  }
}

import { BaseChartService } from '../BaseChartService';
import { IMapActivityChartService } from '../interfaces/IMapActivityChartService';
import { ChartData, ChartDisplayType, ChartMetric } from '../../../types/chart';
import { Character } from '../../../domain/character/Character';
import { format } from 'date-fns';
import { logger } from '../../../lib/logger';

interface ActivityData {
  timestamp: Date;
  signatures: number;
  characterId: bigint;
}

export class MapActivityChartService extends BaseChartService implements IMapActivityChartService {
  async generateMapActivityChart(
    characterIds: string[],
    startDate: Date,
    groupBy: string,
    displayMetric: ChartMetric,
    limit: number
  ): Promise<ChartData> {
    const characterIdsBigInt = characterIds.map(id => BigInt(id));

    logger.info(`Generating map activity chart for ${characterIds.length} characters from ${startDate.toISOString()}`);

    try {
      // Find all related characters via character groups
      const allCharactersNested = await Promise.all(
        characterIds.map(async (id: string) => {
          const character = await this.characterRepository.getCharacter(BigInt(id));
          if (character?.characterGroupId) {
            return this.characterRepository.getCharactersByGroup(character.characterGroupId);
          }
          return character ? [character] : [];
        })
      );
      const allCharacters = allCharactersNested.flat();
      const allCharacterIds = allCharacters.map((c: Character) => c.eveId);

      // Get map activity data
      const activityQuery = await this.mapActivityRepository.getActivityForCharacters(
        allCharacterIds,
        startDate,
        new Date()
      );

      logger.info(`Found ${activityQuery.length} activity records in database`);

      if (activityQuery.length === 0) {
        return this.createEmptyActivityChart(characterIdsBigInt, groupBy, limit);
      }

      const activities = activityQuery.map((activity: any) => ({
        timestamp: new Date(activity.timestamp),
        signatures: activity.signatures ?? 0,
        characterId: BigInt(activity.character_id),
      }));

      // Group data by time periods and character
      const groupedData = this.groupActivitiesByTimeAndCharacter(activities, groupBy, allCharacters);

      // Create chart datasets
      const datasets = await this.createActivityDatasets(groupedData, characterIdsBigInt, displayMetric, limit);

      // Generate time labels
      const labels = this.generateTimeLabels(startDate, new Date(), groupBy);

      return {
        labels,
        datasets,
        title: this.generateChartTitle('map_activity', displayMetric, this.periodFromDates(startDate)),
        displayType: 'line' as ChartDisplayType,
      };
    } catch (error) {
      logger.error('Error generating map activity chart:', error);
      throw error;
    }
  }

  async generateGroupedMapActivityChart(config: {
    characterGroups: string[];
    period: string;
    groupBy: string;
    displayType: ChartDisplayType;
    displayMetric: ChartMetric;
    limit: number;
  }): Promise<ChartData> {
    const startDate = this.calculateStartDate(config.period);

    logger.info(
      `Generating grouped map activity chart for ${config.characterGroups.length} groups from ${startDate.toISOString()}`
    );

    try {
      // Get all characters from the specified groups
      const characterGroups = await Promise.all(
        config.characterGroups.map(async groupId => {
          const group = await this.characterRepository.getCharacterGroup(groupId);
          return {
            group,
            characters: group ? await this.characterRepository.getCharactersByGroup(groupId) : [],
          };
        })
      );

      // Process each group
      const datasets = await Promise.all(
        characterGroups.map(async (groupData, index) => {
          const { group, characters } = groupData;

          if (!group || characters.length === 0) {
            return {
              label: group?.name ?? `Group ${index + 1}`,
              data: [],
              borderColor: this.getColor(index),
              fill: false,
            };
          }

          const characterIds = characters.map(c => c.eveId);
          const activitiesQuery = await this.mapActivityRepository.getActivityForCharacters(
            characterIds,
            startDate,
            new Date()
          );

          const activities = activitiesQuery.map((activity: any) => ({
            timestamp: new Date(activity.timestamp),
            signatures: activity.signatures ?? 0,
            characterId: BigInt(activity.character_id),
          }));

          const groupedData = this.groupActivitiesByTime(activities, config.groupBy);
          const chartData = this.calculateDisplayMetricData(groupedData, config.displayMetric);

          return {
            label: group.name,
            data: chartData,
            borderColor: this.getColor(index),
            fill: false,
          };
        })
      );

      const labels = this.generateTimeLabels(startDate, new Date(), config.groupBy);

      return {
        labels,
        datasets,
        title: this.generateChartTitle('map_activity', config.displayMetric, config.period),
        displayType: config.displayType,
      };
    } catch (error) {
      logger.error('Error generating grouped map activity chart:', error);
      throw error;
    }
  }

  private async createEmptyActivityChart(characterIds: bigint[], groupBy: string, limit: number): Promise<ChartData> {
    logger.warn('No map activity found for the specified characters and time period');

    const emptyDatasets = await Promise.all(
      characterIds.slice(0, limit).map(async (characterId, index) => {
        try {
          const character = await this.characterRepository.getCharacter(characterId);
          return {
            label: character?.name ?? `Character ${characterId}`,
            data: [],
            borderColor: this.getColor(index),
            fill: false,
          };
        } catch (err) {
          return {
            label: `Character ${characterId}`,
            data: [],
            borderColor: this.getColor(index),
            fill: false,
          };
        }
      })
    );

    const labels = this.generateEmptyTimeLabels(groupBy);

    return {
      labels,
      datasets: emptyDatasets,
      title: '',
      displayType: 'line' as ChartDisplayType,
    };
  }

  private groupActivitiesByTimeAndCharacter(
    activities: ActivityData[],
    groupBy: string,
    characters: Character[]
  ): Map<string, Map<string, any>> {
    const grouped = new Map<string, Map<string, any>>();

    activities.forEach(activity => {
      const timeKey = this.getTimeKey(activity.timestamp, groupBy);

      if (!grouped.has(timeKey)) {
        grouped.set(timeKey, new Map());
      }

      const timeGroup = grouped.get(timeKey)!;
      const character = characters.find(c => BigInt(c.eveId) === activity.characterId);

      if (character) {
        const charKey = character.eveId.toString();

        if (!timeGroup.has(charKey)) {
          timeGroup.set(charKey, {
            signatures: 0,
            activities: 0,
          });
        }

        const charData = timeGroup.get(charKey);
        charData.signatures += activity.signatures;
        charData.activities += 1;
      }
    });

    return grouped;
  }

  private groupActivitiesByTime(activities: ActivityData[], groupBy: string): Map<string, any> {
    const grouped = new Map<string, any>();

    activities.forEach(activity => {
      const timeKey = this.getTimeKey(activity.timestamp, groupBy);

      if (!grouped.has(timeKey)) {
        grouped.set(timeKey, {
          signatures: 0,
          activities: 0,
        });
      }

      const timeData = grouped.get(timeKey);
      timeData.signatures += activity.signatures;
      timeData.activities += 1;
    });

    return grouped;
  }

  private async createActivityDatasets(
    groupedData: Map<string, Map<string, any>>,
    characterIds: bigint[],
    displayMetric: ChartMetric,
    limit: number
  ): Promise<any[]> {
    const limitedCharacterIds = characterIds.slice(0, limit);

    return Promise.all(
      limitedCharacterIds.map(async (characterId, index) => {
        const character = await this.characterRepository.getCharacter(characterId);
        const charKey = characterId.toString();

        const data: number[] = [];

        for (const [, timeGroup] of groupedData) {
          const charData = timeGroup.get(charKey);
          if (charData) {
            data.push(this.getMetricValue(charData, displayMetric));
          } else {
            data.push(0);
          }
        }

        return {
          label: character?.name ?? `Character ${characterId}`,
          data,
          borderColor: this.getColor(index),
          fill: false,
        };
      })
    );
  }

  private calculateDisplayMetricData(groupedData: Map<string, any>, displayMetric: ChartMetric): number[] {
    const data: number[] = [];

    for (const [, timeData] of groupedData) {
      data.push(this.getMetricValue(timeData, displayMetric));
    }

    return data;
  }

  private getMetricValue(data: any, displayMetric: ChartMetric): number {
    switch (displayMetric) {
      case 'value': // For map activity, value means signatures
        return data.signatures;
      case 'kills': // For map activity, kills means activities
        return data.activities;
      default:
        return data.signatures;
    }
  }

  private getTimeKey(date: Date, groupBy: string): string {
    switch (groupBy) {
      case 'hour':
        return format(date, 'yyyy-MM-dd HH:00');
      case 'day':
        return format(date, 'yyyy-MM-dd');
      case 'week': {
        const weekStart = new Date(date);
        weekStart.setDate(date.getDate() - date.getDay());
        return format(weekStart, 'yyyy-MM-dd');
      }
      default:
        return format(date, 'yyyy-MM-dd');
    }
  }

  private generateTimeLabels(startDate: Date, endDate: Date, groupBy: string): string[] {
    const labels: string[] = [];
    const current = new Date(startDate);

    while (current <= endDate) {
      labels.push(this.formatLabel(current, groupBy));

      switch (groupBy) {
        case 'hour':
          current.setHours(current.getHours() + 1);
          break;
        case 'day':
          current.setDate(current.getDate() + 1);
          break;
        case 'week':
          current.setDate(current.getDate() + 7);
          break;
      }
    }

    return labels;
  }

  private generateEmptyTimeLabels(groupBy: string): string[] {
    const labels = [];
    const today = new Date();
    const daysToGenerate = groupBy === 'hour' ? 1 : groupBy === 'day' ? 7 : 4;

    for (let i = daysToGenerate - 1; i >= 0; i--) {
      const date = new Date(today);
      date.setDate(date.getDate() - i);
      labels.push(this.formatLabel(date, groupBy));
    }

    return labels;
  }

  private formatLabel(date: Date, groupBy: string): string {
    switch (groupBy) {
      case 'hour':
        return format(date, 'HH:mm');
      case 'day':
        return format(date, 'MMM dd');
      case 'week':
        return format(date, 'MMM dd');
      default:
        return format(date, 'MMM dd');
    }
  }

  private periodFromDates(startDate: Date): string {
    const now = new Date();
    const diffMs = now.getTime() - startDate.getTime();
    const diffHours = diffMs / (1000 * 60 * 60);
    const diffDays = diffHours / 24;

    if (diffHours <= 24) return '24h';
    if (diffDays <= 7) return '7d';
    if (diffDays <= 30) return '30d';
    return '90d';
  }
}

/**
 * Utilities for formatting chart values for display
 */
export class FormatUtils {
  /**
   * Format a numeric value with appropriate suffix (K, M, B)
   * @param value The numeric value to format
   * @returns Formatted string with suffix
   */
  static formatValue(value: number): string {
    if (value >= 1_000_000_000) {
      return `${(value / 1_000_000_000).toFixed(1)}B`;
    } else if (value >= 1_000_000) {
      return `${(value / 1_000_000).toFixed(1)}M`;
    } else if (value >= 1_000) {
      return `${(value / 1_000).toFixed(1)}K`;
    } else {
      return value.toString();
    }
  }

  /**
   * Format a BigInt value with appropriate suffix
   * @param value The BigInt value to format
   * @returns Formatted string with suffix
   */
  static formatBigIntValue(value: bigint): string {
    return this.formatValue(Number(value.toString()));
  }

  /**
   * Format an ISK (EVE currency) value with appropriate suffix
   * @param value The ISK amount as BigInt
   * @returns Formatted string with ISK suffix
   */
  static formatIsk(value: bigint): string {
    const n = Number(value);
    if (n >= 1_000_000_000_000) {
      return `${(n / 1_000_000_000_000).toFixed(2)}T`;
    } else if (n >= 1_000_000_000) {
      return `${(n / 1_000_000_000).toFixed(2)}B`;
    } else if (n >= 1_000_000) {
      return `${(n / 1_000_000).toFixed(2)}M`;
    } else if (n >= 1_000) {
      return `${(n / 1_000).toFixed(2)}K`;
    } else {
      return n.toString();
    }
  }
}

import { format } from 'date-fns';

/**
 * Utilities for time-related chart operations
 */
export class TimeUtils {
  /**
   * Get date format string based on group by period
   * @param groupBy The time grouping
   * @returns A date-fns format string
   */
  static getGroupByFormat(groupBy: 'hour' | 'day' | 'week'): string {
    switch (groupBy) {
      case 'hour':
        return 'HH:mm';
      case 'week':
        return 'MMM dd';
      default:
        return 'MMM dd';
    }
  }

  /**
   * Format a time range as a string
   * @param start Start date
   * @param end End date
   * @returns Formatted date range string
   */
  static formatTimeRange(start: Date, end: Date): string {
    return `${format(start, 'yyyy-MM-dd')} to ${format(end, 'yyyy-MM-dd')}`;
  }

  /**
   * Create time buckets for a date range
   * @param startDate Start date
   * @param endDate End date
   * @param groupBy Time grouping
   * @returns Array of date objects with formatted labels
   */
  static createTimeBuckets(
    startDate: Date,
    endDate: Date,
    groupBy: 'hour' | 'day' | 'week' = 'day'
  ): Array<{ date: Date; label: string }> {
    const result: Array<{ date: Date; label: string }> = [];
    const formatStr = this.getGroupByFormat(groupBy);
    let current = new Date(startDate);

    // Determine increment based on groupBy
    const getNextDate = (date: Date): Date => {
      const next = new Date(date);
      switch (groupBy) {
        case 'hour':
          next.setHours(next.getHours() + 1);
          break;
        case 'week':
          next.setDate(next.getDate() + 7);
          break;
        case 'day':
        default:
          next.setDate(next.getDate() + 1);
          break;
      }
      return next;
    };

    // Generate all time buckets in the range
    while (current <= endDate) {
      result.push({
        date: new Date(current),
        label: format(current, formatStr),
      });
      current = getNextDate(current);
    }

    return result;
  }
}

import { ESIService } from '../ESIService';
import { retryOperation } from '../../utils/retry';
import { logger } from '../../lib/logger';
import { Character } from '../../domain/character/Character';
import { CharacterRepository } from '../../infrastructure/repositories/CharacterRepository';
import { MapClient } from '../../infrastructure/http/MapClient';
import { PrismaClient } from '@prisma/client';
import prisma from '../../infrastructure/persistence/client';
import { Configuration } from '../../config';

export class CharacterSyncService {
  private readonly characterRepository: CharacterRepository;
  private readonly esiService: ESIService;
  private readonly map: MapClient;
  private readonly maxRetries: number;
  private readonly retryDelay: number;
  private readonly prisma: PrismaClient;

  constructor(mapApiUrl: string, mapApiKey: string, maxRetries: number = 3, retryDelay: number = 5000) {
    this.characterRepository = new CharacterRepository(prisma);
    this.esiService = new ESIService();
    this.map = new MapClient(mapApiUrl, mapApiKey);
    this.maxRetries = maxRetries;
    this.retryDelay = retryDelay;
    this.prisma = prisma;
  }

  /**
   * Start the character sync service
   */
  public async start(): Promise<void> {
    logger.info('Starting character sync service...');

    const mapName = Configuration.apis.map.name;
    if (!mapName) {
      logger.warn('MAP_NAME environment variable not set, skipping character sync');
      return;
    }

    try {
      // Fetch map data once and use it for both operations
      const mapData = await retryOperation(
        () => this.map.getUserCharacters(mapName),
        `Fetching character data for map ${mapName}`,
        {
          maxRetries: this.maxRetries,
          initialRetryDelay: this.retryDelay,
          timeout: 30000,
        }
      );

      if (!mapData?.data || !Array.isArray(mapData.data)) {
        logger.warn(`No valid character data available for map ${mapName}`);
        return;
      }

      const characterSyncResults = await this.syncUserCharacters(mapData);
      const groupResults = await this.createCharacterGroups(mapData, mapName);

      logger.info(
        `Character sync service started successfully - Characters: ${characterSyncResults.total} total (${characterSyncResults.synced} synced, ${characterSyncResults.skipped} skipped, ${characterSyncResults.errors} errors), Groups: ${groupResults.total} total (${groupResults.created} created, ${groupResults.updated} updated)`
      );
    } catch (error) {
      logger.error(`Error during character sync: ${error}`);
      throw error;
    }
  }

  public async syncUserCharacters(mapData: any): Promise<{
    total: number;
    synced: number;
    skipped: number;
    errors: number;
  }> {
    try {
      if (!mapData?.data || !Array.isArray(mapData.data)) {
        logger.warn(`No valid character data available`);
        return { total: 0, synced: 0, skipped: 0, errors: 0 };
      }

      // Extract unique characters from the map data
      const uniqueCharacters = new Map();
      for (const user of mapData.data) {
        for (const character of user.characters) {
          if (character.eve_id) {
            uniqueCharacters.set(character.eve_id, character);
          }
        }
      }

      // Track sync statistics
      let syncedCount = 0;
      let errorCount = 0;
      let skippedCount = 0;

      // Sync each character
      for (const [eveId, characterData] of uniqueCharacters) {
        try {
          const result = await this.syncCharacter(eveId.toString(), characterData);
          if (result === 'synced') {
            syncedCount++;
          } else if (result === 'skipped') {
            skippedCount++;
          }
        } catch (error) {
          errorCount++;
          logger.error(`Error syncing character ${eveId}: ${error}`);
        }
      }

      return {
        total: uniqueCharacters.size,
        synced: syncedCount,
        skipped: skippedCount,
        errors: errorCount,
      };
    } catch (error: any) {
      logger.error(`Error syncing user characters: ${error.message}`);
      throw error;
    }
  }

  /**
   * Create character groups based on users from Map API after character sync
   */
  private async createCharacterGroups(
    mapData: any,
    mapName: string
  ): Promise<{
    total: number;
    created: number;
    updated: number;
  }> {
    try {
      if (!mapData?.data || !Array.isArray(mapData.data)) {
        logger.warn(`No valid user data available for map ${mapName}`);
        return { total: 0, created: 0, updated: 0 };
      }

      let createdCount = 0;
      let updatedCount = 0;

      // Process each user group
      for (let i = 0; i < mapData.data.length; i++) {
        const user = mapData.data[i];
        const userCharacters = user.characters ?? [];

        if (userCharacters.length === 0) {
          continue;
        }

        // Extract character IDs for this group
        const characterIds = userCharacters.map((c: any) => BigInt(c.eve_id));

        // Check if we already have a group containing any of these characters
        const existingGroup = await this.prisma.characterGroup.findFirst({
          where: {
            characters: {
              some: {
                eveId: { in: characterIds },
              },
            },
          },
          include: {
            characters: true,
          },
        });

        let groupId: string;
        let groupName: string;

        if (existingGroup) {
          // Use existing group
          groupId = existingGroup.id;
          groupName = existingGroup.map_name;

          // Check if we need to update the main character
          // Use the main_character_eve_id from API, or fall back to first character if null
          const intendedMainCharId = user.main_character_eve_id
            ? BigInt(user.main_character_eve_id)
            : BigInt(userCharacters[0].eve_id);

          if (existingGroup.mainCharacterId !== intendedMainCharId) {
            await this.prisma.characterGroup.update({
              where: { id: groupId },
              data: { mainCharacterId: intendedMainCharId },
            });
            updatedCount++;
          }
        } else {
          // Create new group - use a stable identifier based on user index and main character
          const mainCharacterId = user.main_character_eve_id
            ? BigInt(user.main_character_eve_id)
            : BigInt(userCharacters[0].eve_id);

          groupName = `user-${i}-${mainCharacterId}`;

          const group = await this.prisma.characterGroup.create({
            data: {
              map_name: mapName, // Use the MAP_NAME environment variable
              mainCharacterId: mainCharacterId,
            },
          });
          groupId = group.id;
          createdCount++;
        }

        // Ensure all characters in this user are assigned to this group
        for (const character of userCharacters) {
          try {
            await this.prisma.character.updateMany({
              where: { eveId: BigInt(character.eve_id) },
              data: { characterGroupId: groupId },
            });
          } catch (error) {
            logger.warn(`Could not assign character ${character.eve_id} to group ${groupName}: ${error}`);
          }
        }
      }

      return {
        total: mapData.data.length,
        created: createdCount,
        updated: updatedCount,
      };
    } catch (error) {
      logger.error('Error creating character groups from Map API users:', error);
      throw error;
    }
  }

  private async syncCharacter(eveId: string, mapCharacterData: any): Promise<'synced' | 'skipped'> {
    try {
      // Check if character already exists
      const existingCharacter = await this.characterRepository.getCharacter(BigInt(eveId));

      let characterName: string;
      if (existingCharacter) {
        // Use existing name for updates
        characterName = existingCharacter.name;
      } else {
        // Get character name from ESI only for new characters
        const esiData = await retryOperation(
          () => this.esiService.getCharacter(parseInt(eveId)),
          `Fetching character name for ${eveId}`,
          {
            maxRetries: this.maxRetries,
            initialRetryDelay: this.retryDelay,
            timeout: 30000,
          }
        );

        if (!esiData) {
          logger.warn(`No ESI data available for character ${eveId}`);
          return 'skipped';
        }
        characterName = esiData.name;
      }

      // Create character instance using domain entity with data from both sources
      const character = new Character({
        eveId,
        name: characterName,
        corporationId: mapCharacterData.corporation_id,
        corporationTicker: mapCharacterData.corporation_ticker ?? '',
        allianceId: mapCharacterData.alliance_id,
        allianceTicker: mapCharacterData.alliance_ticker ?? '',
        createdAt: existingCharacter?.createdAt ?? new Date(),
        updatedAt: new Date(),
      });

      // Save character using repository
      await this.characterRepository.upsertCharacter({
        eveId: BigInt(character.eveId),
        name: character.name,
        corporationId: character.corporationId,
        corporationTicker: character.corporationTicker,
        allianceId: character.allianceId,
        allianceTicker: character.allianceTicker,
        characterGroupId: character.characterGroupId,
      });
      return 'synced';
    } catch (error: any) {
      logger.error(`Error syncing character ${eveId}: ${error.message}`);
      throw error;
    }
  }

  public async close(): Promise<void> {
    await this.prisma.$disconnect();
  }
}

import { MapClient } from '../../infrastructure/http/MapClient';
import { CacheRedisAdapter } from '../../cache/CacheRedisAdapter';
import { retryOperation } from '../../utils/retry';
import { logger } from '../../lib/logger';
import { MapActivityResponseSchema } from '../../types/ingestion';
import { MapActivityRepository } from '../../infrastructure/repositories/MapActivityRepository';
import { MapActivity } from '../../domain/activity/MapActivity';
import { Configuration } from '../../config';

export class MapActivityService {
  private readonly map: MapClient;
  private readonly cache: CacheRedisAdapter;
  private readonly mapActivityRepository: MapActivityRepository;
  private readonly maxRetries: number;
  private readonly retryDelay: number;

  constructor(
    mapApiUrl: string,
    mapApiKey: string,
    redisUrl: string = 'redis://localhost:6379',
    cacheTtl: number = 300,
    maxRetries: number = 3,
    retryDelay: number = 5000
  ) {
    this.map = new MapClient(mapApiUrl, mapApiKey);
    this.cache = new CacheRedisAdapter(redisUrl, cacheTtl);
    this.mapActivityRepository = new MapActivityRepository();
    this.maxRetries = maxRetries;
    this.retryDelay = retryDelay;
  }

  /**
   * Start the map activity service
   */
  public async start(): Promise<void> {
    logger.info('Starting map activity service...');

    // Get map name from centralized configuration
    const mapName = Configuration.apis.map.name;
    if (!mapName) {
      logger.warn('MAP_NAME environment variable not set, skipping map activity ingestion');
      return;
    }

    try {
      // Fetch map activity data for the entire map (not per character)
      await this.ingestMapActivity(mapName, 7); // Last 7 days
      logger.info('Map activity service started successfully');
    } catch (error) {
      logger.error(`Error ingesting map activity for map ${mapName}:`, error);
    }
  }

  public async ingestMapActivity(slug: string, days = 7): Promise<void> {
    try {
      // Get map data with retry
      const mapData = await retryOperation(
        async () => {
          const result = await this.map.getCharacterActivity(slug, days);
          if (!result) {
            throw new Error('No data returned from Map API');
          }
          return result;
        },
        `Fetching map data for ${slug}`,
        {
          maxRetries: this.maxRetries,
          initialRetryDelay: this.retryDelay,
          timeout: 30000,
        }
      );

      if (!mapData?.data || !Array.isArray(mapData.data)) {
        logger.warn(`No valid map data available for ${slug}`);
        return;
      }

      // Cache the map data
      await this.cache.set(`map:${slug}`, mapData);

      // Process and store the data
      await this.processMapData(mapData);
    } catch (error: any) {
      logger.error(`Error ingesting map activity for ${slug}: ${error.message}`);
      throw error;
    }
  }

  public async syncRecentMapActivity(slug: string): Promise<void> {
    try {
      const cachedData = await this.cache.get<{ data: any[] }>(`map:${slug}`);
      if (!cachedData) {
        logger.warn(`No cached map data for ${slug}`);
        return;
      }

      await this.processMapData(cachedData);
    } catch (error: any) {
      logger.error(`Error syncing recent map activity for ${slug}: ${error.message}`);
      throw error;
    }
  }

  public async refreshMapActivityData(slug: string, days = 7): Promise<void> {
    try {
      // Clear cache
      await this.cache.delete(`map:${slug}`);

      // Re-ingest data
      await this.ingestMapActivity(slug, days);
    } catch (error: any) {
      logger.error(`Error refreshing map activity data for ${slug}: ${error.message}`);
      throw error;
    }
  }

  private async processMapData(mapData: any): Promise<void> {
    try {
      // Validate the data structure
      const validatedData = MapActivityResponseSchema.parse(mapData);

      if (!validatedData.data || !Array.isArray(validatedData.data)) {
        logger.warn('Invalid map data format');
        return;
      }

      // Process each activity record
      for (const activity of validatedData.data) {
        const mapActivity = new MapActivity({
          characterId: BigInt(activity.character.eve_id),
          timestamp: new Date(activity.timestamp),
          signatures: activity.signatures || 0,
          connections: activity.connections || 0,
          passages: activity.passages || 0,
          allianceId: activity.character.alliance_id ?? null,
          corporationId: activity.character.corporation_id ?? null,
        });

        await this.upsertMapActivity(
          mapActivity.characterId,
          mapActivity.timestamp,
          mapActivity.signatures,
          mapActivity.connections,
          mapActivity.passages,
          mapActivity.allianceId,
          mapActivity.corporationId
        );
      }

      logger.info(`Processed ${validatedData.data.length} map activity records`);
    } catch (error: any) {
      logger.error(`Error processing map data: ${error.message}`);
      throw error;
    }
  }

  public async close(): Promise<void> {
    await this.cache.close();
  }

  async upsertMapActivity(
    characterId: bigint,
    timestamp: Date,
    signatures: number,
    connections: number,
    passages: number,
    allianceId: number | null,
    corporationId: number | null
  ): Promise<void> {
    await this.mapActivityRepository.upsertMapActivity(
      characterId,
      timestamp,
      signatures,
      connections,
      passages,
      allianceId,
      corporationId
    );
  }
}

import { WebSocketKillmail, WebSocketVictim, WebSocketAttacker } from '../../types/websocket';
import { KillFact } from '@prisma/client';

interface MappedKillData {
  killFact: Omit<KillFact, 'killmail_id'> & { killmail_id: bigint };
  victim: {
    character_id?: bigint;
    corporation_id?: bigint;
    alliance_id?: bigint;
    ship_type_id: number;
    damage_taken: number;
  };
  attackers: Array<{
    character_id?: bigint;
    corporation_id?: bigint;
    alliance_id?: bigint;
    damage_done: number;
    final_blow: boolean;
    security_status?: number;
    ship_type_id?: number;
    weapon_type_id?: number;
  }>;
  involvedCharacters: Array<{
    character_id: bigint;
    role: 'attacker' | 'victim';
  }>;
}

export class WebSocketDataMapper {
  /**
   * Maps a WebSocket killmail to database entities
   */
  mapKillmail(killmail: WebSocketKillmail): MappedKillData {
    // Map the main kill fact
    const killFact: MappedKillData['killFact'] = {
      killmail_id: BigInt(killmail.killmail_id),
      kill_time: new Date(killmail.kill_time),
      npc: killmail.zkb.npc,
      solo: killmail.zkb.solo,
      awox: killmail.zkb.awox,
      ship_type_id: killmail.victim.ship_type_id,
      system_id: killmail.system_id,
      labels: killmail.zkb.labels ?? [],
      total_value: BigInt(Math.floor(killmail.zkb.total_value)),
      points: killmail.zkb.points,
    };

    // Map victim data
    const victim = this.mapVictim(killmail.victim);

    // Map attackers
    const attackers = killmail.attackers.map(attacker => this.mapAttacker(attacker));

    // Collect all involved characters
    const involvedCharacters = this.collectInvolvedCharacters(killmail);

    return {
      killFact,
      victim,
      attackers,
      involvedCharacters,
    };
  }

  private mapVictim(victim: WebSocketVictim): MappedKillData['victim'] {
    return {
      character_id: victim.character_id ? BigInt(victim.character_id) : undefined,
      corporation_id: victim.corporation_id ? BigInt(victim.corporation_id) : undefined,
      alliance_id: victim.alliance_id ? BigInt(victim.alliance_id) : undefined,
      ship_type_id: victim.ship_type_id,
      damage_taken: victim.damage_taken,
    };
  }

  private mapAttacker(attacker: WebSocketAttacker): MappedKillData['attackers'][0] {
    return {
      character_id: attacker.character_id ? BigInt(attacker.character_id) : undefined,
      corporation_id: attacker.corporation_id ? BigInt(attacker.corporation_id) : undefined,
      alliance_id: attacker.alliance_id ? BigInt(attacker.alliance_id) : undefined,
      damage_done: attacker.damage_done,
      final_blow: attacker.final_blow,
      security_status: attacker.security_status,
      ship_type_id: attacker.ship_type_id,
      weapon_type_id: attacker.weapon_type_id,
    };
  }

  private collectInvolvedCharacters(killmail: WebSocketKillmail): MappedKillData['involvedCharacters'] {
    const characters: MappedKillData['involvedCharacters'] = [];

    // Add victim if it's a character
    if (killmail.victim.character_id) {
      characters.push({
        character_id: BigInt(killmail.victim.character_id),
        role: 'victim',
      });
    }

    // Add all attackers that are characters
    for (const attacker of killmail.attackers) {
      if (attacker.character_id) {
        characters.push({
          character_id: BigInt(attacker.character_id),
          role: 'attacker',
        });
      }
    }

    return characters;
  }

  /**
   * Extracts loss data for a specific character from a killmail
   */
  extractLossForCharacter(
    killmail: WebSocketKillmail,
    characterId: bigint
  ): {
    killmail_id: bigint;
    character_id: bigint;
    kill_time: Date;
    ship_type_id: number;
    system_id: number;
    total_value: bigint;
    attacker_count: number;
    labels: string[];
  } | null {
    // Check if this character is the victim
    if (killmail.victim.character_id && BigInt(killmail.victim.character_id) === characterId) {
      return {
        killmail_id: BigInt(killmail.killmail_id),
        character_id: characterId,
        kill_time: new Date(killmail.kill_time),
        ship_type_id: killmail.victim.ship_type_id,
        system_id: killmail.system_id,
        total_value: BigInt(Math.floor(killmail.zkb.total_value)),
        attacker_count: killmail.attackers.length,
        labels: killmail.zkb.labels ?? [],
      };
    }

    return null;
  }

  /**
   * Maps WebSocket position data to database format
   */
  mapPosition(position?: WebSocketKillmail['position']): any | null {
    if (!position) {
      return null;
    }

    return {
      x: position.x,
      y: position.y,
      z: position.z,
    };
  }

  /**
   * Maps item data from victim (if needed in the future)
   */
  mapItems(victim: WebSocketVictim): any[] | null {
    if (!victim.items || victim.items.length === 0) {
      return null;
    }

    return victim.items.map(item => ({
      type_id: item.type_id,
      type_name: item.type_name,
      singleton: item.singleton,
      flag: item.flag,
      quantity_dropped: item.quantity_dropped,
      quantity_destroyed: item.quantity_destroyed,
    }));
  }
}

/*
 * WebSocketIngestionService
 * Converted from the browsercentric `phoenix` client to the Nodefirst
 * `phoenix-websocket` implementation.
 *
 * **Key changes**
 * 1. Uses `phoenix-websocket` (async/await API, Node friendly) instead of
 *    the browser Phoenix client.
 * 2. Only a single global WebSocket polyfill is required (`ws`). No more
 *    `location`, `document`, etc.
 * 3. Channels are addressed by topic string  there is no separate
 *    `Channel` object.  We keep a constant `TOPIC` ("killmails:lobby") and
 *    use `subscribeToTopic` / `sendMessage` for all interactions.
 * 4. Builtin reconnect logic in `phoenix-websocket` replaces the custom
 *    retry loop.  We still track connected state so existing metrics work.
 * 5. Event handlers are supplied when subscribing.
 *
 * Library docs: https://inkarnaterpg.github.io/phoenix-websocket/ ([npmjs.com](https://www.npmjs.com/package/phoenix-websocket))
 */

// Expose a Node WebSocket implementation for phoenixwebsocket.
import { WebSocket } from 'ws';
(global as any).WebSocket ??= WebSocket; // Required in a pureNode context ([npmjs.com](https://www.npmjs.com/package/phoenix-websocket))

import { PhoenixWebsocket } from 'phoenix-websocket';

import { logger } from '../../lib/logger';
import { CharacterRepository } from '../../infrastructure/repositories/CharacterRepository';
import { KillRepository } from '../../infrastructure/repositories/KillRepository';
import { WebSocketKillmail, WebSocketKillmailUpdate } from '../../types/websocket';
import { WebSocketDataMapper } from './WebSocketDataMapper';
import { PrismaClient } from '@prisma/client';

interface WebSocketPreloadConfig {
  enabled: boolean;
  limitPerSystem: number;
  sinceHours: number;
  deliveryBatchSize: number;
  deliveryIntervalMs: number;
}

interface WebSocketConfig {
  url: string;
  reconnectIntervalMs?: number; // kept for backwardscompat, but automatic reconnect is now handled by the lib
  maxReconnectAttempts?: number;
  timeout?: number;
  preload?: WebSocketPreloadConfig;
}

export class WebSocketIngestionService {
  /** PhoenixWebSocket client instance */
  private socket: PhoenixWebsocket | null = null;
  /** Constant topic we work with */
  private readonly TOPIC = 'killmails:lobby';

  private characterRepository: CharacterRepository;
  private killRepository: KillRepository;
  private dataMapper: WebSocketDataMapper;
  private config: WebSocketConfig;

  private isRunning = false;
  private connected = false;

  private subscribedCharacters: Set<number> = new Set();
  private subscribedSystems: Set<number> = new Set();

  constructor(config: WebSocketConfig, prisma: PrismaClient) {
    this.config = {
      reconnectIntervalMs: 5_000,
      maxReconnectAttempts: 10,
      timeout: 10_000,
      ...config,
    };

    this.characterRepository = new CharacterRepository(prisma);
    this.killRepository = new KillRepository(prisma);
    this.dataMapper = new WebSocketDataMapper();
  }

  /* ------------------------------------------------------------------
   * Lifecycle
   * ---------------------------------------------------------------- */

  async start(): Promise<void> {
    if (this.isRunning) {
      logger.warn('WebSocket ingestion service is already running');
      return;
    }

    this.isRunning = true;
    logger.info('Starting WebSocket ingestion service');

    try {
      await this.connect();
      await this.subscribeToTrackedCharacters();
    } catch (error) {
      logger.error('Failed to start WebSocket ingestion service', {
        error,
        url: this.config.url,
        message: error instanceof Error ? error.message : String(error),
        stack: error instanceof Error ? error.stack : undefined,
      });
      this.isRunning = false;
      throw error;
    }
  }

  async stop(): Promise<void> {
    if (!this.isRunning) return;

    logger.info('Stopping WebSocket ingestion service');
    this.isRunning = false;

    if (this.socket) {
      try {
        await this.socket.unsubscribeToTopic(this.TOPIC);
      } catch (err) {
        logger.debug('Topic unsubscribe threw (ignoring)', err);
      }
      this.socket.disconnect();
      this.socket = null;
    }

    this.connected = false;
    this.subscribedCharacters.clear();
    this.subscribedSystems.clear();
  }

  /* ------------------------------------------------------------------
   * Connection & topic join
   * ---------------------------------------------------------------- */

  private async connect(): Promise<void> {
    logger.info(`Connecting to Phoenix WebSocket at ${this.config.url}`);

    this.socket = new PhoenixWebsocket(this.config.url, { client_identifier: 'eve-chart-bot' }, this.config.timeout);

    // Track connection status for metrics.
    this.socket.onConnectedCallback = () => {
      this.connected = true;
      logger.info('WebSocket connected');
    };

    this.socket.onDisconnectedCallback = () => {
      this.connected = false;
      logger.warn('WebSocket disconnected  library will retry automatically');
    };

    // Establish the lowlevel connection.
    await this.socket.connect();

    // Join the lobby topic & attach broadcast handlers.
    await this.joinLobby();
  }

  private async joinLobby(): Promise<void> {
    if (!this.socket) throw new Error('Socket not initialised');

    const preload = this.getPreloadConfig();
    const params = preload ? { preload } : undefined;

    logger.info('Subscribing to lobby topic', { params });

    await this.socket.subscribeToTopic(
      this.TOPIC,
      params,
      {
        killmail_update: async (data?: { [key: string]: any }) => {
          if (!data) return;
          const payload = data as WebSocketKillmailUpdate;
          try {
            logger.info(`Received killmail update for system ${payload.system_id}`, {
              killmailCount: payload.killmails.length,
              preload: payload.preload,
              timestamp: payload.timestamp,
            });

            // Log each individual killmail
            payload.killmails.forEach((killmail, index) => {
              logger.info(`Killmail ${index + 1}/${payload.killmails.length}:`, {
                killmailId: killmail.killmail_id,
                systemId: killmail.system_id,
                killTime: killmail.kill_time,
                victimName: killmail.victim?.character_name ?? 'Unknown',
                victimShip: killmail.victim?.ship_name ?? 'Unknown',
                attackerCount: killmail.attackers?.length ?? 0,
                finalBlowBy: killmail.attackers?.find(a => a.final_blow)?.character_name ?? 'Unknown',
                totalValue: killmail.zkb?.total_value
                  ? `${(killmail.zkb.total_value / 1000000).toFixed(2)}M ISK`
                  : 'Unknown',
              });
            });

            await this.processKillmails(payload.killmails, payload.preload ?? false);
          } catch (err) {
            logger.error('Failed to process killmail update', err);
          }
        },
        kill_count_update: (data?: { [key: string]: any }) => {
          if (!data) return;
          logger.debug(`Kill count update for system ${data.system_id}: ${data.count} kills`);
        },
      },
      // Reconnect handler  ensure character/system subscriptions are replayed
      async (reconnectPromise: Promise<void>) => {
        try {
          await reconnectPromise;
          logger.info('Rejoined lobby after reconnect  refreshing subscriptions');
          await this.subscribeToTrackedCharacters();
          if (this.subscribedSystems.size) {
            await this.subscribeToSystems(Array.from(this.subscribedSystems));
          }
        } catch (error) {
          logger.error('Failed to rejoin lobby', error);
        }
      }
    );

    logger.info('Joined lobby topic successfully');
  }

  /* ------------------------------------------------------------------
   * Subscription helpers
   * ---------------------------------------------------------------- */

  private async subscribeToTrackedCharacters(): Promise<void> {
    if (!this.socket) throw new Error('Socket not initialized');

    const characters = await this.characterRepository.getAllCharacters();
    const characterIds = characters.map(c => Number(c.eveId));

    if (characterIds.length === 0) {
      logger.warn('No tracked characters found');
      return;
    }

    const preload = this.getPreloadConfig();
    const payload: any = { character_ids: characterIds };
    if (preload) payload.preload = preload;

    logger.info(`Subscribing to ${characterIds.length} characters...`, {
      sampleIds: characterIds.slice(0, 5),
      preloadEnabled: !!preload,
    });

    try {
      const startTime = Date.now();
      const response = await this.socket.sendMessage(this.TOPIC, 'subscribe_characters', payload);
      const duration = Date.now() - startTime;

      characterIds.forEach(id => this.subscribedCharacters.add(id));
      logger.info(
        `Successfully subscribed to ${characterIds.length} characters in ${duration}ms${preload ? ' with preload' : ''}`,
        {
          response,
          characterIds: characterIds.slice(0, 10), // Log first 10 for debugging
          totalCount: characterIds.length,
          duration,
        }
      );
    } catch (error) {
      logger.error('Failed to subscribe to characters', {
        error,
        characterCount: characterIds.length,
        errorMessage: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  }

  async updateCharacterSubscriptions(addCharacterIds: number[] = [], removeCharacterIds: number[] = []): Promise<void> {
    if (!this.socket) {
      logger.warn('Socket not initialised  cannot update subscriptions');
      return;
    }

    const preload = this.getPreloadConfig();

    if (addCharacterIds.length) {
      logger.info(`Adding subscription for ${addCharacterIds.length} characters...`);
      const payload: any = { character_ids: addCharacterIds };
      if (preload) payload.preload = preload;
      await this.socket
        .sendMessage(this.TOPIC, 'subscribe_characters', payload)
        .then(() => {
          addCharacterIds.forEach(id => this.subscribedCharacters.add(id));
          logger.info(
            `Successfully added subscription for ${addCharacterIds.length} characters${preload ? ' with preload' : ''}`,
            { totalSubscribed: this.subscribedCharacters.size }
          );
        })
        .catch((error: any) => {
          logger.error('Failed to add character subscriptions', error);
          throw error;
        });
    }

    if (removeCharacterIds.length) {
      await this.socket
        .sendMessage(this.TOPIC, 'unsubscribe_characters', {
          character_ids: removeCharacterIds,
        })
        .then(() => {
          removeCharacterIds.forEach(id => this.subscribedCharacters.delete(id));
          logger.info(`Removed subscription for ${removeCharacterIds.length} characters`);
        })
        .catch((error: any) => {
          logger.error('Failed to remove character subscriptions', error);
          throw error;
        });
    }
  }

  async subscribeToSystems(systemIds: number[]): Promise<void> {
    if (!this.socket || systemIds.length === 0) return;

    const preload = this.getPreloadConfig();
    const payload: any = { systems: systemIds };
    if (preload) payload.preload = preload;

    logger.info(`Subscribing to ${systemIds.length} systems...`);

    try {
      await this.socket.sendMessage(this.TOPIC, 'subscribe_systems', payload);
      systemIds.forEach(id => this.subscribedSystems.add(id));
      logger.info(`Successfully subscribed to ${systemIds.length} systems${preload ? ' with preload' : ''}`, {
        systemIds: systemIds.slice(0, 10), // Log first 10 for debugging
        totalCount: systemIds.length,
      });
    } catch (error) {
      logger.error('Failed to subscribe to systems', error);
      throw error;
    }
  }

  /* ------------------------------------------------------------------
   * Killmail processing
   * ---------------------------------------------------------------- */

  private async processKillmails(killmails: WebSocketKillmail[], isPreload: boolean): Promise<void> {
    for (const killmail of killmails) {
      try {
        const involvedCharacters = this.getInvolvedCharacters(killmail);
        const trackedCharacters = involvedCharacters.filter(id => this.subscribedCharacters.has(id));

        if (trackedCharacters.length === 0 && !this.subscribedSystems.has(killmail.system_id)) {
          continue; // Skip if not relevant
        }

        const killData = this.dataMapper.mapKillmail(killmail);
        await this.killRepository.ingestKillmail(
          killData.killFact,
          killData.victim,
          killData.attackers,
          killData.involvedCharacters
        );

        logger.info(`Ingested killmail ${killmail.killmail_id}`, {
          system: killmail.system_id,
          isPreload,
          trackedCharacters,
        });
      } catch (error) {
        logger.error(`Failed to process killmail ${killmail.killmail_id}`, error);
      }
    }
  }

  private getInvolvedCharacters(killmail: WebSocketKillmail): number[] {
    const characters: number[] = [];

    if (killmail.victim?.character_id) {
      characters.push(killmail.victim.character_id);
    }

    if (killmail.attackers) {
      for (const attacker of killmail.attackers) {
        if (attacker.character_id) {
          characters.push(attacker.character_id);
        }
      }
    }

    return characters;
  }

  /* ------------------------------------------------------------------
   * Utility helpers
   * ---------------------------------------------------------------- */

  private getPreloadConfig() {
    if (!this.config.preload?.enabled) return undefined;

    return {
      enabled: true,
      limit_per_system: this.config.preload.limitPerSystem,
      since_hours: this.config.preload.sinceHours,
      delivery_batch_size: this.config.preload.deliveryBatchSize,
      delivery_interval_ms: this.config.preload.deliveryIntervalMs,
    };
  }

  getStatus() {
    return {
      isRunning: this.isRunning,
      isConnected: this.connected,
      subscribedCharacters: this.subscribedCharacters.size,
      subscribedSystems: this.subscribedSystems.size,
    };
  }
}

/**
 * Types related to Discord interactions
 */

/**
 * Basic character summary for simplified display
 */
export interface CharacterSummary {
  eveId: string;
  name: string;
}

/**
 * Character group summary for discord commands
 */
export interface CharacterGroupSummary {
  groupId: string;
  name: string;
  characters: CharacterSummary[];
}

/**
 * Interface for chart command options
 */
export interface ChartCommandOptions {
  type: string;
  period: string;
  group?: string;
  character?: string;
  metric?: string;
  displayType?: string;
}

/**
 * Response structure for Discord commands
 */
export interface CommandResponse {
  content?: string;
  imageUrl?: string;
  embed?: any;
  error?: string;
}

import { z } from 'zod';

// API Response Types
export const ZkillResponseSchema = z.object({
  killmail_id: z.number(),
  zkb: z.object({
    locationID: z.number().optional(),
    hash: z.string(),
    fittedValue: z.number().optional().default(0),
    droppedValue: z.number().optional().default(0),
    destroyedValue: z.number().optional().default(0),
    totalValue: z.number(),
    points: z.number().optional().default(0),
    npc: z.boolean().optional().default(false),
    solo: z.boolean().optional().default(false),
    awox: z.boolean().optional().default(false),
    labels: z.array(z.string()).optional().default([]),
  }),
});

