import { PrismaClient } from '@prisma/client';
import { logger } from '../lib/logger';

/**
 * Utility class for safely managing character groups
 * to prevent empty groups from being created
 */
export class CharacterGroupUtils {
  /**
   * Create a character group only if we have characters to associate with it
   * @param prisma Prisma client instance
   * @param mapName The group map name
   * @param characterIds Array of character IDs to associate with the group
   * @param mainCharacterId Optional main character ID for the group
   * @returns The created group ID or null if no group was created
   */
  static async createCharacterGroupSafely(
    prisma: PrismaClient,
    mapName: string,
    characterIds: string[],
    mainCharacterId?: string
  ): Promise<string | null> {
    try {
      // Validate inputs
      if (!mapName) {
        logger.error('Cannot create group: missing map name');
        return null;
      }

      if (!characterIds || characterIds.length === 0) {
        logger.warn(`Cannot create group '${mapName}': no characters provided`);
        return null;
      }

      // Convert character IDs to BigInt for database queries
      const characterIdsBigInt = characterIds.map(id => BigInt(id));

      // First, check if any of these characters already belong to a group
      const existingCharacters = await prisma.character.findMany({
        where: {
          eveId: {
            in: characterIdsBigInt,
          },
          characterGroupId: {
            not: null,
          },
        },
        include: {
          characterGroup: true,
        },
      });

      // If some characters already belong to a group, use that group instead of creating a new one
      if (existingCharacters.length > 0) {
        // Use the first character's group as the target group for all characters
        const existingGroup = existingCharacters[0].characterGroup;
        if (!existingGroup) {
          logger.warn('Character has null characterGroup');
          return null;
        }
        logger.info(
          `Found existing group ${existingGroup.id} (${existingGroup.map_name}) for ${existingCharacters.length} characters`
        );

        // Update all provided characters to use this group
        await Promise.all(
          characterIdsBigInt.map(charId =>
            prisma.character.updateMany({
              where: { eveId: charId },
              data: { characterGroupId: existingGroup.id },
            })
          )
        );

        // Update main character if provided
        if (mainCharacterId) {
          await prisma.characterGroup.update({
            where: { id: existingGroup.id },
            data: { mainCharacterId: BigInt(mainCharacterId) },
          });
        }

        return existingGroup.id;
      }

      // Check if characters exist in the database
      const charactersInDb = await prisma.character.findMany({
        where: {
          eveId: {
            in: characterIdsBigInt,
          },
        },
      });

      if (charactersInDb.length === 0) {
        logger.warn(`Cannot create group '${mapName}': no valid characters found`);
        return null;
      }

      logger.info(`Creating character group '${mapName}' with ${charactersInDb.length} characters`);

      // Use a transaction to ensure atomicity
      const result = await prisma.$transaction(async tx => {
        // Create the group first
        const group = await tx.characterGroup.create({
          data: {
            map_name: mapName,
            mainCharacterId: mainCharacterId ? BigInt(mainCharacterId) : null,
          },
        });

        // Associate characters with the group
        await Promise.all(
          charactersInDb.map(char =>
            tx.character.update({
              where: { eveId: char.eveId },
              data: { characterGroupId: group.id },
            })
          )
        );

        return group;
      });

      logger.info(`Successfully created character group ${result.id} with ${charactersInDb.length} characters`);
      return result.id;
    } catch (error) {
      logger.error(
        {
          error: error instanceof Error ? error.message : String(error),
          stack: error instanceof Error ? error.stack : undefined,
        },
        `Failed to create character group '${mapName}'`
      );
      return null;
    }
  }

  /**
   * A safer version of the upsert operation that ensures groups always have characters
   * @param prisma Prisma client instance
   * @param mapName The group map name
   * @param characterIds Array of character IDs to associate with the group
   * @param mainCharacterId Optional main character ID for the group
   * @returns The created/updated group ID or null if no group was created/updated
   */
  static async upsertCharacterGroupSafely(
    prisma: PrismaClient,
    mapName: string,
    characterIds: string[],
    mainCharacterId?: string
  ): Promise<string | null> {
    try {
      // First, check if any of these characters already belong to a group
      if (characterIds && characterIds.length > 0) {
        // Convert character IDs to BigInt for database queries
        const characterIdsBigInt = characterIds.map(id => BigInt(id));

        const existingCharacters = await prisma.character.findMany({
          where: {
            eveId: {
              in: characterIdsBigInt,
            },
            characterGroupId: {
              not: null,
            },
          },
          include: {
            characterGroup: true,
          },
        });

        // If some characters already belong to a group, use that group instead of creating a new one
        if (existingCharacters.length > 0) {
          // Use the first character's group as the target group for all characters
          const existingGroup = existingCharacters[0].characterGroup;
          if (!existingGroup) {
            logger.warn('Character has null characterGroup');
            return null;
          }
          logger.info(
            `Found existing group ${existingGroup.id} (${existingGroup.map_name}) for ${existingCharacters.length} characters`
          );

          // Update all provided characters to use this group
          await Promise.all(
            characterIdsBigInt.map(charId =>
              prisma.character
                .update({
                  where: { eveId: charId },
                  data: { characterGroupId: existingGroup.id },
                })
                .catch(e => {
                  logger.warn(`Could not update character ${charId}: ${e.message}`);
                  return null;
                })
            )
          );

          // Update main character if provided
          if (mainCharacterId && existingGroup.mainCharacterId?.toString() !== mainCharacterId) {
            await prisma.characterGroup.update({
              where: { id: existingGroup.id },
              data: { mainCharacterId: BigInt(mainCharacterId) },
            });
          }

          return existingGroup.id;
        }
      }

      // Try to find existing group by map name
      const existingGroupByMapName = await prisma.characterGroup.findFirst({
        where: { map_name: mapName },
        include: { characters: true },
      });

      if (existingGroupByMapName) {
        // Group exists but may need character updates
        if (characterIds && characterIds.length > 0) {
          // Convert character IDs to BigInt
          const characterIdsBigInt = characterIds.map(id => BigInt(id));

          // Update characters to belong to this group
          await Promise.all(
            characterIdsBigInt.map(charId =>
              prisma.character
                .update({
                  where: { eveId: charId },
                  data: { characterGroupId: existingGroupByMapName.id },
                })
                .catch(e => {
                  logger.warn(`Could not update character ${charId}: ${e.message}`);
                  return null;
                })
            )
          );
        }

        // Update main character if provided
        if (mainCharacterId && existingGroupByMapName.mainCharacterId?.toString() !== mainCharacterId) {
          await prisma.characterGroup.update({
            where: { id: existingGroupByMapName.id },
            data: { mainCharacterId: BigInt(mainCharacterId) },
          });
        }

        return existingGroupByMapName.id;
      }

      // No existing group found, create new one using safe method
      return await CharacterGroupUtils.createCharacterGroupSafely(prisma, mapName, characterIds, mainCharacterId);
    } catch (error) {
      logger.error(
        {
          error: error instanceof Error ? error.message : String(error),
          stack: error instanceof Error ? error.stack : undefined,
        },
        `Failed to upsert character group '${mapName}'`
      );
      return null;
    }
  }

  /**
   * Clean up empty character groups (groups with no associated characters)
   * @param prisma Prisma client instance
   * @returns Number of groups deleted
   */
  static async cleanupEmptyCharacterGroups(prisma: PrismaClient): Promise<number> {
    try {
      // Find all groups that have no characters
      const emptyGroups = await prisma.characterGroup.findMany({
        where: {
          characters: {
            none: {},
          },
        },
        select: {
          id: true,
          map_name: true,
        },
      });

      if (emptyGroups.length === 0) {
        logger.info('No empty character groups found');
        return 0;
      }

      logger.info(`Found ${emptyGroups.length} empty character groups to delete`);

      // Delete all empty groups
      const deletedCount = await prisma.characterGroup.deleteMany({
        where: {
          id: {
            in: emptyGroups.map(group => group.id),
          },
        },
      });

      logger.info(
        `Deleted ${deletedCount.count} empty character groups: ${emptyGroups
          .map(g => `${g.map_name} (${g.id})`)
          .join(', ')}`
      );

      return deletedCount.count;
    } catch (error) {
      logger.error('Failed to cleanup empty character groups:', error);
      return 0;
    }
  }
}

/**
 * Common conversion utilities for domain entities
 */

/**
 * Ensures a value is converted to BigInt, handling string, number, and bigint inputs
 */
export function ensureBigInt(value?: string | number | bigint | null): bigint | null {
  if (value == null) return null;
  if (typeof value === 'bigint') return value;
  if (typeof value === 'number') return BigInt(value);
  if (typeof value === 'string') return BigInt(value);
  throw new Error(`Cannot convert ${typeof value} to BigInt`);
}

/**
 * Ensures a value is converted to BigInt, with a required non-null result
 */
export function ensureRequiredBigInt(value?: string | number | bigint | null): bigint {
  const result = ensureBigInt(value);
  if (result == null) {
    throw new Error('Value cannot be null when BigInt is required');
  }
  return result;
}

/**
 * Converts BigInt to string for serialization
 */
export function bigIntToString(value: bigint | null): string | null {
  return value?.toString() ?? null;
}

/**
 * Converts Date to ISO string for serialization, handling null values
 */
export function dateToISOString(value: Date | string | null): string | null {
  if (!value) return null;
  if (value instanceof Date) return value.toISOString();
  return value;
}

/**
 * Ensures a value is converted to Date, handling string and Date inputs
 */
export function ensureDate(value: Date | string | null): Date | null {
  if (!value) return null;
  if (value instanceof Date) return value;
  return new Date(value);
}

/**
 * Helper functions for building Prisma queries
 */

/**
 * Builds a where filter object for Prisma queries based on a set of filters.
 * Automatically handles date fields by converting them to 'gte' operators.
 *
 * @param filters The filters to apply
 * @returns A where filter object for Prisma
 */
export function buildWhereFilter(filters: Record<string, any>) {
  return Object.entries(filters).reduce(
    (acc, [key, value]) => {
      if (value === undefined || value === null) {
        return acc;
      }

      // Handle date fields
      if (key.toLowerCase().includes('date') && value instanceof Date) {
        acc[key] = { gte: value };
      }
      // Handle array values
      else if (Array.isArray(value)) {
        if (value.length > 0) {
          acc[key] = { in: value };
        }
      }
      // Handle simple values
      else {
        acc[key] = value;
      }

      return acc;
    },
    {} as Record<string, any>
  );
}

/**
 * Builds a pagination object for Prisma queries
 *
 * @param page The page number (1-based)
 * @param pageSize The number of items per page
 * @returns A pagination object with skip and take values
 */
export function buildPagination(page: number = 1, pageSize: number = 10) {
  const skip = (page - 1) * pageSize;
  return {
    skip,
    take: pageSize,
  };
}

/**
 * Utility functions for building query strings and handling URL parameters
 */

/**
 * Build a query string from an object of parameters
 * @param params Object containing query parameters
 * @param options Options for query string building
 * @returns Query string (without leading ?)
 */
export function buildQueryString(
  params: Record<string, string | number | boolean | undefined | null>,
  options: {
    sort?: boolean;
    encodeValues?: boolean;
    skipEmpty?: boolean;
    arrayFormat?: 'brackets' | 'comma' | 'repeat';
  } = {}
): string {
  const { sort = true, encodeValues = true, skipEmpty = true, arrayFormat = 'repeat' } = options;

  // Filter out empty values if requested
  const filteredParams = skipEmpty
    ? Object.entries(params).filter(([, value]) => value !== null && value !== undefined && value !== '')
    : Object.entries(params).filter(([, value]) => value !== null && value !== undefined);

  // Sort parameters if requested
  const sortedParams = sort ? filteredParams.sort(([a], [b]) => a.localeCompare(b)) : filteredParams;

  // Build query string
  return sortedParams
    .map(([key, value]) => {
      // Handle arrays
      if (Array.isArray(value)) {
        switch (arrayFormat) {
          case 'brackets':
            return value
              .map(v => {
                const encodedKey = encodeValues ? encodeURIComponent(`${key}[]`) : `${key}[]`;
                const encodedValue = encodeValues ? encodeURIComponent(String(v)) : String(v);
                return `${encodedKey}=${encodedValue}`;
              })
              .join('&');
          case 'comma': {
            const encodedKey = encodeValues ? encodeURIComponent(key) : key;
            const encodedValue = encodeValues ? encodeURIComponent(value.join(',')) : value.join(',');
            return `${encodedKey}=${encodedValue}`;
          }
          case 'repeat':
          default:
            return value
              .map(v => {
                const encodedKey = encodeValues ? encodeURIComponent(key) : key;
                const encodedValue = encodeValues ? encodeURIComponent(String(v)) : String(v);
                return `${encodedKey}=${encodedValue}`;
              })
              .join('&');
        }
      }

      const stringValue = String(value);
      const encodedValue = encodeValues ? encodeURIComponent(stringValue) : stringValue;
      const encodedKey = encodeValues ? encodeURIComponent(key) : key;
      return `${encodedKey}=${encodedValue}`;
    })
    .join('&');
}

/**
 * Build a cache key from endpoint and parameters
 * @param prefix Cache key prefix
 * @param endpoint API endpoint
 * @param params Query parameters
 * @param options Cache key building options
 * @returns Cache key string
 */
export function buildCacheKey(
  prefix: string,
  endpoint: string,
  params?: Record<string, any>,
  options: {
    includeTimestamp?: boolean;
    ttl?: number;
    version?: string;
  } = {}
): string {
  const { includeTimestamp = false, version } = options;

  let cacheKey = `${prefix}:${endpoint}`;

  if (version) {
    cacheKey += `:v${version}`;
  }

  if (params && Object.keys(params).length > 0) {
    const queryString = buildQueryString(params, {
      sort: true,
      encodeValues: false,
    });
    cacheKey += `?${queryString}`;
  }

  if (includeTimestamp) {
    cacheKey += `:${Date.now()}`;
  }

  return cacheKey;
}

/**
 * Generate cache key with hash for long parameter lists
 * @param prefix Cache key prefix
 * @param endpoint API endpoint
 * @param params Query parameters
 * @returns Cache key string with hash
 */
export function buildHashedCacheKey(prefix: string, endpoint: string, params?: Record<string, any>): string {
  if (!params || Object.keys(params).length === 0) {
    return `${prefix}:${endpoint}`;
  }

  const queryString = buildQueryString(params, {
    sort: true,
    encodeValues: false,
  });

  // Simple hash function for cache keys
  let hash = 0;
  for (let i = 0; i < queryString.length; i++) {
    const char = queryString.charCodeAt(i);
    hash = (hash << 5) - hash + char;
    hash = hash & hash; // Convert to 32-bit integer
  }

  return `${prefix}:${endpoint}:${Math.abs(hash).toString(36)}`;
}

/**
 * Parse a query string into an object
 * @param queryString Query string (with or without leading ?)
 * @param options Parsing options
 * @returns Object containing parsed parameters
 */
export function parseQueryString(
  queryString: string,
  options: {
    decodeValues?: boolean;
    arrayFormat?: 'brackets' | 'comma' | 'repeat';
    parseNumbers?: boolean;
    parseBooleans?: boolean;
  } = {}
): Record<string, string | string[] | number | boolean> {
  const { decodeValues = true, arrayFormat = 'repeat', parseNumbers = false, parseBooleans = false } = options;

  const params: Record<string, any> = {};

  // Remove leading ? if present
  const cleanQuery = queryString.startsWith('?') ? queryString.slice(1) : queryString;

  if (!cleanQuery) {
    return params;
  }

  const pairs = cleanQuery.split('&');
  for (const pair of pairs) {
    const [key, value] = pair.split('=');
    if (!key) continue;

    const decodedKey = decodeValues ? decodeURIComponent(key) : key;
    const decodedValue = value ? (decodeValues ? decodeURIComponent(value) : value) : '';

    // Handle array formats
    if (arrayFormat === 'brackets' && decodedKey.endsWith('[]')) {
      const arrayKey = decodedKey.slice(0, -2);
      if (!params[arrayKey]) {
        params[arrayKey] = [];
      }
      params[arrayKey].push(parseValue(decodedValue, { parseNumbers, parseBooleans }));
    } else if (arrayFormat === 'comma' && decodedValue.includes(',')) {
      params[decodedKey] = decodedValue.split(',').map(v => parseValue(v.trim(), { parseNumbers, parseBooleans }));
    } else if (arrayFormat === 'repeat' && params[decodedKey]) {
      // Convert to array if we see the same key again
      if (!Array.isArray(params[decodedKey])) {
        params[decodedKey] = [params[decodedKey]];
      }
      params[decodedKey].push(parseValue(decodedValue, { parseNumbers, parseBooleans }));
    } else {
      params[decodedKey] = parseValue(decodedValue, {
        parseNumbers,
        parseBooleans,
      });
    }
  }

  return params;
}

/**
 * Parse a string value to appropriate type
 */
function parseValue(
  value: string,
  options: { parseNumbers?: boolean; parseBooleans?: boolean }
): string | number | boolean {
  const { parseNumbers = false, parseBooleans = false } = options;

  if (parseBooleans) {
    if (value.toLowerCase() === 'true') return true;
    if (value.toLowerCase() === 'false') return false;
  }

  if (parseNumbers && !isNaN(Number(value)) && value !== '') {
    return Number(value);
  }

  return value;
}

/**
 * Merge multiple parameter objects, with later objects taking precedence
 * @param paramObjects Array of parameter objects to merge
 * @returns Merged parameter object
 */
export function mergeParams(...paramObjects: Array<Record<string, any> | undefined>): Record<string, any> {
  return paramObjects.reduce((merged: Record<string, any>, params) => {
    if (params) {
      return { ...merged, ...params };
    }
    return merged;
  }, {});
}

/**
 * Convert parameters to strings suitable for URL encoding
 * @param params Parameter object
 * @returns Parameter object with string values
 */
export function stringifyParams(params: Record<string, any>): Record<string, string> {
  const stringified: Record<string, string> = {};

  for (const [key, value] of Object.entries(params)) {
    if (value !== null && value !== undefined) {
      if (Array.isArray(value)) {
        stringified[key] = value.join(',');
      } else {
        stringified[key] = String(value);
      }
    }
  }

  return stringified;
}

/**
 * Build URL with query parameters
 * @param baseUrl Base URL
 * @param params Query parameters
 * @param options Query string building options
 * @returns Complete URL with query string
 */
export function buildUrl(
  baseUrl: string,
  params?: Record<string, any>,
  options?: Parameters<typeof buildQueryString>[1]
): string {
  if (!params || Object.keys(params).length === 0) {
    return baseUrl;
  }

  const queryString = buildQueryString(params, options);
  const separator = baseUrl.includes('?') ? '&' : '?';

  return `${baseUrl}${separator}${queryString}`;
}

/**
 * Build URL with path parameters and query parameters
 * @param baseUrl Base URL with path parameter placeholders (e.g., '/api/users/:id')
 * @param pathParams Object containing path parameter values
 * @param queryParams Query parameters
 * @param options Query string building options
 * @returns Complete URL with path and query parameters
 */
export function buildUrlWithPath(
  baseUrl: string,
  pathParams: Record<string, string | number> = {},
  queryParams?: Record<string, any>,
  options?: Parameters<typeof buildQueryString>[1]
): string {
  let url = baseUrl;

  // Replace path parameters
  for (const [key, value] of Object.entries(pathParams)) {
    url = url.replace(`:${key}`, encodeURIComponent(String(value)));
  }

  // Add query parameters
  if (queryParams && Object.keys(queryParams).length > 0) {
    const queryString = buildQueryString(queryParams, options);
    const separator = url.includes('?') ? '&' : '?';
    url += `${separator}${queryString}`;
  }

  return url;
}

/**
 * Sanitize parameters by removing undefined/null values and converting to strings
 * @param params Raw parameter object
 * @returns Sanitized parameter object
 */
export function sanitizeParams(params: Record<string, any>): Record<string, string> {
  const sanitized: Record<string, string> = {};

  for (const [key, value] of Object.entries(params)) {
    if (value !== null && value !== undefined && value !== '') {
      // Convert BigInt to string
      if (typeof value === 'bigint') {
        sanitized[key] = value.toString();
      } else if (Array.isArray(value)) {
        sanitized[key] = value.map(v => (typeof v === 'bigint' ? v.toString() : String(v))).join(',');
      } else {
        sanitized[key] = String(value);
      }
    }
  }

  return sanitized;
}

/**
 * Validate URL format
 * @param url URL to validate
 * @returns True if URL is valid
 */
export function isValidUrl(url: string): boolean {
  try {
    new URL(url);
    return true;
  } catch {
    return false;
  }
}

/**
 * Extract domain from URL
 * @param url URL to extract domain from
 * @returns Domain string or null if invalid
 */
export function extractDomain(url: string): string | null {
  try {
    return new URL(url).hostname;
  } catch {
    return null;
  }
}

/**
 * Build pagination metadata
 * @param page Current page number
 * @param pageSize Items per page
 * @param totalItems Total number of items
 * @returns Pagination metadata object
 */
export function buildPaginationMeta(page: number, pageSize: number, totalItems: number) {
  const totalPages = Math.ceil(totalItems / pageSize);
  const hasNext = page < totalPages;
  const hasPrev = page > 1;

  return {
    page,
    pageSize,
    totalItems,
    totalPages,
    hasNext,
    hasPrev,
    nextPage: hasNext ? page + 1 : null,
    prevPage: hasPrev ? page - 1 : null,
  };
}

import { logger } from '../lib/logger';
import { timerManager } from './timerManager';
import {
  HTTP_MAX_RETRIES,
  HTTP_INITIAL_RETRY_DELAY,
  HTTP_MAX_RETRY_DELAY,
  HTTP_TIMEOUT,
  Configuration,
} from '../config';

/**
 * Configuration options for retry logic
 */
export interface RetryOptions {
  /** Maximum number of retry attempts */
  maxRetries?: number;
  /** Base delay in milliseconds between retries */
  baseDelayMs?: number;
  /** Initial delay for exponential backoff (alias for baseDelayMs) */
  initialDelayMs?: number;
  /** Initial retry delay (legacy alias) */
  initialRetryDelay?: number;
  /** Maximum delay in milliseconds for exponential backoff */
  maxDelayMs?: number;
  /** Maximum retry delay (legacy alias) */
  maxRetryDelay?: number;
  /** Whether to use exponential backoff (true) or constant delay (false) */
  useExponentialBackoff?: boolean;
  /** Backoff multiplier factor for exponential backoff */
  backoffFactor?: number;
  /** Custom error handler function */
  onError?: (error: Error, attempt: number) => void;
  /** Predicate to determine if an error should trigger a retry */
  shouldRetry?: (error: Error) => boolean;
  /** Optional timeout for each attempt in milliseconds */
  timeout?: number;
  /** Service/operation name for logging */
  serviceName?: string;
  /** Abort signal to cancel retry operation */
  abortSignal?: AbortSignal;
  /** Whether to add jitter to retry delays */
  addJitter?: boolean;
}

/**
 * Unified retry function that combines features from all retry implementations
 * @param fn The function to retry
 * @param description Description of the operation for logging
 * @param options Retry configuration options
 * @returns A Promise that resolves with the result of the function or rejects after max retries
 */
export async function retry<T>(fn: () => Promise<T>, description?: string, options: RetryOptions = {}): Promise<T> {
  // Normalize options to handle legacy aliases
  const baseDelay =
    options.baseDelayMs ?? options.initialDelayMs ?? options.initialRetryDelay ?? HTTP_INITIAL_RETRY_DELAY;

  const maxDelay = options.maxDelayMs ?? options.maxRetryDelay ?? HTTP_MAX_RETRY_DELAY;

  const {
    maxRetries = HTTP_MAX_RETRIES,
    useExponentialBackoff = true,
    backoffFactor = 2,
    onError,
    shouldRetry = () => true,
    timeout = options.timeout,
    serviceName = options.serviceName ?? description ?? 'Operation',
    abortSignal,
    addJitter = true,
  } = options;

  let lastError: Error | null = null;
  let currentDelay = baseDelay;

  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    // Check if operation was aborted
    if (abortSignal?.aborted) {
      throw new Error(`${serviceName} aborted: ${abortSignal.reason ?? 'Operation cancelled'}`);
    }

    try {
      // Create operation promise
      let operationPromise = fn();

      // Add timeout if specified
      if (timeout) {
        const timeoutPromise = new Promise<never>((_, reject) => {
          setTimeout(() => {
            reject(new Error(`${serviceName} timed out after ${timeout}ms`));
          }, timeout);
        });
        operationPromise = Promise.race([operationPromise, timeoutPromise]);
      }

      return await operationPromise;
    } catch (error) {
      lastError = error instanceof Error ? error : new Error(String(error));

      // Check if we should retry this error
      if (!shouldRetry(lastError)) {
        logger.error(`${serviceName} error not eligible for retry: ${lastError.message}`);
        throw lastError;
      }

      // Check if this is the last attempt or if aborted
      if (attempt >= maxRetries || abortSignal?.aborted) {
        break;
      }

      // Call custom error handler if provided
      if (onError) {
        onError(lastError, attempt);
      } else {
        // Default error logging
        const isTimeout = lastError.message.includes('timed out');
        const isRateLimit = lastError.message.includes('rate limit') || lastError.message.includes('429');

        if (isTimeout) {
          logger.warn(`${serviceName} timeout on attempt ${attempt}/${maxRetries}`);
        } else if (isRateLimit) {
          logger.warn(`${serviceName} rate limit hit on attempt ${attempt}/${maxRetries}`);
        } else {
          logger.warn(`${serviceName} failed on attempt ${attempt}/${maxRetries}: ${lastError.message}`);
        }
      }

      // Calculate next delay
      if (useExponentialBackoff) {
        currentDelay = Math.min(currentDelay * Math.pow(backoffFactor, attempt - 1), maxDelay);
      }

      // Add jitter if enabled
      let finalDelay = currentDelay;
      if (addJitter) {
        const jitter = Math.random() * Configuration.jitter.maxMs;
        finalDelay = currentDelay + jitter;
      }

      logger.info(
        `Retrying ${serviceName} in ${Math.round(finalDelay / 1000)}s (attempt ${attempt + 1}/${maxRetries})`
      );

      // Wait before retrying with abort support
      try {
        if (abortSignal) {
          await timerManager.delay(finalDelay, abortSignal);
        } else {
          await new Promise(resolve => setTimeout(resolve, finalDelay));
        }
      } catch (error) {
        throw new Error(
          `${serviceName} aborted during retry delay: ${error instanceof Error ? error.message : String(error)}`
        );
      }
    }
  }

  if (abortSignal?.aborted) {
    throw new Error(`${serviceName} aborted: ${abortSignal.reason || 'Operation cancelled'}`);
  }

  throw new Error(`${serviceName} failed after ${maxRetries} attempts: ${lastError?.message}`);
}

/**
 * Legacy function name for backward compatibility
 * @deprecated Use retry() instead
 */
export async function retryOperation<T>(
  operation: () => Promise<T>,
  description: string,
  options: RetryOptions = {}
): Promise<T> {
  return retry(operation, description, options);
}

/**
 * Legacy function name for backward compatibility
 * @deprecated Use retry() instead
 */
export async function retryWithBackoff<T>(operation: () => Promise<T>, options: RetryOptions = {}): Promise<T> {
  return retry(operation, options.serviceName, options);
}

/**
 * Convenience wrapper for common retry patterns
 */
export const retryStrategies = {
  /**
   * HTTP request retry with exponential backoff
   * Retries on network errors and 5xx responses
   */
  http: <T>(fn: () => Promise<T>, maxRetries = HTTP_MAX_RETRIES): Promise<T> =>
    retry(fn, 'HTTP request', {
      maxRetries,
      baseDelayMs: HTTP_INITIAL_RETRY_DELAY,
      maxDelayMs: HTTP_MAX_RETRY_DELAY,
      useExponentialBackoff: true,
      timeout: HTTP_TIMEOUT,
      shouldRetry: error => {
        // Retry on network errors or server errors (5xx)
        const isNetworkError =
          error.message.includes('ECONNREFUSED') ||
          error.message.includes('ETIMEDOUT') ||
          error.message.includes('ENOTFOUND') ||
          error.message.includes('ECONNRESET') ||
          error.message.includes('ECONNABORTED');
        const isServerError =
          error.message.includes('500') ||
          error.message.includes('502') ||
          error.message.includes('503') ||
          error.message.includes('504');
        const isTimeout = error.message.includes('timed out');
        const isRateLimit = error.message.includes('429') || error.message.includes('rate limit');
        return isNetworkError || isServerError || isTimeout || isRateLimit;
      },
    }),

  /**
   * Database operation retry with constant backoff
   * Good for transient database connection issues
   */
  database: <T>(fn: () => Promise<T>, maxRetries = 3): Promise<T> =>
    retry(fn, 'Database operation', {
      maxRetries,
      baseDelayMs: 500,
      useExponentialBackoff: false,
      shouldRetry: error => {
        // Retry on connection errors or deadlocks
        return (
          error.message.includes('connection') ||
          error.message.includes('deadlock') ||
          error.message.includes('too many connections') ||
          error.message.includes('ECONNREFUSED')
        );
      },
    }),
};

/**
 * Rate limit helper for API clients
 */
export class RateLimiter {
  private lastRequestTime: number = 0;

  constructor(private readonly minDelayMs: number) {}

  /**
   * Wait if necessary to respect rate limits
   */
  async wait(signal?: AbortSignal): Promise<void> {
    const now = Date.now();
    const elapsed = now - this.lastRequestTime;
    if (elapsed < this.minDelayMs) {
      const delay = this.minDelayMs - elapsed;
      if (signal) {
        await timerManager.delay(delay, signal);
      } else {
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
    this.lastRequestTime = Date.now();
  }
}

/**
 * Circuit breaker pattern implementation
 */
export class CircuitBreaker {
  private failureCount = 0;
  private nextAttemptTime = 0;
  private state: 'CLOSED' | 'OPEN' | 'HALF_OPEN' = 'CLOSED';

  constructor(
    private readonly maxFailuresBeforeOpen: number = 3,
    private readonly cooldownPeriodMs: number = 30000, // 30 seconds
    private readonly serviceName: string = 'Unknown Service'
  ) {}

  /**
   * Execute an operation through the circuit breaker
   */
  async execute<T>(operation: () => Promise<T>): Promise<T> {
    if (this.state === 'OPEN') {
      if (Date.now() < this.nextAttemptTime) {
        throw new Error(
          `Circuit breaker OPEN for ${
            this.serviceName
          }: cooling down until ${new Date(this.nextAttemptTime).toISOString()}`
        );
      } else {
        // Transition to HALF_OPEN to test if service is back
        this.state = 'HALF_OPEN';
        logger.info(`Circuit breaker for ${this.serviceName} transitioning to HALF_OPEN for test`);
      }
    }

    try {
      const result = await operation();

      // Success - reset failure count and close circuit if needed
      if (this.state === 'HALF_OPEN') {
        logger.info(`Circuit breaker for ${this.serviceName} test successful, transitioning to CLOSED`);
        this.state = 'CLOSED';
      }

      this.failureCount = 0;
      return result;
    } catch (error) {
      this.failureCount++;

      logger.warn(
        `Circuit breaker for ${this.serviceName} recorded failure ${this.failureCount}/${this.maxFailuresBeforeOpen}`,
        {
          error: error instanceof Error ? error.message : String(error),
          currentState: this.state,
        }
      );

      if (this.failureCount >= this.maxFailuresBeforeOpen || this.state === 'HALF_OPEN') {
        // Open the circuit
        this.state = 'OPEN';
        this.nextAttemptTime = Date.now() + this.cooldownPeriodMs;

        logger.error(
          `Circuit breaker for ${this.serviceName} OPENED due to ${
            this.failureCount
          } failures. Will attempt retry at ${new Date(this.nextAttemptTime).toISOString()}`
        );
      }

      throw error;
    }
  }

  /**
   * Get current circuit breaker state
   */
  getState(): { state: string; failureCount: number; nextAttemptTime: number } {
    return {
      state: this.state,
      failureCount: this.failureCount,
      nextAttemptTime: this.nextAttemptTime,
    };
  }

  /**
   * Manually reset the circuit breaker
   */
  reset(): void {
    this.state = 'CLOSED';
    this.failureCount = 0;
    this.nextAttemptTime = 0;
    logger.info(`Circuit breaker for ${this.serviceName} manually reset`);
  }
}
